import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o,c as p,a as g,b as t,e as a,d as r,f as s}from"./app-9ec59630.js";const n={},h=t("p",null,[t("strong",null,"那羅山"),a("，又稱拉洛山，海拔1157M，三等三角點No.6242，位於新竹縣尖石鄉錦屏村的那羅部落，與高台山隔著那羅溪遙遙相望，為李棟山、大混山向西延伸之獨立山頭，山頂樹林圍繞無視野。")],-1),c=t("p",null,"那羅山沿途經過竹林、山棕林、杉木林、松樹林，可說是林相景觀豐富，最後再攀爬岩石至山頂，全程一路陡上，尤其今日悶熱無風，蚊子超多，走起來頗為累人。",-1),d=s('<p>走走日期：2012/07/21</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>台3線轉120縣道往尖石、內灣方向，過尖石鄉公所，右轉過尖石大橋接竹60鄉道，至那羅溪與錦屏溪交會的三叉路口時，左轉過錦屏大橋，穿過&quot;泰雅勇士塑像&quot;的明隧道，至竹60鄉道大約7K處，左側見那羅社區指標取左進入，續行約60公尺右側3層樓房叉路取左，經過子軒商店後叉路取左過九鄰橋，至那羅76號民宅前空地停車(也可續往上行至登山口停車，但路窄陡峭濕滑)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→那羅76號民宅(位於左側，右側房屋不知門牌號碼)，整裝後往上行<br><img src="https://1013399.github.io/image-3/193/228198016_l.jpg" alt="那羅山" loading="lazy"></p><p>→登山口(產道轉彎處叉路)<br><img src="https://1013399.github.io/image-3/193/228198025_l.jpg" alt="那羅山" loading="lazy"></p><p>→登山口仰望那羅山<br><img src="https://1013399.github.io/image-3/193/228198036_l.jpg" alt="那羅山" loading="lazy"></p><p>→登山口旁石頭三角點噴漆(編號6242誤噴為3242)<br><img src="https://1013399.github.io/image-3/193/228198046_l.jpg" alt="那羅山" loading="lazy"></p><p>→登山口有些崩塌，小心爬上<br><img src="https://1013399.github.io/image-3/193/228198055_l.jpg" alt="那羅山" loading="lazy"></p><p>→進入竹林山徑，一路陡上<br><img src="https://1013399.github.io/image-3/193/228198061_l.jpg" alt="那羅山" loading="lazy"></p><p>→竹林平緩路段<br><img src="https://1013399.github.io/image-3/193/228198080_l.jpg" alt="那羅山" loading="lazy"></p><p>→平緩路段很短，又繼續陡上，開始出現拉繩<br><img src="https://1013399.github.io/image-3/193/228198089_l.jpg" alt="那羅山" loading="lazy"></p><p>→小平坦地，休息後續行<br><img src="https://1013399.github.io/image-3/193/228198100_l.jpg" alt="那羅山" loading="lazy"></p><p>→出竹林，進入山棕林<br><img src="https://1013399.github.io/image-3/193/228198112_l.jpg" alt="那羅山" loading="lazy"></p><p>→路旁巨木<br><img src="https://1013399.github.io/image-3/193/228199031_l.jpg" alt="那羅山" loading="lazy"></p><p>→拉繩陡上稜線<br><img src="https://1013399.github.io/image-3/193/228198119_l.jpg" alt="那羅山" loading="lazy"></p><p>→上至稜線取左續行<br><img src="https://1013399.github.io/image-3/193/228198127_l.jpg" alt="那羅山" loading="lazy"></p><p>→開始出現石塊，繼續陡上<br><img src="https://1013399.github.io/image-3/193/228198141_l.jpg" alt="那羅山" loading="lazy"></p><p>→雜木林平緩路段<br><img src="https://1013399.github.io/image-3/193/228198155_l.jpg" alt="那羅山" loading="lazy"></p><p>→攀岩路段，爬上巨石可於林縫中遙望對面高台山稜線<br><img src="https://1013399.github.io/image-3/193/228198968_l.jpg" alt="那羅山" loading="lazy"></p><p>→那羅山山頂<br><img src="https://1013399.github.io/image-3/193/228199038_l.jpg" alt="那羅山" loading="lazy"></p><p>→那羅山三等三角點No.6242<br><img src="https://1013399.github.io/image-3/193/228199048_l.jpg" alt="那羅山" loading="lazy"></p><p>→休息後原路折返<br> →回到登山口<br> →回到那羅76號停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時(含休息)</p><p>高低落差：約550公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',26),m=t("br",null,null,-1),b={href:"http://tw.myblog.yahoo.com/kentjon106/photo?pid=7552",target:"_blank",rel:"noopener noreferrer"},_=t("br",null,null,-1),u=t("img",{src:"https://1013399.github.io/image-3/193/228199087_l.jpg",alt:"那羅山 參考地圖",loading:"lazy"},null,-1);function y(j,z){const i=e("ExternalLinkIcon");return o(),p("div",null,[h,c,g(" more "),d,t("p",null,[a("(裁剪過原圖)"),m,a(" 原圖連結→"),t("a",b,[a("蜜豆的家"),r(i)]),_,u])])}const N=l(n,[["render",y],["__file","post-193-2012-07-24.html.vue"]]);export{N as default};
