import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as r,c as s,b as i,e as t,d as g,a as e,f as l}from"./app-4a074c75.js";const h={},n=i("p",null,[i("strong",null,"金龜岩登山步道"),t("與"),i("strong",null,"猴洞登山步道"),t("，位於新竹縣北埔、竹東與五峰交界之五指山下，步道於100年12月13日完工，串連登頂步道、橫向步道與金龜岩等3條受阻隔之步道，闢設步道全長約900公尺，為交通部觀光局參山國家風景區管理處委由新竹縣政府代辦，沿途林相包括楓香、樟樹林與柳杉林等，自然生態豐富，讓您能最近距離感受到大自然的美麗。")],-1),m={href:"http://www.hsinchu.gov.tw/modules/v6_mseeage/news/detail.asp?id=201112230003&key=&sd=&ed=&SUnit=&Sclass=&rad=1",target:"_blank",rel:"noopener noreferrer"},b=l('<p>若從觀音禪寺出發，走金龜岩步道20分鐘後，即可從金龜岩進入猴洞步道；若從受北宮出發，則沿著指標牌走，約略10分鐘，可到達步道猴洞入口，評估個人體能，可再連結登頂步道，登上海拔1601公尺的五指山山頂，欣賞秀麗景致。</p><p>走走日期：2012/06/24</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>由竹東走122縣道往五峰方向，於122縣道30.3K附近右轉陡上往五指山，過了北埔冷泉叉路後約20公尺，取左再取右往觀音禪寺(路口有指標)，將車停於觀音寺前廣場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→觀音禪寺前廣場<br><img src="https://1013399.github.io/image-4/197/225235780_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→五指山觀音禪寺，參拜後取右往天君殿<br><img src="https://1013399.github.io/image-4/197/225235895_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→地福天洞水池<br><img src="https://1013399.github.io/image-4/197/225235898_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→天君殿，從「佛」字旁邊進入步道<br><img src="https://1013399.github.io/image-4/197/225235901_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→金龜岩步道入口<br><img src="https://1013399.github.io/image-4/197/225235905_l.jpg" alt="金龜岩步道入口" loading="lazy"></p><p>→沿途可見許多巨石<br><img src="https://1013399.github.io/image-4/197/225235911_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→石階佈滿青苔，有點濕滑，須小心行走<br><img src="https://1013399.github.io/image-4/197/225235913_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道大部份除了石階就是木棧道<br><img src="https://1013399.github.io/image-4/197/225235921_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→第一處休息椅，在此休息一下<br><img src="https://1013399.github.io/image-4/197/225235933_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道轉趨陡上<br><img src="https://1013399.github.io/image-4/197/225235932_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-4/197/225235941_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→金龜岩步道終點，接上新設碎石步道，金龜岩需再往前60公尺<br><img src="https://1013399.github.io/image-4/197/225235943_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→休息平台叉路，往左接上猴洞步道，先取直往金龜岩(約10公尺)<br><img src="https://1013399.github.io/image-4/197/225235946_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→金龜岩，拍照後折返<br><img src="https://1013399.github.io/image-4/197/225235954_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→回到休息平台<br><img src="https://1013399.github.io/image-4/197/225235957_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→平台旁設有步道導覽圖<br><img src="https://1013399.github.io/image-4/197/225235958_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→休息後續行，進入猴洞步道，石猴造型各異<br><img src="https://1013399.github.io/image-4/197/225235963_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→猴洞步道800M處<br><img src="https://1013399.github.io/image-4/197/225235967_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道轉為長陡上<br><img src="https://1013399.github.io/image-4/197/225235971_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→路旁可愛的蕈類<br><img src="https://1013399.github.io/image-4/197/225235975_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道700M處<br><img src="https://1013399.github.io/image-4/197/225235980_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→繼續長陡上<br><img src="https://1013399.github.io/image-4/197/225235986_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→叉路，往右可接上步道500M處，但被設路障阻擋，續直上<br><img src="https://1013399.github.io/image-4/197/225235989_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道600M處<br><img src="https://1013399.github.io/image-4/197/225235992_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→小山頭，步道最高點(海拔約810公尺)，遇台中山友<br><img src="https://1013399.github.io/image-4/197/225235995_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→續行，步道陡下<br><img src="https://1013399.github.io/image-4/197/225235997_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→休息平台，不休息續行<br><img src="https://1013399.github.io/image-4/197/225236001_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→過了休息平台約10公尺路旁有顆山字水泥柱<br><img src="https://1013399.github.io/image-4/197/225236003_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道500M處，右側有路(前一叉路)來會合<br><img src="https://1013399.github.io/image-4/197/225236006_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道展望點<br><img src="https://1013399.github.io/image-4/197/225236009_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→俯瞰五指山公路及遠眺竹東地區<br><img src="https://1013399.github.io/image-4/197/225236011_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道400M處<br><img src="https://1013399.github.io/image-4/197/225236016_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p>',37),c={href:"http://tw.myblog.yahoo.com/su-0342/article?mid=10253",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-4/197/225236019_l.jpg",alt:"金龜岩登山步道、猴洞登山步道",loading:"lazy"},null,-1),u=l('<p>→步道300M處<br><img src="https://1013399.github.io/image-4/197/225236025_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→木棧橋<br><img src="https://1013399.github.io/image-4/197/225236027_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道200M處<br><img src="https://1013399.github.io/image-4/197/225236028_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→猴洞景觀解說牌<br><img src="https://1013399.github.io/image-4/197/225236032_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→過此橋即將進入猴洞<br><img src="https://1013399.github.io/image-4/197/225236037_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→猴洞入(出)口<br><img src="https://1013399.github.io/image-4/197/225236044_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→猴洞為長34公尺的陡下石縫<br><img src="https://1013399.github.io/image-4/197/225236046_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→猴洞另一端入(出)口，也設有解說牌<br><img src="https://1013399.github.io/image-4/197/225236048_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→續行陡下<br><img src="https://1013399.github.io/image-4/197/225236054_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→步道100M處<br><img src="https://1013399.github.io/image-4/197/225236058_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→再穿越石縫<br><img src="https://1013399.github.io/image-4/197/225236061_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→再陡上木棧道<br><img src="https://1013399.github.io/image-4/197/225236065_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→叉路，往左為五指山橫向步道可上五指山或往竹林禪院，續直行<br><img src="https://1013399.github.io/image-4/197/225236067_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→接寬大土路，取左下<br><img src="https://1013399.github.io/image-4/197/225236077_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→接水泥產道，取左下<br><img src="https://1013399.github.io/image-4/197/225236081_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→接五指山公路，左往五指山玉皇宮，直行柏油路回觀音禪寺<br><img src="https://1013399.github.io/image-4/197/225236083_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→左側為受北宮財神廟<br><img src="https://1013399.github.io/image-4/197/225236086_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→觀音禪寺叉路，取右回<br><img src="https://1013399.github.io/image-4/197/225236092_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><p>→回到觀音禪寺<br><img src="https://1013399.github.io/image-4/197/225236094_l.jpg" alt="金龜岩登山步道、猴洞登山步道" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約250公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',22),y=i("br",null,null,-1),j={href:"http://blog.xuite.net/yang5757/blog/58757553",target:"_blank",rel:"noopener noreferrer"},z=i("br",null,null,-1),f=i("img",{src:"https://1013399.github.io/image-4/197/225236223_l.jpg",alt:"金龜岩登山步道、猴洞登山步道 參考地圖",loading:"lazy"},null,-1);function x(M,k){const a=o("ExternalLinkIcon");return r(),s("div",null,[n,i("p",null,[t("(資料摘自："),i("a",m,[t("新竹縣政府新聞"),g(a)]),t(")")]),e(" more "),b,i("p",null,[t("→路旁驚見「細點根結蘭」(細點根結蘭解說："),i("a",c,[t("福星花園"),g(a)]),t(")"),d,_]),u,i("p",null,[t("(圖中黃色部份)"),y,t(" 原圖連結→"),i("a",j,[t("重新站起&一步一腳印(yang5757)"),g(a)]),z,f])])}const N=p(h,[["render",x],["__file","post-197-2012-06-26.html.vue"]]);export{N as default};
