import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{o as t,c as g,a as p,b as i,e as l,f as r}from"./app-b8aee252.js";const e={},o=i("p",null,[i("strong",null,"石壁潭生態步道"),l("，簡稱石潭步道，位於新竹縣芎林鄉石潭村，步道原本是輸送農產品的產業道路，路幅只容搬運車通行，地方期盼發展已久，由沿線約廿名地主無償提供用地，縣政府發包整闢步道，可銜接修心宮至石壁潭山的豐柑步道(2800步道)，目前已接近階段性完成，當地地方希望創造觀光價值，成為清幽林蔭、老少咸宜的步道，分享山林。")],-1),s=r('<p>今日按照步道簡介路線，由石潭村三鄰入口進入，依順時針方向，從竹林別莊口出，輕鬆慢走，連休息只需1.5小時。</p><p>走走日期：2012/04/08</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>竹東往芎林方向，過竹林大橋後遇紅綠燈接120縣道(富林路)，續直行再遇紅綠燈接123縣道(福昌街)，左轉約20公尺即為步道入口(123縣道6.5Km處)，於路旁停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→石壁潭生態步道簡介牌<br><img src="https://1013399.github.io/image-4/202/216409251_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→簡介牌背面有步道路線圖<br><img src="https://1013399.github.io/image-4/202/216409253_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→石潭村三鄰入口，位於簡介牌之馬路斜對面<br><img src="https://1013399.github.io/image-4/202/216409271_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→叉路，右往「竹林別莊」，取左行<br><img src="https://1013399.github.io/image-4/202/216409278_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→經過「鳳麟橋」<br><img src="https://1013399.github.io/image-4/202/216409292_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→叉路，依指標取右，經過民宅後左彎即進入步道<br><img src="https://1013399.github.io/image-4/202/216409294_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→此段步道尚未完工<br><img src="https://1013399.github.io/image-4/202/216410126_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→新建小型休憩平台<br><img src="https://1013399.github.io/image-4/202/216409305_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→步道是利用原有產道整建<br><img src="https://1013399.github.io/image-4/202/216409307_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→部分步道稍陡，到達新建的觀景涼亭<br><img src="https://1013399.github.io/image-4/202/216409312_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→觀景涼亭展望佳，遠眺竹東方向，在此休息<br><img src="https://1013399.github.io/image-4/202/216409315_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→續行，不久便接上「修心宮」上來的傳統山徑，取右上<br><img src="https://1013399.github.io/image-4/202/216409318_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→新建步道與傳統山徑幾乎平行<br><img src="https://1013399.github.io/image-4/202/216409320_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→取右步道陡上<br><img src="https://1013399.github.io/image-4/202/216409323_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→步道與傳統山徑會合<br><img src="https://1013399.github.io/image-4/202/216409326_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→叉路，此處約為石潭步道最高點，左上木棧道往石壁潭山、飛鳳山，續行步道<br><img src="https://1013399.github.io/image-4/202/216409328_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→叉路，左上也可往石壁潭山、飛鳳山，取右續行<br><img src="https://1013399.github.io/image-4/202/216409329_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→叉路，左往石壁潭山、飛鳳山，取右下<br><img src="https://1013399.github.io/image-4/202/216409330_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→新建步道終止接寬大土路<br><img src="https://1013399.github.io/image-4/202/216409332_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→寬大土路結束，到達空曠地，不知是否為路線圖上的「望海台」<br><img src="https://1013399.github.io/image-4/202/216409335_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→循空曠地右側小徑續往下行<br><img src="https://1013399.github.io/image-4/202/216409337_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→經過高壓電塔<br><img src="https://1013399.github.io/image-4/202/216409338_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→續行小產道，又濕又滑又陡，稍後遇二個叉路，先取右再取左接柏油路<br><img src="https://1013399.github.io/image-4/202/216409341_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→經過自來水芎林淨水廠<br><img src="https://1013399.github.io/image-4/202/216409343_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→叉路，左為竹林別莊大門，取右下，開始下起一陣大雨<br><img src="https://1013399.github.io/image-4/202/216409344_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→經過草地遊憩區，下方還有溜滑梯等兒童遊樂設施<br><img src="https://1013399.github.io/image-4/202/216409348_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→回到123縣道(福昌街)，取右回<br><img src="https://1013399.github.io/image-4/202/216409238_l.jpg" alt="石壁潭生態步道(石潭步道)" loading="lazy"></p><p>→回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p><p>高低落差：約240公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(拍攝自步道簡介牌)<br><img src="https://1013399.github.io/image-4/202/216409409_l.jpg" alt="石壁潭生態步道(石潭步道) 參考地圖" loading="lazy"></p>',37);function h(m,n){return t(),g("div",null,[o,p(" more "),s])}const d=a(e,[["render",h],["__file","post-202-2012-04-10.html.vue"]]);export{d as default};
