import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as r,b as i,f as t,d as l,a as o,e as s}from"./app-a9847bbb.js";const h={},n=i("p",null,[i("strong",null,"大湖尖山"),t("，又名尖崠仔山，海拔1313公尺(有資料為1357或1359公尺)，位於嘉義縣番路鄉大湖村，無三角點，為源自阿里山山脈光崙山較突出的一條支稜，山脊蜿蜒巍峨，山勢陡峭，是嘉義縣三尖四凍之首。尖山步道的石階完整，全程竹林庇蔭，沿途潮濕涼爽，充滿了各種不知名的珍奇植物。剛入山時，步道的起伏一般，越往後走，地形越是陡峭，是值得挑戰的一條登山步道，該山名列台灣小百岳No.061。")],-1),m={href:"http://www.fanlu.gov.tw/travel.htm",target:"_blank",rel:"noopener noreferrer"},c=s('<p>走走日期：2012/02/27</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>走完大凍山後，由大凍山休閒渡假中心回阿里山公路(台18線)往石棹方向，於石棹轉159甲縣道(大華公路)往番路方向，在159甲縣道25.8Km處(網路資料26.2Km有誤)，右轉產業道路(路口有大湖尖山2KM及峰藝製茶所指標)...</p><p>→產道叉路，右往木屑工廠，取左行<br><img src="https://1013399.github.io/image-4/208/212766091_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→產道叉路，右往民宅(右側為「清光金華隱靜修道舍」)，續直行<br><img src="https://1013399.github.io/image-4/208/212766071_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→自159甲縣道約行1.5Km後至第一涼亭路旁較寬處停車<br><img src="https://1013399.github.io/image-4/208/212765818_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→停車處仰望大湖尖山<br><img src="https://1013399.github.io/image-4/208/212765823_l.jpg" alt="大湖尖山" loading="lazy"></p><p>→依尖山步道指標方向，先走一段小產道<br><img src="https://1013399.github.io/image-4/208/212765831_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→大湖尖山登山口<br><img src="https://1013399.github.io/image-4/208/212765837_l.jpg" alt="大湖尖山登山口" loading="lazy"></p><p>→一開始就是陡上石階<br><img src="https://1013399.github.io/image-4/208/212765843_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→上至平緩處，景色優美(好像是台灣肖楠)<br><img src="https://1013399.github.io/image-4/208/212765849_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→第二涼亭，天氣晴朗時視野應該不錯，在此休息午餐後續上石階<br><img src="https://1013399.github.io/image-4/208/212765859_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→進入平緩的竹林路段<br><img src="https://1013399.github.io/image-4/208/212765865_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→第三涼亭，往前行接上產業道路<br><img src="https://1013399.github.io/image-4/208/212765871_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→休息後取右上石階<br><img src="https://1013399.github.io/image-4/208/212765880_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→這段石階超陡，從第三涼亭至山頂落差約150公尺<br><img src="https://1013399.github.io/image-4/208/212765884_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→大岩壁<br><img src="https://1013399.github.io/image-4/208/212765891_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→途中展望<br><img src="https://1013399.github.io/image-4/208/212765896_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→進入竹林，繼續陡上<br><img src="https://1013399.github.io/image-4/208/212765905_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→叉路，左右皆可，左是舊步道(較陡且雜草多)，取右新步道<br><img src="https://1013399.github.io/image-4/208/212765913_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→大湖尖山，山頂有座涼亭，左側為舊步道上來的路<br><img src="https://1013399.github.io/image-4/208/212765921_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→山頂有塊鐵網架，標示海拔1357公尺，另有顆基石，字跡模糊<br><img src="https://1013399.github.io/image-4/208/212765930_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→雲霧漸散，山頂視野佳，休息後原路折返<br><img src="https://1013399.github.io/image-4/208/212765938_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→回到第三涼亭，取右改走產業道路下山<br><img src="https://1013399.github.io/image-4/208/212765948_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→叉路，右上不知去向，續直行<br><img src="https://1013399.github.io/image-4/208/212765952_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→叉路，直行不知去向，取左下(原則遇叉路取往下的就對了)<br><img src="https://1013399.github.io/image-4/208/212765961_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→叉路，續直行<br><img src="https://1013399.github.io/image-4/208/212765976_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→叉路，續直行<br><img src="https://1013399.github.io/image-4/208/212765991_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→善勤橋，續直行<br><img src="https://1013399.github.io/image-4/208/212766002_l.jpg" alt="尖山步道、大湖尖山 善勤橋" loading="lazy"></p><p>→叉路，直行往鬼谷、綠豆篁，取左下<br><img src="https://1013399.github.io/image-4/208/212766019_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→回到第一涼亭停車處，休息後取車回159甲縣道往嘉義方向<br><img src="https://1013399.github.io/image-4/208/212766059_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><p>→159甲縣道途中經過大湖國小停車，仰望大湖尖山英姿<br><img src="https://1013399.github.io/image-4/208/212765799_l.jpg" alt="尖山步道、大湖尖山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息、午餐)</p><p>高低落差：約400公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',36),b={href:"http://blog.xuite.net/laoweiz/blog/17061028",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-4/208/212766197_l.jpg",alt:"尖山步道、大湖尖山",loading:"lazy"},null,-1);function u(z,j){const a=p("ExternalLinkIcon");return e(),r("div",null,[n,i("p",null,[t("(資料摘自："),i("a",m,[t("嘉義縣番路鄉公所全球資訊網"),l(a)]),t(")")]),o(" more "),c,i("p",null,[t("原圖連結→"),i("a",b,[t("老尉子的部落格"),l(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-208-2012-03-06.html.vue"]]);export{x as default};
