import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as r,c as o,b as i,e as a,d as l,a as h,f as g}from"./app-b8aee252.js";const s={},n=i("p",null,[i("strong",null,"獨立山"),a("，海拔高840公尺，位於嘉義縣竹崎鄉緞繻村，無三角點，以阿里山森林鐵道螺旋上山而聞名，登上獨立山頂，即可環顧四周的青翠高山，俯瞰文峰山、金獅山與竹崎的風貌。由於臨近嘉義市區，車程僅30分鐘，因此成為熱門的登山步道，該山名列台灣小百岳No.058。")],-1),d={href:"http://www.chuchi.gov.tw/03tour/01view.asp?id=3181",target:"_blank",rel:"noopener noreferrer"},c=g('<h2 id="獨立山國家步道" tabindex="-1"><a class="header-anchor" href="#獨立山國家步道" aria-hidden="true">#</a> 獨立山國家步道</h2><p>獨立山國家步道，分為兩個路段，一段是由緞繻村松腳至紅楠坑的獨立山古道，接續的另一段從紅楠坑到獨立山頂，由林務局於民國86年闢建完成的健康步道，另外還有一條紅楠坑古道往奉天岩、大巖頂(海拔1120公尺)。今日稍早走完二尖山、大尖山及梨子腳山後，直接由梅山鄉太平村驅車直上奉天岩起登。</p><p>走走日期：2012/02/26</p><h3 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h3><p>由梨子腳山登山口(四叉路口)回往梅山鄉太平村，經過太平國小、派出所接162甲縣道，直行穿越道路往奉天岩(路口有獨立山指標)，約行6Km抵達奉天岩附近停車。</p><h3 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h3><p>→奉天岩停車處，左上與直行皆往奉天岩，取右下階梯往獨立山<br><img src="https://1013399.github.io/image-4/210/212763013_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→沿途設有勵志標語<br><img src="https://1013399.github.io/image-4/210/212763017_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→每100公尺設有里程木樁<br><img src="https://1013399.github.io/image-4/210/212763020_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→農產品小店<br><img src="https://1013399.github.io/image-4/210/212763024_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→叉路，左上往奉天岩，取右續行<br><img src="https://1013399.github.io/image-4/210/212763029_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→明海良心店(時候不早了，已打烊)，提供飲料、泡麵、鍋碗瓢盆等讓山友使用，自行誠實投幣<br><img src="https://1013399.github.io/image-4/210/212763034_l.jpg" alt="明海良心店" loading="lazy"></p><p>→明海良心店前也提供「十大功勞茶」，免費飲用<br><img src="https://1013399.github.io/image-4/210/212763037_l.jpg" alt="明海良心店 十大功勞茶" loading="lazy"></p><p>→明海良心店前叉路，左下往獨立山，先取右看看十大功勞茶樹的樣子<br><img src="https://1013399.github.io/image-4/210/212763042_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→十大功勞茶樹，拍照後續往下<br><img src="https://1013399.github.io/image-4/210/212763046_l.jpg" alt="十大功勞茶樹" loading="lazy"></p><p>→紅楠坑叉路，有小販，右下往樟腦寮，越過鐵路左下往獨立山車站，取右健康步道往獨立山<br><img src="https://1013399.github.io/image-4/210/212763051_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→叉路口設有指標<br><img src="https://1013399.github.io/image-4/210/212763054_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→健康步道導引圖<br><img src="https://1013399.github.io/image-4/210/212763062_l.jpg" alt="獨立山登山健康步道導引圖" loading="lazy"></p><p>→健康步道沿鐵路而行<br><img src="https://1013399.github.io/image-4/210/212763074_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→十字路口，左下往獨立山車站，右越過鐵路也可往獨立山(但封閉中)，取直上往獨立山<br><img src="https://1013399.github.io/image-4/210/212763078_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→陡上枕木步道，不久便可達獨立山<br><img src="https://1013399.github.io/image-4/210/212763082_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→獨立山山頂<br><img src="https://1013399.github.io/image-4/210/212763087_l.jpg" alt="獨立山山頂" loading="lazy"></p><p>→山頂無三角點，但有一顆農林航空測量所衛星控制點057<br><img src="https://1013399.github.io/image-4/210/212763092_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→山頂設有獨立山螺旋鐵道解說圖，休息後原路折返<br><img src="https://1013399.github.io/image-4/210/212763097_l.jpg" alt="獨立山螺旋鐵道解說圖" loading="lazy"></p><p>→回到十字路口，取右下往獨立山車站參觀</p><p>→接上鐵道路<br><img src="https://1013399.github.io/image-4/210/212763101_l.jpg" alt="獨立山國家步道、獨立山" loading="lazy"></p><p>→前方即為獨立山車站<br><img src="https://1013399.github.io/image-4/210/212763104_l.jpg" alt="獨立山車站" loading="lazy"></p><p>→左後方為10號隧道<br><img src="https://1013399.github.io/image-4/210/212763107_l.jpg" alt="獨立山車站 10號隧道" loading="lazy"></p><p>→獨立山車站，海拔743公尺<br><img src="https://1013399.github.io/image-4/210/212763108_l.jpg" alt="獨立山車站" loading="lazy"></p><p>→續往前行，見9號隧道，取右側小路上行<br><img src="https://1013399.github.io/image-4/210/212763110_l.jpg" alt="獨立山車站 10號隧道" loading="lazy"></p><p>→回到紅楠坑叉路<br> →回到明海良心店<br> →回到奉天岩停車處，取車往奮起湖</p><h3 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時(含休息)</p><p>高低落差：總落差約135公尺</p><ul><li>奉天岩停車處 → 紅楠坑叉路    下降約55公尺</li><li>紅楠坑叉路   → 十字路口      下降約10公尺</li><li>十字路口     → 獨立山        上升約45公尺</li><li>獨立山       → 十字路口      下降約45公尺</li><li>十字路口     → 獨立山車站    下降約25公尺</li><li>獨立山車站   → 紅楠坑叉路    上升約35公尺</li><li>紅楠坑叉路   → 奉天岩停車處  上升約55公尺</li></ul></div><h3 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h3>',34),m={href:"http://www.chuchi.gov.tw/03tour/01view.asp?id=3181",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-4/210/213053181_l.jpg",alt:"獨立山國家步道、獨立山 參考地圖",loading:"lazy"},null,-1),u=g('<h2 id="太興岩步道" tabindex="-1"><a class="header-anchor" href="#太興岩步道" aria-hidden="true">#</a> 太興岩步道</h2><p>→往奮起湖途中，於166縣道67.7Km停車賞景<br><img src="https://1013399.github.io/image-4/210/212763111_l.jpg" alt="奮起湖途中，於166縣道67.7Km停車賞景" loading="lazy"></p><p>→此處是「太興岩步道」，往下走可到剛才經過的嘉154鄉道太興國小<br><img src="https://1013399.github.io/image-4/210/212763116_l.jpg" alt="太興岩步道" loading="lazy"></p><p>→視野遼闊，景緻宜人<br><img src="https://1013399.github.io/image-4/210/212763119_l.jpg" alt="太興岩步道" loading="lazy"></p><p>→風起雲湧<br><img src="https://1013399.github.io/image-4/210/212763012_l.jpg" alt="太興岩步道" loading="lazy"></p>',5);function j(y,z){const t=e("ExternalLinkIcon");return r(),o("div",null,[n,i("p",null,[a("(資料摘自："),i("a",d,[a("嘉義縣竹崎鄉公所網站"),l(t)]),a(")")]),h(" more "),c,i("p",null,[a("原圖連結→"),i("a",m,[a("嘉義縣竹崎鄉公所網站"),l(t)]),b,_]),u])}const w=p(s,[["render",j],["__file","post-210-2012-03-03.html.vue"]]);export{w as default};
