import{_ as n}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as r,c as s,a as p,b as t,f as a,d as e,w as g,e as h}from"./app-2f66486a.js";const c={},d=t("p",null,[t("strong",null,"梨子腳山"),a("，又名掘尺嶺山、祝壽山，海拔1176公尺，位於嘉義縣梅山鄉，山頂有顆三等三角點No.228，與大尖山、二尖山、馬鞍山、太平山等連成一線，都是阿里山山脈之支脈，沿途視野遼闊，可鳥瞰南投、雲林、嘉義縣等景緻，該山名列台灣小百岳No.057。")],-1),_=t("p",null,"走走日期：2012/02/26",-1),m=t("h2",{id:"行車路線",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#行車路線","aria-hidden":"true"},"#"),a(" 行車路線")],-1),b=t("p",null,"由二尖山休閒茶園回往太平村方向，回到稍早經過的重要四叉路口，於路旁較寬處停車。",-1),u=h('<h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→重要四叉路口，左往太平山，右往二尖山，停妥車後取直往梨子腳山<br><img src="https://1013399.github.io/image-4/211/212762518_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→鹿草蘭園<br><img src="https://1013399.github.io/image-4/211/212762522_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→叉路，左不知去向，取右往梨子腳山(其實小車可開上來停於此處)<br><img src="https://1013399.github.io/image-4/211/212762528_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→開始拾階而上<br><img src="https://1013399.github.io/image-4/211/212762530_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→茶園工寮，牆上噴漆標示海拔1100<br><img src="https://1013399.github.io/image-4/211/212762534_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→叉路，右下往茶園，續往上行<br><img src="https://1013399.github.io/image-4/211/212762537_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→往梨子腳山由此左上階梯，旁有球形垃圾桶<br><img src="https://1013399.github.io/image-4/211/212762541_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→此處階梯較陡<br><img src="https://1013399.github.io/image-4/211/212762544_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→圓形水泥大水塔<br><img src="https://1013399.github.io/image-4/211/212762547_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→梨子腳山山頂<br><img src="https://1013399.github.io/image-4/211/212762552_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→山頂無標示牌，有處建築物遺跡，霧濛濛、視野差<br><img src="https://1013399.github.io/image-4/211/212762557_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→228連假，造訪編號228三等三角點的梨子腳山<br><img src="https://1013399.github.io/image-4/211/212762510_l.jpg" alt="梨子腳山" loading="lazy"></p><p>→休息後原路折返<br> →回到停車處，取車往獨立山</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約50分鐘(含休息)</p><p>高低落差：約150公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',17),f={href:"http://blog.xuite.net/laoweiz/blog/17063690",target:"_blank",rel:"noopener noreferrer"},z=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-4/211/212762572_l.jpg",alt:"梨子腳山 參考地圖",loading:"lazy"},null,-1);function y(x,k){const l=i("RouterLink"),o=i("ExternalLinkIcon");return r(),s("div",null,[d,p(" more "),_,m,b,t("p",null,[a("(參考前篇文章："),e(l,{to:"/posts/post-212-2012-03-01.html"},{default:g(()=>[a("【嘉義梅山】二尖山、大尖山")]),_:1}),a(")")]),u,t("p",null,[a("原圖連結→"),t("a",f,[a("老尉子的部落格"),e(o)]),z,j])])}const V=n(c,[["render",y],["__file","post-211-2012-03-02.html.vue"]]);export{V as default};
