import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as r,c as n,a as l,b as e,e as a,d as s,f as p}from"./app-455fc4b3.js";const h={},c=e("p",null,[e("strong",null,"紅毛埤山"),a("，海拔150公尺，山頂有顆二等三角點No.1096，位於嘉義市蘭潭水庫旁，蘭潭水庫舊稱紅毛埤，為荷蘭人所建，此山因而得名。紅毛埤山為嘉義市最高峰，是阿里山山脈延往嘉南平原的最後山稜，過紅毛埤山後即是嘉南平原，該山名列台灣小百岳No.062。")],-1),d=p('<p>一般造訪紅毛埤山三角點，是由蘭潭水庫旁的二漢坡或好漢坡起登，再由步道途中的大水塔旁小徑切入產道抵達，而且可同時順訪附近的山頂子山(海拔141公尺，三等三角點No.305)，今日上午走完高雄壽山返回北部，準備至嘉義午餐，順訪這座小百岳，於是便採最短路線，由嘉義市大雅路直接開車進入紅毛埤巷，3分鐘便可抵達。</p><p>走走日期：2012/01/29</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>由高雄北上走國道3號，國道3號下中埔交流道往嘉義方向，遇忠義路右轉，過忠義橋後接彌陀路，於彌陀路底四叉路右轉公義路再接大雅路，至大雅路一段639號旁直接開入紅毛埤巷(爲了閃避巷內民宅的惡犬)，約10公尺叉路取左，於路旁較寬處停車(或將車停於路底，路底可迴車)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→過了紅毛埤190號民宅叉路取右進入，需注意路旁電桿上的三角點噴漆<br><img src="https://1013399.github.io/image-4/216/210029105_l.jpg" alt="紅毛埤190號民宅" loading="lazy"></p><p>→先經過檳榔園<br><img src="https://1013399.github.io/image-4/216/210029112_l.jpg" alt="紅毛埤山 檳榔園" loading="lazy"></p><p>→再經過香蕉園，取左便可見三角點<br><img src="https://1013399.github.io/image-4/216/210029118_l.jpg" alt="紅毛埤山 香蕉園" loading="lazy"></p><p>→紅毛埤山二等三角點<br><img src="https://1013399.github.io/image-4/216/210029124_l.jpg" alt="紅毛埤山二等三角點" loading="lazy"></p><p>→二等三角點No.1096，拍照後原路折返<br><img src="https://1013399.github.io/image-4/216/210029101_l.jpg" alt="紅毛埤山 二等三角點No.1096" loading="lazy"></p><p>→回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約10分鐘</p><p>高低落差：不到5公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(原圖誤植為大雅路一段359號，正確應為大雅路一段639號)</p>',15),g={href:"http://blog.xuite.net/laoweiz/blog/17176995",target:"_blank",rel:"noopener noreferrer"},_=e("br",null,null,-1),m=e("img",{src:"https://1013399.github.io/image-4/216/210029158_l.jpg",alt:"紅毛埤山",loading:"lazy"},null,-1);function u(b,f){const t=i("ExternalLinkIcon");return r(),n("div",null,[c,l(" more "),d,e("p",null,[a("原圖連結→"),e("a",g,[a("老尉子的部落格"),s(t)]),_,m])])}const z=o(h,[["render",u],["__file","post-216-2012-02-08.html.vue"]]);export{z as default};
