import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,b as i,e as t,d as l,a as e,f as s}from"./app-b8aee252.js";const h={},n=i("p",null,[i("strong",null,"壽山"),t("，位於高雄鼓山西側沿海，南隔高雄港與旗后山相對，有旗鼓相當之謂，北端則為桃子園軍區，內補一等三角點原於營區內但已崩失。壽山名稱頗多，從最初的打狗山、柴山至目前之「壽山」。壽山山形猶如腎臟，崢崎的珊瑚礁與鐘乳石，蘊含豐富人文歷史，以及多樣的動植物景觀；再加上登山口眾多且展望佳，是假日健行的熱門路線。")],-1),m={href:"http://www.taiwan100mt.com/mountain.php?mountain=77",target:"_blank",rel:"noopener noreferrer"},b=i("hr",null,null,-1),c=i("p",null,"壽山位於高雄西邊濱海，縱貫鼓山全區，南北長約5.5km，東西寬約2.5km，全區最高海拔356公尺，是高雄市天然屏障，地質屬隆起珊瑚礁石灰岩，天然岩洞及榕樹甚多，該山名列台灣小百岳No.077。",-1),d={href:"http://gohiking.myweb.hinet.net/h7/KHSshoushan.htm",target:"_blank",rel:"noopener noreferrer"},_=i("hr",null,null,-1),u=i("p",null,"壽山又可分為南壽山和北壽山，一般以南壽山較為熱門，要繞完南、北壽山，恐需花上一整天的時間，今日以走走南壽山為主。",-1),y=i("p",null,"走走日期：2012/01/29",-1),z=i("h2",{id:"行車路線",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#行車路線","aria-hidden":"true"},"#"),t(" 行車路線")],-1),j=i("p",null,"國道1號下九如交流道，右轉九如一路，過了九如大橋後，於九如四路左轉華安街再左轉鼓山三路，接上鼓山二路遇興隆路右轉，至壽山動物園停車場停車(計次收費30元)。",-1),f=i("h3",{id:"參考地圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),t(" 參考地圖")],-1),x={href:"http://twup.org/khzoo/?pn=view&rid=p47nhap1r3sy",target:"_blank",rel:"noopener noreferrer"},k=i("br",null,null,-1),w=i("img",{src:"https://1013399.github.io/image-4/217/210405731_l.jpg",alt:"壽山 行車參考地圖",loading:"lazy"},null,-1),N=s('<h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→壽山動物園停車場<br><img src="https://1013399.github.io/image-4/217/210028265_l.jpg" alt="壽山動物園停車場" loading="lazy"></p><p>→壽山公園入口<br><img src="https://1013399.github.io/image-4/217/210028269_l.jpg" alt="壽山公園入口" loading="lazy"></p><p>→進入壽山公園<br><img src="https://1013399.github.io/image-4/217/210028270_l.jpg" alt="壽山公園" loading="lazy"></p><p>→叉路，左往動物園，取右登山步道<br><img src="https://1013399.github.io/image-4/217/210028279_l.jpg" alt="壽山公園" loading="lazy"></p><p>→接回馬路，取右續行<br><img src="https://1013399.github.io/image-4/217/210028281_l.jpg" alt="壽山公園" loading="lazy"></p><p>→路旁有壽山步道導覽圖，續往前行<br><img src="https://1013399.github.io/image-4/217/210028287_l.jpg" alt="壽山步道導覽圖" loading="lazy"></p><p>→右側有叉路可上山，續直行<br><img src="https://1013399.github.io/image-4/217/210028290_l.jpg" alt="壽山公園" loading="lazy"></p><p>→壽山登山口，取右進入<br><img src="https://1013399.github.io/image-4/217/210028292_l.jpg" alt="壽山登山口" loading="lazy"></p><p>→叉路，左右皆可，續直行石階<br><img src="https://1013399.github.io/image-4/217/210028296_l.jpg" alt="壽山" loading="lazy"></p><p>→右側圍牆旁小路也有人走(應該是前一叉路過來的)<br><img src="https://1013399.github.io/image-4/217/210028300_l.jpg" alt="壽山" loading="lazy"></p><p>→左步道與右步道叉路，往左會經過翠嶺亭在良友亭與右叉路會合，取右往觀林亭<br><img src="https://1013399.github.io/image-4/217/210028303_l.jpg" alt="壽山" loading="lazy"></p><p>→步道與圍牆旁小路差不多是平行，此處牆上寫著萬里長城<br><img src="https://1013399.github.io/image-4/217/210028306_l.jpg" alt="壽山" loading="lazy"></p><p>→步道叉路多，續行階梯主步道<br><img src="https://1013399.github.io/image-4/217/210028311_l.jpg" alt="壽山" loading="lazy"></p><p>→步道與圍牆旁小路會合<br><img src="https://1013399.github.io/image-4/217/210028313_l.jpg" alt="壽山" loading="lazy"></p><p>→叉路，取左上<br><img src="https://1013399.github.io/image-4/217/210028316_l.jpg" alt="壽山" loading="lazy"></p><p>→觀林亭，右有叉路，取左續行<br><img src="https://1013399.github.io/image-4/217/210028318_l.jpg" alt="壽山" loading="lazy"></p><p>→叉路，取左上木棧道<br><img src="https://1013399.github.io/image-4/217/210028323_l.jpg" alt="壽山" loading="lazy"></p><p>→叉路，取右往相思亭<br><img src="https://1013399.github.io/image-4/217/210028333_l.jpg" alt="壽山" loading="lazy"></p><p>→寬大平緩路徑，沿途視野不錯<br><img src="https://1013399.github.io/image-4/217/210028337_l.jpg" alt="壽山" loading="lazy"></p><p>→相思亭，旁有觀景平台，右有叉路，取左續行<br><img src="https://1013399.github.io/image-4/217/210028341_l.jpg" alt="壽山 相思亭" loading="lazy"></p><p>→寬大階梯緩上<br><img src="https://1013399.github.io/image-4/217/210028349_l.jpg" alt="壽山" loading="lazy"></p><p>→接上寬大土路，應是與前一叉路會合<br><img src="https://1013399.github.io/image-4/217/210028353_l.jpg" alt="壽山" loading="lazy"></p><p>→翠嶺亭叉路，左往翠嶺亭，取右良友亭<br><img src="https://1013399.github.io/image-4/217/210028358_l.jpg" alt="壽山 翠嶺亭叉路" loading="lazy"></p><p>→良友亭<br><img src="https://1013399.github.io/image-4/217/210028431_l.jpg" alt="壽山 良友亭" loading="lazy"></p><p>→良友亭旁叉路，直行寬大土路亦可，取左上較近<br><img src="https://1013399.github.io/image-4/217/210028359_l.jpg" alt="壽山" loading="lazy"></p><p>→接回寬大土路，取左續行<br><img src="https://1013399.github.io/image-4/217/210028364_l.jpg" alt="壽山" loading="lazy"></p><p>→路旁榕樹相當特殊<br><img src="https://1013399.github.io/image-4/217/210028367_l.jpg" alt="壽山 榕樹" loading="lazy"></p><p>→七蔓站，人潮眾多<br><img src="https://1013399.github.io/image-4/217/210028368_l.jpg" alt="壽山 七蔓站" loading="lazy"></p><p>→七蔓站有養生茶供山友飲用<br><img src="https://1013399.github.io/image-4/217/210028374_l.jpg" alt="壽山 七蔓站 養生茶" loading="lazy"></p><p>→取七蔓站旁好漢坡直上好漢亭，直行也可到達好漢亭<br><img src="https://1013399.github.io/image-4/217/210028376_l.jpg" alt="壽山" loading="lazy"></p><p>→陡上好漢坡，都是珊瑚礁岩路徑<br><img src="https://1013399.github.io/image-4/217/210028378_l.jpg" alt="壽山 好漢坡 珊瑚礁岩" loading="lazy"></p><p>→接上另一條好漢坡，取左續陡上<br><img src="https://1013399.github.io/image-4/217/210028383_l.jpg" alt="壽山 好漢坡" loading="lazy"></p><p>→好漢亭<br><img src="https://1013399.github.io/image-4/217/210028388_l.jpg" alt="壽山 好漢亭" loading="lazy"></p><p>→休息後，取好漢亭前小徑往三角點<br><img src="https://1013399.github.io/image-4/217/210028390_l.jpg" alt="壽山" loading="lazy"></p><p>→上至稜線，出現軍區圍牆<br><img src="https://1013399.github.io/image-4/217/210028393_l.jpg" alt="壽山 軍區圍牆" loading="lazy"></p><p>→再往前走約50步，圍牆旁有簡易小梯，由此進入<br><img src="https://1013399.github.io/image-4/217/210028424_l.jpg" alt="壽山 簡易小梯" loading="lazy"></p><p>→叉路，取左<br><img src="https://1013399.github.io/image-4/217/210028418_l.jpg" alt="壽山" loading="lazy"></p><p>→叉路，取右<br><img src="https://1013399.github.io/image-4/217/210028397_l.jpg" alt="壽山" loading="lazy"></p><p>→陡上攻頂<br><img src="https://1013399.github.io/image-4/217/210028410_l.jpg" alt="壽山山頂" loading="lazy"></p><p>→壽山一等主控點No.3203，旁有軍事管制區警告牌<br><img src="https://1013399.github.io/image-4/217/210028403_l.jpg" alt="壽山一等主控點No.3203" loading="lazy"></p><p>→眺望濃霧中的85大樓，管制區不宜久留，拍照後速速退回<br><img src="https://1013399.github.io/image-4/217/210028408_l.jpg" alt="濃霧中的85大樓" loading="lazy"></p><p>→回到好漢亭<br> →回到七蔓站<br> →回到良友亭</p><p>→回到翠嶺亭叉路，取右往翠嶺亭下山<br><img src="https://1013399.github.io/image-4/217/210028436_l.jpg" alt="壽山 翠嶺亭叉路" loading="lazy"></p><p>→翠嶺亭，穿越涼亭續行<br><img src="https://1013399.github.io/image-4/217/210028441_l.jpg" alt="壽山 翠嶺亭" loading="lazy"></p><p>→路旁有顆無名基石<br><img src="https://1013399.github.io/image-4/217/210028447_l.jpg" alt="壽山" loading="lazy"></p><p>→翠嶺亭旁榕樹樹根錯綜複雜<br><img src="https://1013399.github.io/image-4/217/210028452_l.jpg" alt="壽山" loading="lazy"></p><p>→叉路，右前方有路標，取右行<br><img src="https://1013399.github.io/image-4/217/210028462_l.jpg" alt="壽山" loading="lazy"></p><p>→往動物園方向走<br><img src="https://1013399.github.io/image-4/217/210028465_l.jpg" alt="壽山" loading="lazy"></p><p>→這裡原是軍方的坑道戰訓練場<br><img src="https://1013399.github.io/image-4/217/210028471_l.jpg" alt="壽山 原軍方的坑道戰訓練場" loading="lazy"></p><p>→接上木棧道<br><img src="https://1013399.github.io/image-4/217/210028475_l.jpg" alt="壽山" loading="lazy"></p><p>→壽山的老榕樹非常多，且樹根盤繞多種變化<br><img src="https://1013399.github.io/image-4/217/210028480_l.jpg" alt="壽山 老榕樹" loading="lazy"></p><p>→右側圍籬內為壽山動物園<br><img src="https://1013399.github.io/image-4/217/210028492_l.jpg" alt="壽山動物園" loading="lazy"></p><p>→回到左步道與右步道叉路，取右回<br><img src="https://1013399.github.io/image-4/217/210028261_l.jpg" alt="壽山" loading="lazy"></p><p>→回到登山口<br> →回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約250公尺</p></div><h2 id="參考地圖-1" tabindex="-1"><a class="header-anchor" href="#參考地圖-1" aria-hidden="true">#</a> 參考地圖</h2><p>→實際行走路線</p><figure><img src="https://1013399.github.io/image-4/217/210028565_l.jpg" alt="壽山 實際行走路線" tabindex="0" loading="lazy"><figcaption>壽山 實際行走路線</figcaption></figure>',60),v={href:"http://blog.xuite.net/laoweiz/blog/19249047",target:"_blank",rel:"noopener noreferrer"},V=i("br",null,null,-1),B=i("img",{src:"https://1013399.github.io/image-4/217/210028563_l.jpg",alt:"壽山 參考地圖",loading:"lazy"},null,-1);function E(S,C){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,i("p",null,[t("(參考資料："),i("a",m,[t("歐都納小百岳大百科"),l(a)]),t(")")]),e(" more "),b,c,i("p",null,[t("(參考資料："),i("a",d,[t("HiSpring優遊山林溪"),l(a)]),t(")")]),_,u,y,z,j,f,i("p",null,[t("原圖連結→"),i("a",x,[t("高雄市壽山動物園"),l(a)]),k,w]),N,i("p",null,[t("原圖連結→"),i("a",v,[t("老尉子的部落格"),l(a)]),V,B])])}const L=g(h,[["render",E],["__file","post-217-2012-02-07.html.vue"]]);export{L as default};
