import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as g,c as n,a as h,b as a,e as t,d as l,f as e}from"./app-b8aee252.js";const p={},s=a("p",null,"聖母登山步道，位於宜蘭縣礁溪鄉五峰旗瀑布風景區上方，原是一條天主教徒翻山越嶺的朝聖之路，步道終點有一座聖母山莊，是前往蘭陽五岳之一的三角崙山(小百岳No.083)之中繼站。",-1),d=a("p",null,"五峰旗瀑布風景區，位於宜蘭縣礁溪鄉，為蘭陽八景之一，由於瀑布後方有五座山峰並列而得名，瀑布全長約100公尺，共分為三層，第一層(上層)瀑布約50公尺，但目前封閉中，第二層(中層)瀑布約30公尺，瀑布旁設有五峰亭，第三層(下層)瀑布則約20公尺，瀑布下方自然形成一個小水潭可戲水，步道四周綠意盎然，令人心曠神怡，風景區並闢有攔砂壩，河床旁設有烤肉遊憩區，適合闔家前往休憩的好去處。",-1),c=a("p",null,"龍潭湖，位於宜蘭縣礁溪鄉龍潭村，舊名大埤，俗稱大陂湖，為昔日蘭陽十二勝之一，亦是蘭陽五大名湖中面積最大的湖泊；龍潭湖三面環山，面積約17公頃，湖面遼闊，水深4.5丈，是宜蘭五大名湖中面積最大者，湖面如鏡，青山倒影，景緻宜人，悠遠寧靜。(以上參考網路資料)",-1),m=e('<p>今日原本計畫走走聖母登山步道及造訪三角崙山，無奈天候不佳，只走到聖母朝聖地，回程經五峰旗瀑布下山，由於時間尚早，便順遊附近的龍潭湖。</p><p>走走日期： 2012/01/24</p><h2 id="聖母朝聖地、五峰旗瀑布風景區" tabindex="-1"><a class="header-anchor" href="#聖母朝聖地、五峰旗瀑布風景區" aria-hidden="true">#</a> 聖母朝聖地、五峰旗瀑布風景區</h2><h3 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h3><p>國道3號轉國道5號下頭城交流道，左轉台9線往礁溪，至礁溪火車站叉路口右轉德陽路，至忠孝路口左轉續行德陽路，至大忠路口，右轉五峰路(宜2鄉道)往五峰旗瀑布，在五峰旗瀑布風景區入口前左轉至下方大停車場停車(停車費50元)。</p><h3 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h3><p>→由停車場循溪畔續往前行<br><img src="https://1013399.github.io/image-4/220/209603415_l.jpg" alt="峰旗瀑布風景區入口下方大停車場" loading="lazy"></p><p>→攔砂壩<br><img src="https://1013399.github.io/image-4/220/209603421_l.jpg" alt="攔砂壩" loading="lazy"></p><p>→叉路，右往五峰旗瀑布風景區入口，左可往林美石盤步道，續直行<br><img src="https://1013399.github.io/image-4/220/209603495_l.jpg" alt="五峰旗瀑布風景區入口" loading="lazy"></p><p>→叉路，右往第三層瀑布，取左續行土石路<br><img src="https://1013399.github.io/image-4/220/209603428_l.jpg" alt="五峰旗瀑布 第三層瀑布" loading="lazy"></p><p>→叉路，右往第二層瀑布，取左續行土石路<br><img src="https://1013399.github.io/image-4/220/209603431_l.jpg" alt="五峰旗瀑布 第二層瀑布" loading="lazy"></p><p>→抵達聖母朝聖地，天壇造型的天主教堂<br><img src="https://1013399.github.io/image-4/220/209603447_l.jpg" alt="聖母朝聖地 天壇造型的天主教堂" loading="lazy"></p><p>→旁有聖母像<br><img src="https://1013399.github.io/image-4/220/209603455_l.jpg" alt="聖母朝聖地 聖母像" loading="lazy"></p><p>→教堂前視野佳(但今日霧茫茫)，地上有標示海拔高度<br><img src="https://1013399.github.io/image-4/220/209603460_l.jpg" alt="聖母朝聖地 海拔168公尺" loading="lazy"></p><p>→遠望第一層瀑布，參觀休息後原路折返<br><img src="https://1013399.github.io/image-4/220/209603453_l.jpg" alt="五峰旗瀑布 第一層瀑布" loading="lazy"></p><p>→回到第二層瀑布叉路，取左往瀑布風景區</p><p>→叉路，左上往第一層瀑布，目前封閉中，續直行<br><img src="https://1013399.github.io/image-4/220/209603467_l.jpg" alt="五峰旗瀑布 第一層瀑布" loading="lazy"></p><p>→第二層瀑布及五峰亭<br><img src="https://1013399.github.io/image-4/220/209603470_l.jpg" alt="五峰旗瀑布 第二層瀑布及五峰亭" loading="lazy"></p><p>→再往前觀看瀑布全貌，旁有觀瀑平台，折回往下續行<br><img src="https://1013399.github.io/image-4/220/209603476_l.jpg" alt="五峰旗瀑布" loading="lazy"></p><p>→第三層瀑布，續往下行<br><img src="https://1013399.github.io/image-4/220/209603477_l.jpg" alt="五峰旗瀑布 第三層瀑布" loading="lazy"></p><p>→五峰旗瀑布風景區入口，攤販林立<br><img src="https://1013399.github.io/image-4/220/209603485_l.jpg" alt="五峰旗瀑布風景區入口" loading="lazy"></p><p>→右側有叉路可回溪畔至停車場，叉路口兩棵纏繞的樹，很特殊<br><img src="https://1013399.github.io/image-4/220/209603507_l.jpg" alt="兩棵纏繞的樹" loading="lazy"></p><p>→風景區入口設有路線圖<br><img src="https://1013399.github.io/image-4/220/209603489_l.jpg" alt="五峰旗瀑布 路線圖" loading="lazy"></p><p>→往回拍攝風景區入口，續直行車道回停車場<br><img src="https://1013399.github.io/image-4/220/209603513_l.jpg" alt="五峰旗瀑布風景區入口" loading="lazy"></p><p>→回到停車場</p><h3 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p><p>高低落差：約110公尺</p></div><h3 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h3>',28),_={href:"http://www.tonyhuang39.com/tony0511/tony0511.html",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),u=a("img",{src:"https://1013399.github.io/image-4/220/209604257_l.jpg",alt:"聖母朝聖地、五峰旗瀑布風景區 參考地圖",loading:"lazy"},null,-1),y=e('<h2 id="礁溪龍潭湖" tabindex="-1"><a class="header-anchor" href="#礁溪龍潭湖" aria-hidden="true">#</a> 礁溪龍潭湖</h2><h3 id="行車路線-1" tabindex="-1"><a class="header-anchor" href="#行車路線-1" aria-hidden="true">#</a> 行車路線</h3><p>五峰旗風景區停車場接五峰路往礁溪方向，過德陽路續行大忠路，接礁溪路四段右轉，至礁溪路一段遇開蘭路右轉，至路底接四結路左轉，續行接龍泉路，於龍泉路69巷右轉再接漳福路，便可到達龍潭湖停車場停車(免費)。</p><p>→湖光山色，美不勝收<br><img src="https://1013399.github.io/image-4/220/209603519_l.jpg" alt="礁溪龍潭湖" loading="lazy"><br><img src="https://1013399.github.io/image-4/220/209603523_l.jpg" alt="礁溪龍潭湖" loading="lazy"><br><img src="https://1013399.github.io/image-4/220/209603529_l.jpg" alt="礁溪龍潭湖" loading="lazy"><br><img src="https://1013399.github.io/image-4/220/209603537_l.jpg" alt="礁溪龍潭湖" loading="lazy"><br><img src="https://1013399.github.io/image-4/220/209603541_l.jpg" alt="礁溪龍潭湖" loading="lazy"><br><img src="https://1013399.github.io/image-4/220/209603411_l.jpg" alt="礁溪龍潭湖" loading="lazy"></p><h2 id="參考地圖-1" tabindex="-1"><a class="header-anchor" href="#參考地圖-1" aria-hidden="true">#</a> 參考地圖</h2>',5),j={href:"http://www.tonyhuang39.com/tony0633/tony0633.html",target:"_blank",rel:"noopener noreferrer"},z=a("br",null,null,-1),f=a("img",{src:"https://1013399.github.io/image-4/220/209698422_l.jpg",alt:"礁溪龍潭湖 參考地圖",loading:"lazy"},null,-1),x={href:"http://blog.yam.com/evenlin/article/18251366",target:"_blank",rel:"noopener noreferrer"},k=a("br",null,null,-1),v=a("img",{src:"https://1013399.github.io/image-4/220/209698424_l.jpg",alt:"礁溪龍潭湖 參考地圖",loading:"lazy"},null,-1);function w(N,V){const i=r("ExternalLinkIcon");return g(),n("div",null,[s,d,c,h(" more "),m,a("p",null,[t("原圖連結→"),a("a",_,[t("Tony的自然人文旅記"),l(i)]),b,u]),y,a("p",null,[t("原圖連結→"),a("a",j,[t("Tony的自然人文旅記"),l(i)]),z,f]),a("p",null,[t("原圖連結→"),a("a",x,[t("一哥的玩遍全台灣部落格"),l(i)]),k,v])])}const T=o(p,[["render",w],["__file","post-220-2012-02-01.html.vue"]]);export{T as default};
