import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as g,c as r,a as e,b as a,f as i,d as o,w as s,e as h}from"./app-63889d57.js";const n={},m=a("p",null,[a("strong",null,"樹杞林山"),i("，海拔286公尺，位於新竹縣竹東鎮南方，三等三角點NO.101，登山口在公園路(公園高分49電力電線桿附近)，循保線路約10分鐘可到達三角點，曾於2009/06/13造訪過，今日盛逢民國101年元旦，特邀同事一起走走編號101三等三角點的樹杞林山。")],-1),c=a("p",null,"走訪樹杞林山，一定要順便走走附近的「森林公園登山步道」及「大鄉自然步道」才有意思，於是按照2009/06/13的行程再走一次，發現山徑似乎已被整理過，三角點也容易尋獲，輕鬆走完一圈，只花了二個多小時。",-1),d=h('<p>走走日期： 2012/01/01</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>竹東鎮東寧路(縣道122)往北埔、五峰方向，至大同路右轉至竹東警察分局，左轉康寧街，過竹東國中後遇公園路再右轉，可將車停於鎮立圖書館、羽球館或普照宮。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→普照宮停車場<br><img src="https://1013399.github.io/image-4/221/207398688_l.jpg" alt="普照宮停車場" loading="lazy"></p><p>→森林公園登山步道<br><img src="https://1013399.github.io/image-4/221/207398714_l.jpg" alt="森林公園登山步道" loading="lazy"></p><p>→步道旁楊桃造型的休息椅<br><img src="https://1013399.github.io/image-4/221/207398718_l.jpg" alt="楊桃造型的休息椅" loading="lazy"></p><p>→竹東鎮歷史沿革解說牌<br><img src="https://1013399.github.io/image-4/221/207398729_l.jpg" alt="竹東鎮歷史沿革解說牌" loading="lazy"></p><p>→眺望對面的芎林石碧潭山、飛鳳山<br><img src="https://1013399.github.io/image-4/221/207398736_l.jpg" alt="芎林石碧潭山、飛鳳山" loading="lazy"></p><p>→雙涼亭、體能活動區(左有叉路往大鄉自然步道，續直行)<br><img src="https://1013399.github.io/image-4/221/207398738_l.jpg" alt="森林公園登山步道" loading="lazy"></p><p>→陡下大願寺旁的階梯步道<br><img src="https://1013399.github.io/image-4/221/207398751_l.jpg" alt="大願寺旁的階梯步道" loading="lazy"></p><p>→接回公園路，取左上行往樹杞林山(約40~50度的柏油路，需走20多分鐘)<br><img src="https://1013399.github.io/image-4/221/207398757_l.jpg" alt="森林公園登山步道" loading="lazy"></p><p>→樹杞林山登山口(前方為竹東垃圾場，附近有公園高幹49電線杆)<br><img src="https://1013399.github.io/image-4/221/207398763_l.jpg" alt="樹杞林山登山口" loading="lazy"></p><p>→高壓電塔(新竹-竹東線037)<br><img src="https://1013399.github.io/image-4/221/207398769_l.jpg" alt="樹杞林山" loading="lazy"></p><p>→三角點叉路(過高壓電塔後約10公尺)，取右往三角點<br><img src="https://1013399.github.io/image-4/221/207398778_l.jpg" alt="樹杞林山" loading="lazy"></p><p>→樹杞林山，三等三角點NO.101，無視野，回保線路續行<br><img src="https://1013399.github.io/image-4/221/207398785_l.jpg" alt="樹杞林山，三等三角點NO.101" loading="lazy"></p><p>→保線路旁有缺口，展望不錯<br><img src="https://1013399.github.io/image-4/221/207398808_l.jpg" alt="樹杞林山" loading="lazy"></p><p>→大窩山叉路，右往大窩山，續行保線路<br><img src="https://1013399.github.io/image-4/221/207398812_l.jpg" alt="樹杞林山 大窩山叉路" loading="lazy"></p><p>→過了高壓電塔(038)，接大鄉自然步道，左右皆可，今取右<br><img src="https://1013399.github.io/image-4/221/207398824_l.jpg" alt="樹杞林山" loading="lazy"></p><p>→植物解說牌，非常適合小學生教學<br><img src="https://1013399.github.io/image-4/221/207398827_l.jpg" alt="樹杞林山 植物解說牌" loading="lazy"></p><p>→叉路，依指標取左往樹屋<br><img src="https://1013399.github.io/image-4/221/207398843_l.jpg" alt="樹杞林山" loading="lazy"></p><p>→叉路旁有步道導覽圖<br><img src="https://1013399.github.io/image-4/221/207398854_l.jpg" alt="樹杞林山 步道導覽圖" loading="lazy"></p><p>→植物解說牌<br><img src="https://1013399.github.io/image-4/221/207398863_l.jpg" alt="樹杞林山 植物解說牌" loading="lazy"></p><p>→油桐花落葉舖滿步道<br><img src="https://1013399.github.io/image-4/221/207398872_l.jpg" alt="樹杞林山 油桐花落葉舖滿步道" loading="lazy"></p><p>→樹屋<br><img src="https://1013399.github.io/image-4/221/207398880_l.jpg" alt="樹杞林山 樹屋" loading="lazy"></p><p>→叉路，左回大鄉自然步道，取右往研究路<br><img src="https://1013399.github.io/image-4/221/207398891_l.jpg" alt="樹杞林山" loading="lazy"></p><p>→竹東圳<br><img src="https://1013399.github.io/image-4/221/207398894_l.jpg" alt="竹東圳" loading="lazy"></p><p>→研究路59巷6弄(大鄉自然步道出口，續行研究路)<br> →左切研究路偶數號門牌道路(不需走到中豐路)<br> →研究路82號(取廢棄豬寮小徑上山)<br> →回到森林公園雙涼亭<br> →回到普照宮</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約140分鐘(含休息)</p><p>高低落差：</p><ul><li>普照宮至雙涼亭(森林公園步道高點)    上升約30公尺</li><li>雙涼亭至公園路柏油路(大願寺附近)    下降約70公尺</li><li>公園路柏油路至樹杞林山(電塔037)     上升約100公尺</li><li>樹杞林山至研究路82號                下降約120公尺</li><li>研究路82號至雙涼亭                  上升約90公尺</li><li>雙涼亭至普照宮停車場                下降約30公尺</li></ul></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>藍天圖集(登山補給站改版後，原連結已失效，點圖可放大)<br><img src="https://1013399.github.io/image-4/221/207399019_l.jpg" alt="樹杞林山" loading="lazy"></p>',32);function b(_,u){const t=p("RouterLink");return g(),r("div",null,[m,e(" more "),c,a("p",null,[i("關於竹東森林公園登山步道及大鄉自然步道，請參考此文章→"),o(t,{to:"/posts/post-360-2009-06-15.html"},{default:s(()=>[i("【新竹竹東】森林公園登山步道、樹杞林山、大鄉自然步道")]),_:1}),i("。")]),d])}const z=l(n,[["render",b],["__file","post-221-2012-01-03.html.vue"]]);export{z as default};
