import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as p,c as g,a as r,b as t,f as a,d as l,e as n}from"./app-63889d57.js";const s={},h=t("p",null,[t("strong",null,"鵲子山"),a("，海拔679公尺，位於宜蘭縣礁溪鄉，屬於雪山山脈旁支的獨立山頭，又名"),t("strong",null,"鵠仔山"),a("、"),t("strong",null,"鴻子山"),a("，日治時期日本人因山形似富士山，稱之為"),t("strong",null,"礁溪富士山"),a("，山頂有顆三等三角點No.839基石，殘留的瞭望台鐵架已倒塌，山頂視野良好，該山名列台灣小百岳No.084。")],-1),c=t("p",null,"攀登鵲子山，一般說來有三條路徑：",-1),d=t("ul",null,[t("li",null,"第一條是從五峰旗往聖母登山步道途中轉進台電保線路，接到登山步道之後上鵲子山，此路距離遙遠，路線曲折，少有人行。"),t("li",null,"第二條是從清修宮登山口起登，距離中等，路況不佳，所以使用的人也不多。"),t("li",null,"第三條則是從圓通寺登山口出發，路況較好，距離較短，再加上登山口海拔較高，所以是最多人所選擇的路徑。")],-1),m={href:"http://blog.udn.com/malabay/4062493",target:"_blank",rel:"noopener noreferrer"},_=n('<p>今日選擇第三條路線，從圓通寺出發，到達登山口前會經過民宅，有數隻惡犬需注意。而登山路徑雖然明顯，但後段芒草夾道、山徑濕滑，也需小心慢行。</p><p>走走日期：2011/12/25</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>於福隆遊客中心出發，循濱海公路(台2線)南下，至頭城後轉北宜公路(台9線)往新店，於台9線58.9Km處左下往上新花園，途經跑馬古道入口續行至圓通寺叉路，依指示牌右轉往圓通寺(直行可往已廢棄的上新花園)，於圓通寺前路旁空地停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→圓通寺前路旁空地停車，沿著寺前道路出發<br><img src="https://1013399.github.io/image-4/223/206796776_l.jpg" alt="圓通寺" loading="lazy"></p><p>→猴洞坑溪小橋<br><img src="https://1013399.github.io/image-4/223/206796778_l.jpg" alt="猴洞坑溪小橋" loading="lazy"></p><p>→過小橋後遇叉路，右側山徑就是登山口，取右進入樹林<br><img src="https://1013399.github.io/image-4/223/206796781_l.jpg" alt="鵲子山" loading="lazy"></p><p>→出樹林後為林務局造林地<br><img src="https://1013399.github.io/image-4/223/206796783_l.jpg" alt="林務局造林地" loading="lazy"></p><p>→造林解說牌<br><img src="https://1013399.github.io/image-4/223/206796785_l.jpg" alt="造林解說牌" loading="lazy"></p><p>→前方山頭不知是否為鵲子山<br><img src="https://1013399.github.io/image-4/223/206796786_l.jpg" alt="鵲子山" loading="lazy"></p><p>→兩側芒草似乎剛修剪過<br><img src="https://1013399.github.io/image-4/223/206796788_l.jpg" alt="鵲子山" loading="lazy"></p><p>→上至稜線展望絕佳，俯瞰蘭陽平原(5張相片合成，點圖可放大)<br><img src="https://1013399.github.io/image-4/223/206796843_l.jpg" alt="鵲子山" loading="lazy"></p><p>→開始進入芒草區<br><img src="https://1013399.github.io/image-4/223/206796795_l.jpg" alt="鵲子山" loading="lazy"></p><p>→陡上濕滑芒草山徑<br><img src="https://1013399.github.io/image-4/223/206796798_l.jpg" alt="鵲子山" loading="lazy"></p><p>→出芒草區進入樹林<br><img src="https://1013399.github.io/image-4/223/206796801_l.jpg" alt="鵲子山" loading="lazy"></p><p>→樹林裡平緩好走<br><img src="https://1013399.github.io/image-4/223/206796803_l.jpg" alt="鵲子山" loading="lazy"></p><p>→轉為陡上<br><img src="https://1013399.github.io/image-4/223/206796805_l.jpg" alt="鵲子山" loading="lazy"></p><p>→出樹林又進入芒草區<br><img src="https://1013399.github.io/image-4/223/206796808_l.jpg" alt="鵲子山" loading="lazy"></p><p>→前方山頭應該是鵲子山了<br><img src="https://1013399.github.io/image-4/223/206796811_l.jpg" alt="鵲子山" loading="lazy"></p><p>→再進入芒草區，此處芒草更密<br><img src="https://1013399.github.io/image-4/223/206796813_l.jpg" alt="鵲子山" loading="lazy"></p><p>→叉路，左下往清修宮登山口，取右往鵲子山三角點<br><img src="https://1013399.github.io/image-4/223/206796815_l.jpg" alt="鵲子山" loading="lazy"></p><p>→鵲子山三等三角點No.839<br><img src="https://1013399.github.io/image-4/223/206796818_l.jpg" alt="鵲子山" loading="lazy"></p><p>→鵲子山山頂芒草稍多，展望有限，休息後原路折返<br><img src="https://1013399.github.io/image-4/223/206796774_l.jpg" alt="鵲子山" loading="lazy"></p><p>→回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時30分鐘(上山50分鐘、下山30分鐘、休息賞景10分鐘)</p><p>高低落差：約270公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',28),b=t("br",null,null,-1),u={href:"http://tw.myblog.yahoo.com/kentjon106/photo?pid=4910",target:"_blank",rel:"noopener noreferrer"},y=t("br",null,null,-1),j=t("img",{src:"https://1013399.github.io/image-4/223/206796862_l.jpg",alt:"鵲子山 參考地圖",loading:"lazy"},null,-1);function z(f,x){const i=e("ExternalLinkIcon");return p(),g("div",null,[h,r(" more "),c,d,t("p",null,[a("(參考資料："),t("a",m,[a("翠湖居士udn部落格"),l(i)]),a(")")]),_,t("p",null,[a("(修改過)"),b,a(" 原圖連結→"),t("a",u,[a("蜜豆的家-部落格相簿"),l(i)]),y,j])])}const v=o(s,[["render",z],["__file","post-223-2011-12-29.html.vue"]]);export{v as default};
