import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,e as t,d as e,f as s}from"./app-39f3f472.js";const h={},n=i("p",null,[i("strong",null,"南港山"),t("，海拔374公尺，為台北市南港山系的主要山峰，三等三角點No.695位於步道旁，南港山步道錯綜複雜，可以連接姆指山，也可以串連四獸山(虎、豹、獅、象)，路線更可以有許多的組合變化，為台北近郊諸多登山步道中，非常熱門的郊山健行路線，該山名列台灣小百岳No.013。"),i("br"),t("     "),i("strong",null,"九五峰"),t("，海拔375公尺，為南港山系的最高點，無基石，但有一顆上刻九五峰巨石，為紀念楊森將軍以九五高齡攀登此峰而得名。峰頂展望絕佳，天氣晴朗時，視野一望無際，台北101大樓、遠方關渡和基隆河的海天一色，乃至於五指山和七星山系的綿延起伏的山脈，絕佳遼闊的景緻展露無遺。")],-1),m=s('<p><strong>九五峰東北嶺</strong>，海拔約310公尺，土地調查局圖根點，基石位於休憩觀景處之步道欄杆外，附近有路燈401號。這顆基石是從土城小崙子山公墓區移植過來的，此處視野展望極佳，俯瞰台北市。</p><p>南港山登山路線眾多，但今日天候不佳，撐傘雨中漫步行，因此選擇較短路線，由忠義宮起登，經復興園、十方禪寺、黃禪園上南港山稜線，來回花了兩個多小時，全程霧濛濛、視野有限。</p><p>走走日期：2011/11/13</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號北上，出木柵交流道轉國道3甲(台北連絡道)至台北市辛亥路出口，續行辛亥路至基隆路右轉，遇信義路再右轉直行至六段後轉為福德街，於福德街221巷右轉進入(須注意221巷，上午為傳統市場，不宜太早進入)，途中經過塯公國中，自巷口起算約行980公尺叉路，取左往真光禪寺，再走約200公尺忠義宮叉路附近找空地停車(約可停4部車)，或到真光禪寺停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→忠義宮叉路，直行往真光禪寺，路右為忠義宮牌樓<br><img src="https://1013399.github.io/image-4/227/202592344_l.jpg" alt="忠義宮叉路" loading="lazy"></p><p>→路左為壽天宮牌樓，取右過忠義宮牌樓往上行<br><img src="https://1013399.github.io/image-4/227/202592352_l.jpg" alt="壽天宮牌樓" loading="lazy"></p><p>→忠義宮<br><img src="https://1013399.github.io/image-4/227/202592357_l.jpg" alt="忠義宮" loading="lazy"></p><p>→朝玄宮<br><img src="https://1013399.github.io/image-4/227/202592361_l.jpg" alt="朝玄宮" loading="lazy"></p><p>→復興園叉路<br><img src="https://1013399.github.io/image-4/227/202592367_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→右側有復興園牌樓，穿過牌樓可往九五坪、攀岩上南港山稜線<br><img src="https://1013399.github.io/image-4/227/202592373_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→左側可往真光禪寺、南天宮<br><img src="https://1013399.github.io/image-4/227/202592339_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→於復興園觀看101大樓<br><img src="https://1013399.github.io/image-4/227/202592341_l.jpg" alt="101大樓" loading="lazy"></p><p>→依地上標示方向，取直行往九五峰<br><img src="https://1013399.github.io/image-4/227/202592335_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→此步道屬於虎山自然步道<br><img src="https://1013399.github.io/image-4/227/202592376_l.jpg" alt="虎山自然步道" loading="lazy"></p><p>→途經十方大法禪寺<br><img src="https://1013399.github.io/image-4/227/202592383_l.jpg" alt="十方大法禪寺" loading="lazy"></p><p>→十方禪寺前觀看101大樓<br><img src="https://1013399.github.io/image-4/227/202592388_l.jpg" alt="101大樓" loading="lazy"></p><p>→叉路，直行可經山神廟上南港山稜線，取右陡上</p><figure><img src="https://1013399.github.io/image-4/227/202592392_l.jpg" alt="南港山、九五峰、九五峰東北嶺" tabindex="0" loading="lazy"><figcaption>南港山、九五峰、九五峰東北嶺</figcaption></figure><p>→梅花亭<br><img src="https://1013399.github.io/image-4/227/202592395_l.jpg" alt="梅花亭" loading="lazy"></p><p>→古色古香的黃禪園<br><img src="https://1013399.github.io/image-4/227/202592401_l.jpg" alt="黃禪園" loading="lazy"></p><p>→美雲屏<br><img src="https://1013399.github.io/image-4/227/202592405_l.jpg" alt="美雲屏" loading="lazy"></p><p>→永安亭<br><img src="https://1013399.github.io/image-4/227/202592409_l.jpg" alt="永安亭" loading="lazy"></p><p>→最後陡上<br><img src="https://1013399.github.io/image-4/227/202592416_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→上至南港山稜線，左往中華技術學院，取右行<br><img src="https://1013399.github.io/image-4/227/202592421_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→沿途叉路多，皆取上行<br><img src="https://1013399.github.io/image-4/227/202592428_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→此步道為四獸山步道<br><img src="https://1013399.github.io/image-4/227/202592435_l.jpg" alt="四獸山步道" loading="lazy"></p><p>→長壽亭<br><img src="https://1013399.github.io/image-4/227/202592439_l.jpg" alt="南港山、九五峰、九五峰東北嶺 長壽亭" loading="lazy"></p><p>→步道又再陡上<br><img src="https://1013399.github.io/image-4/227/202592442_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→福德山莊<br><img src="https://1013399.github.io/image-4/227/202592448_l.jpg" alt="南港山、九五峰、九五峰東北嶺 福德山莊 登山口" loading="lazy"></p><p>→途中雲霧稍散，觀看101大樓<br><img src="https://1013399.github.io/image-4/227/202592328_l.jpg" alt="101大樓" loading="lazy"></p><p>→上至休憩觀景處，再往前為九五峰東北嶺，有顆圖根點，但沒注意到<br><img src="https://1013399.github.io/image-4/227/202592452_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→高壓電塔013<br><img src="https://1013399.github.io/image-4/227/202592458_l.jpg" alt="高壓電塔013" loading="lazy"></p><p>→親山步道打印台<br><img src="https://1013399.github.io/image-4/227/202592466_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→左前方為超高頻電視轉播站<br><img src="https://1013399.github.io/image-4/227/202592470_l.jpg" alt="超高頻電視轉播站" loading="lazy"></p><p>→前方有座涼亭，休息後續行<br><img src="https://1013399.github.io/image-4/227/202592476_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→九五峰叉路，左為繞腰路，取直上九五峰<br><img src="https://1013399.github.io/image-4/227/202592479_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→九五峰，今日霧茫茫無視野<br><img src="https://1013399.github.io/image-4/227/202592485_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→流龍頭<br><img src="https://1013399.github.io/image-4/227/202592494_l.jpg" alt="流龍頭" loading="lazy"></p><p>→南港山，三等三角點No.695，基石位於步道旁，拍照後原路折返<br><img src="https://1013399.github.io/image-4/227/202592316_l.jpg" alt="南港山、九五峰、九五峰東北嶺" loading="lazy"></p><p>→回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時10分鐘(含休息)</p><p>高低落差：約270公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',45),c=i("br",null,null,-1),b={href:"http://www.tonyhuang39.com/tony0624/tony0624.html",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-4/227/202686254_l.jpg",alt:"南港山、九五峰、九五峰東北嶺 南港山縱走地圖",loading:"lazy"},null,-1);function u(y,j){const a=l("ExternalLinkIcon");return p(),o("div",null,[n,r(" more "),m,i("p",null,[t("(紅色為今日行走路線)"),c,t(" 原圖連結→"),i("a",b,[t("Tony的自然人文旅記"),e(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-227-2011-11-15.html.vue"]]);export{x as default};
