import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as r,c as n,a as h,b as t,e as i,d as a,w as p,f as o}from"./app-3fad0725.js";const c={},m=t("p",null,[t("strong",null,"紅淡山"),i("，海拔208公尺，位於仁愛區與信義區交界，山頂有顆三等三角點No.1024，山區因日治時期日本人遍植紅淡比樹而得名，山頂視野良好，可眺望基隆全景，素有「基隆的陽明山」的美譽，為基隆地區登山健行的最佳選擇，更是老少咸宜且極富教育性的生態休閒園區，該山名列台灣小百岳No.007。")],-1),d=t("p",null,"紅淡山登山路線大致有幾條：分別為南榮路463巷寶明寺、南榮路319巷南榮國中、三坑火車站對面的南天宮、南新街51巷、基隆金寶塔上方之佛心亭及劉銘傳路之銘傳國中，今日選擇由銘傳國中起登，是感覺路線比較簡單不複雜。",-1),_=t("p",null,"走走日期：2011/11/05",-1),b=t("h2",{id:"行車路線",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#行車路線","aria-hidden":"true"},"#"),i(" 行車路線")],-1),u=o('<h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→銘傳國中登山口<br><img src="https://1013399.github.io/image-4/229/201995948_l.jpg" alt="紅淡山 銘傳國中登山口" loading="lazy"></p><p>→登山口導覽圖<br><img src="https://1013399.github.io/image-4/229/201995955_l.jpg" alt="紅淡山登山口導覽圖" loading="lazy"></p><p>→平緩的步道階梯<br><img src="https://1013399.github.io/image-4/229/201995961_l.jpg" alt="紅淡山" loading="lazy"></p><p>→每隔一段距離，地上嵌有「紅淡山」鐵牌<br><img src="https://1013399.github.io/image-4/229/201995966_l.jpg" alt="紅淡山" loading="lazy"></p><p>→十字叉路，左往「幸福亭」，直往愛九路，取右往紅淡山<br><img src="https://1013399.github.io/image-4/229/201995976_l.jpg" alt="紅淡山" loading="lazy"></p><p>→紅淡山不鏽鋼牌樓，直行上階梯<br><img src="https://1013399.github.io/image-4/229/201995983_l.jpg" alt="紅淡山" loading="lazy"></p><p>→叉路，左側有空地，右往「山友亭」<br><img src="https://1013399.github.io/image-4/229/201995991_l.jpg" alt="紅淡山" loading="lazy"></p><p>→山友亭<br><img src="https://1013399.github.io/image-4/229/201996001_l.jpg" alt="紅淡山 山友亭" loading="lazy"></p><p>→休息後直上好漢坡<br><img src="https://1013399.github.io/image-4/229/201996013_l.jpg" alt="紅淡山" loading="lazy"></p><p>→此段稱為「雙龍山櫻花迴廊」<br><img src="https://1013399.github.io/image-4/229/201996029_l.jpg" alt="紅淡山 雙龍山櫻花迴廊" loading="lazy"></p><p>→長壽亭<br><img src="https://1013399.github.io/image-4/229/201996044_l.jpg" alt="紅淡山 長壽亭" loading="lazy"></p><p>→步道兩旁都是櫻花<br><img src="https://1013399.github.io/image-4/229/201996057_l.jpg" alt="紅淡山 櫻花" loading="lazy"></p><p>→「雙龍山紫薇亭」，好漢坡結束<br><img src="https://1013399.github.io/image-4/229/201996060_l.jpg" alt="紅淡山 雙龍山紫薇亭" loading="lazy"></p><p>→叉路，右往南榮社區，續往上行<br><img src="https://1013399.github.io/image-4/229/201996071_l.jpg" alt="紅淡山" loading="lazy"></p><p>→十字叉路<br><img src="https://1013399.github.io/image-4/229/201996083_l.jpg" alt="紅淡山" loading="lazy"></p><p>→右側為「梅花亭」及「自強山莊」<br><img src="https://1013399.github.io/image-4/229/201996093_l.jpg" alt="紅淡山" loading="lazy"></p><p>→左往「石象櫻花園」，取直行<br><img src="https://1013399.github.io/image-4/229/201996105_l.jpg" alt="紅淡山" loading="lazy"></p><p>→數公尺後又遇叉路，右往南榮社區，取左行<br><img src="https://1013399.github.io/image-4/229/201996113_l.jpg" alt="紅淡山" loading="lazy"></p><p>→十字叉路，直行往小溪頭山莊，右往觀景塔，取左行<br><img src="https://1013399.github.io/image-4/229/201996122_l.jpg" alt="紅淡山" loading="lazy"></p><p>→叉路，直行步道往許林山莊、基隆金寶塔，取左行<br><img src="https://1013399.github.io/image-4/229/201996135_l.jpg" alt="紅淡山" loading="lazy"></p><p>→叉路，往右接回剛才的直行步道，取左行<br><img src="https://1013399.github.io/image-4/229/201996152_l.jpg" alt="紅淡山" loading="lazy"></p><p>→象頭園<br><img src="https://1013399.github.io/image-4/229/201996172_l.jpg" alt="紅淡山 象頭園" loading="lazy"></p><p>→叉路，直行應該是往月眉方向，取左往三角點<br><img src="https://1013399.github.io/image-4/229/201996191_l.jpg" alt="紅淡山" loading="lazy"></p><p>→看到這兩隻狐狸就到三角點了<br><img src="https://1013399.github.io/image-4/229/201996203_l.jpg" alt="紅淡山 狐狸" loading="lazy"></p><p>→旁邊有紅淡山石刻<br><img src="https://1013399.github.io/image-4/229/201996212_l.jpg" alt="紅淡山石刻" loading="lazy"></p><p>→紅淡山，三等三角點No.1024<br><img src="https://1013399.github.io/image-4/229/201996221_l.jpg" alt="紅淡山 三等三角點No.1024" loading="lazy"></p><p>→山頂視野不錯，眺望基隆港，休息後原路折返<br><img src="https://1013399.github.io/image-4/229/201995942_l.jpg" alt="紅淡山山頂視野" loading="lazy"></p>',28),y=o('<h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時(含休息)</p><p>高低落差：約160公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',3),j={href:"http://tw.myblog.yahoo.com/fuli1961/article?mid=3627&prev=3476&next=3598",target:"_blank",rel:"noopener noreferrer"},z=t("br",null,null,-1),f=t("img",{src:"https://1013399.github.io/image-4/229/201996273_l.jpg",alt:"紅淡山 參考地圖",loading:"lazy"},null,-1);function x(N,k){const l=g("RouterLink"),e=g("ExternalLinkIcon");return r(),n("div",null,[m,h(" more "),d,_,b,t("p",null,[i("參觀完 "),a(l,{to:"/posts/post-230-2011-11-09.html"},{default:p(()=>[i("槓子寮砲台")]),_:1}),i(" 後，回往立德路接深澳坑路(102縣道)，不久轉為東光路再接東信路、信一路(皆為102縣道)，至信一路與義六路十字路口，左轉過橋後直走劉銘傳路，至路底銘傳國中前停車。")]),u,t("p",null,[i("→回到銘傳國中登山口，取車續往 "),a(l,{to:"/posts/post-228-2011-11-11.html"},{default:p(()=>[i("大武崙山")]),_:1})]),y,t("p",null,[i("原圖連結→"),t("a",j,[i("山影狐蹤部落格"),a(e)]),z,f])])}const L=s(c,[["render",x],["__file","post-229-2011-11-10.html.vue"]]);export{L as default};
