import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as o,a as r,b as i,f as a,d as e,e as s}from"./app-c5577115.js";const h={},n=i("p",null,[i("strong",null,"基隆山"),a("，原名雞籠山，又名大肚美人山，海拔588公尺，山頂有顆三等三角點No.1109，位於新北市瑞芳區九份老街附近，是一座錐形死火山，登山階梯步道沿途視野極佳，山頂平台可眺望鼻頭角、八斗子以及和平島的海岸線，秋天季節非常適合來此健行賞景，該山名列台灣小百岳No.006。")],-1),m=s('<p>趁今日東北角天氣晴朗，造訪基隆附近四座小百岳，順序為No.6基隆山、No.4槓子寮山、No.7紅淡山、No.3大武崙山，感謝老天爺賞臉，得以順利完成。</p><p>走走日期： 2011/11/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道1號北上出八堵聯絡道，接台62線再下瑞芳交流道往瑞芳，遇中山路(台2丁線)左轉，過瑞芳橋後右轉明燈路(102縣道)，經過102縣道15Km的九份老街後，約於102縣道15.3Km隔頂停車場停車(假日收費每小時50元)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→於隔頂停車場仰望基隆山<br><img src="https://1013399.github.io/image-4/231/201992936_l.jpg" alt="基隆山" loading="lazy"></p><p>→基隆山登山口<br><img src="https://1013399.github.io/image-4/231/201992946_l.jpg" alt="基隆山登山口" loading="lazy"></p><p>→第一處觀景台<br><img src="https://1013399.github.io/image-4/231/201992957_l.jpg" alt="基隆山 第一處觀景台" loading="lazy"></p><p>→觀景台旁立有登山口意象<br><img src="https://1013399.github.io/image-4/231/201992925_l.jpg" alt="基隆山登山口" loading="lazy"></p><p>→觀景台上導覽圖<br><img src="https://1013399.github.io/image-4/231/201992963_l.jpg" alt="基隆山 觀景台上導覽圖" loading="lazy"></p><p>→叉路，左為支線較緩但較遠，續直上<br><img src="https://1013399.github.io/image-4/231/201992976_l.jpg" alt="基隆山" loading="lazy"></p><p>→第二處觀景台<br><img src="https://1013399.github.io/image-4/231/201992984_l.jpg" alt="基隆山 第二處觀景台" loading="lazy"></p><p>→觀景台上有金瓜石的介紹<br><img src="https://1013399.github.io/image-4/231/201993006_l.jpg" alt="基隆山 金瓜石的介紹" loading="lazy"></p><p>→觀景台上俯瞰九份地區<br><img src="https://1013399.github.io/image-4/231/201992995_l.jpg" alt="基隆山 九份地區" loading="lazy"></p><p>→觀景台上眺望鼻頭角、陰陽海<br><img src="https://1013399.github.io/image-4/231/201993011_l.jpg" alt="基隆山 鼻頭角、陰陽海" loading="lazy"></p><p>→第三處觀景台<br><img src="https://1013399.github.io/image-4/231/201993019_l.jpg" alt="基隆山 第三處觀景台" loading="lazy"></p><p>→觀景台上遠眺五分山<br><img src="https://1013399.github.io/image-4/231/201993024_l.jpg" alt="基隆山 五分山" loading="lazy"></p><p>→基隆山盡是五節芒，幾乎看不到樹<br><img src="https://1013399.github.io/image-4/231/201993037_l.jpg" alt="基隆山 五節芒" loading="lazy"></p><p>→過了第三處觀景台，階梯更陡<br><img src="https://1013399.github.io/image-4/231/201993047_l.jpg" alt="基隆山" loading="lazy"></p><p>→第一座涼亭，不休息續往上<br><img src="https://1013399.github.io/image-4/231/201993058_l.jpg" alt="基隆山 涼亭" loading="lazy"></p><p>→叉路，支線在此接回，續直上<br><img src="https://1013399.github.io/image-4/231/201993066_l.jpg" alt="基隆山" loading="lazy"></p><p>→第二座涼亭<br><img src="https://1013399.github.io/image-4/231/201993075_l.jpg" alt="基隆山 涼亭" loading="lazy"></p><p>→基隆山山頂基地台及第三座涼亭<br><img src="https://1013399.github.io/image-4/231/201993081_l.jpg" alt="基隆山山頂基地台 第三座涼亭" loading="lazy"></p><p>→基隆山，三等三角點No.1109，就在圍牆旁<br><img src="https://1013399.github.io/image-4/231/201993087_l.jpg" alt="基隆山 三等三角點No.1109" loading="lazy"></p><p>→山頂觀景台視野絕佳<br><img src="https://1013399.github.io/image-4/231/201993107_l.jpg" alt="基隆山 山頂觀景台" loading="lazy"></p><p>→眺望深澳石油港、八斗子漁港及碧砂漁港<br><img src="https://1013399.github.io/image-4/231/201993091_l.jpg" alt="基隆山 深澳石油港、八斗子漁港及碧砂漁港" loading="lazy"></p><p>→遠眺基隆嶼<br><img src="https://1013399.github.io/image-4/231/201993096_l.jpg" alt="基隆山 基隆嶼" loading="lazy"></p><p>→眺望鼻頭角、陰陽海<br><img src="https://1013399.github.io/image-4/231/201993114_l.jpg" alt="基隆山 鼻頭角、陰陽海" loading="lazy"></p><p>→遠望無耳茶壺山<br><img src="https://1013399.github.io/image-4/231/201992911_l.jpg" alt="無耳茶壺山" loading="lazy"></p><p>→俯瞰金水公路(北34鄉道)<br><img src="https://1013399.github.io/image-4/231/201993122_l.jpg" alt="基隆山 俯瞰金水公路(北34鄉道)" loading="lazy"></p><p>→山頂觀景台右側缺口可往基隆山東峰，休息後原路折返<br><img src="https://1013399.github.io/image-4/231/201993135_l.jpg" alt="基隆山" loading="lazy"></p><p>→回到第二座涼亭<br> →回到第一座涼亭<br> →回到第三處觀景台<br> →回到第二處觀景台<br> →回到第一處觀景台，取左側水泥路回停車場<br> →回到停車場，取車續往槓子寮山</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：80分鐘(含休息、賞景)</p><p>高低落差：約260公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',35),c={href:"http://www.tadpole.net.tw/2011/04/006.html",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-4/231/201993901_l.jpg",alt:"基隆山 參考地圖",loading:"lazy"},null,-1);function _(u,j){const t=g("ExternalLinkIcon");return p(),o("div",null,[n,r(" more "),m,i("p",null,[a("原圖連結→"),i("a",c,[a("蝌蚪的網路雜記"),e(t)]),b,d])])}const f=l(h,[["render",_],["__file","post-231-2011-11-08.html.vue"]]);export{f as default};
