import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as e,b as i,e as t,d as l,f as s}from"./app-e90ae243.js";const h={},n=i("p",null,[i("strong",null,"大橫屏山"),t("，海拔1206公尺，位於南投縣國姓鄉與台中市太平區的交界，稜線呈南北走向，橫亙在台中盆地的東方，整條稜線的距離長達6公里以上，山勢雄偉，狀似一巨大屏風鎮守於此，該山名列台灣小百岳No.045。")],-1),m=i("p",null,[t("大橫屏山山頂原有二等三角點No.1119，於921大地震時崩落山谷，並造成傳統路徑的流失，2006年千岳俱樂部另闢新徑，並於山頂補立基石一顆。"),i("br"),t("     不過由於許久無人造訪，似已成為乏人問津且難以親近的小百岳，但近來網路上已陸陸續續有多篇關於大橫屏山攀登記錄，今日趁路況稍好，特來此一訪。")],-1),c=i("p",null,"攀爬大橫屏山，一定要結伴同行，並且穿著長袖長褲及穿戴手套，雖然近來有登山前輩砍草開路，但芒草、刺藤仍多，石頭長滿青苔濕滑，必須特別小心。",-1),b=i("p",null,"走走日期： 2011/10/16",-1),d=i("h2",{id:"行車路線",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#行車路線","aria-hidden":"true"},"#"),t(" 行車路線")],-1),_={href:"http://tw.myblog.yahoo.com/kentjon106/article?mid=7154&prev=7200&next=7092",target:"_blank",rel:"noopener noreferrer"},u=s('<p>國道6號下國姓交流道，接台14線往國姓鄉，於台14線約39Km處左轉133縣道，經過國姓街上再接台21線往東勢(天冷)方向，經過長流國小在台21線20.8Km處阿碧檳榔左轉進入長旗巷(農投國16)，一路沿「後角寮」、「碧山農場」指標行駛，遇叉路皆取往上方向，過「心下坑橋」及民宅後，再過鐵柵門(若關上，自行打開通過)...</p><p>→自長旗巷口行駛約8Km後，見鐵皮屋<br><img src="https://1013399.github.io/image-4/233/200144296_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→往前約200公尺，電桿「後角寮分209」<br><img src="https://1013399.github.io/image-4/233/200144313_l.jpg" alt="大橫屏山 後角寮分209" loading="lazy"></p><p>→再往前一點，於路旁較寬處停車<br><img src="https://1013399.github.io/image-4/233/200144318_l.jpg" alt="大橫屏山" loading="lazy"></p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→停車處欣賞大橫屏山，出發續行產業道路<br><img src="https://1013399.github.io/image-4/233/200144322_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→路基掏空處<br><img src="https://1013399.github.io/image-4/233/200144325_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→產道崩塌處，已可通行<br><img src="https://1013399.github.io/image-4/233/200144335_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→倒木擋道<br><img src="https://1013399.github.io/image-4/233/200144340_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→水泥產道終點，轉為土路<br><img src="https://1013399.github.io/image-4/233/200144482_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→叉路，取左上行<br><img src="https://1013399.github.io/image-4/233/200144347_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→不知道什麼花，長在路中央<br><img src="https://1013399.github.io/image-4/233/200144351_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→大橫屏山登山口，路旁樹上有三角點指示<br><img src="https://1013399.github.io/image-4/233/200144358_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→低頭鑽入芒草登山口<br><img src="https://1013399.github.io/image-4/233/200144362_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→進入樹林<br><img src="https://1013399.github.io/image-4/233/200144370_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→出現第一處拉繩，爬上後驚見一群台灣彌猴<br><img src="https://1013399.github.io/image-4/233/200144375_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→兩張鐵椅<br><img src="https://1013399.github.io/image-4/233/200144380_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→過鐵椅後，進入麻竹叢，路徑轉為陡上<br><img src="https://1013399.github.io/image-4/233/200144384_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→無拉繩，手腳並用攀爬<br><img src="https://1013399.github.io/image-4/233/200144388_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→跨過小山溝<br><img src="https://1013399.github.io/image-4/233/200144391_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→多刺的黃藤已結果<br><img src="https://1013399.github.io/image-4/233/200144394_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→路徑又轉為平緩<br><img src="https://1013399.github.io/image-4/233/200144399_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→此段落差較大，繼續攀爬<br><img src="https://1013399.github.io/image-4/233/200144403_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→進入芒萁路段<br><img src="https://1013399.github.io/image-4/233/200144410_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→爬上稜線，取右續行，又見一隻猴子<br><img src="https://1013399.github.io/image-4/233/200144413_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→觀景點視野佳，但雲霧圍繞看不清(往太平方向)<br><img src="https://1013399.github.io/image-4/233/200144420_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→上至小山頭，也有二處觀景點<br><img src="https://1013399.github.io/image-4/233/200144427_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→平行走了一段路後，開始往下走(下降落差約30M)<br><img src="https://1013399.github.io/image-4/233/200144433_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→下坡途中，仰望前方的大橫屏山<br><img src="https://1013399.github.io/image-4/233/200144442_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→下至低鞍處(需再陡上約80M落差)<br><img src="https://1013399.github.io/image-4/233/200144447_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→小心跨過滑石<br><img src="https://1013399.github.io/image-4/233/200144452_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→巨石，從左側攀爬<br><img src="https://1013399.github.io/image-4/233/200144455_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→至巨石上方，繼續攀爬<br><img src="https://1013399.github.io/image-4/233/200144461_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→最後陡上<br><img src="https://1013399.github.io/image-4/233/200144465_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→上至平緩處，從大石右側繞過<br><img src="https://1013399.github.io/image-4/233/200144477_l.jpg" alt="大橫屏山" loading="lazy"></p><p>→大橫屏山，千岳俱樂部補立二等三角點No.1119<br><img src="https://1013399.github.io/image-4/233/200144480_l.jpg" alt="大橫屏山 千岳俱樂部補立二等三角點No.1119" loading="lazy"></p><p>→午餐及休息後，原路折返<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：4小時40分鐘(上山約2小時30分鐘、下山約1小時40分鐘、午餐及休息約30分鐘)</p><p>高低落差：約370公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',40),y={href:"http://tw.myblog.yahoo.com/kentjon106/photo?pid=7156",target:"_blank",rel:"noopener noreferrer"},j=i("br",null,null,-1),z=i("img",{src:"https://1013399.github.io/image-4/233/200144920_l.jpg",alt:"大橫屏山 參考地圖",loading:"lazy"},null,-1);function f(x,N){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,e(" more "),m,c,b,d,i("p",null,[t("(詳細路線圖解可參考→"),i("a",_,[t("蜜豆的家"),l(a)]),t(")")]),u,i("p",null,[t("原圖連結→"),i("a",y,[t("蜜豆的家-部落格相簿"),l(a)]),j,z])])}const V=g(h,[["render",f],["__file","post-233-2011-10-18.html.vue"]]);export{V as default};
