import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as e,c as r,a as s,b as t,f as i,d as l,e as g}from"./app-a39bec1a.js";const n={},h=t("p",null,[t("strong",null,"後尖山"),i("，海拔1,008公尺，位於南投縣魚池鄉與水里鄉界稜，在日月潭風景區南面，為日月潭四兄妹老么，山頂有一顆三等三角點No.5009，設有休憩觀景台，展望極佳，可欣賞著日月潭美麗湖光山色，步道沿線植物林相完整優美，該山名列台灣小百岳No.052。")],-1),c=g('<p>後尖山有數處登山口，今日走「頭社佛堂」的傳統登山步道(後尖山步道B段)，頭社佛堂設有廁所、停車場，非常方便。</p><p>走走日期： 2011/10/01</p><h2 id="後尖山-行車路線" tabindex="-1"><a class="header-anchor" href="#後尖山-行車路線" aria-hidden="true">#</a> 後尖山 行車路線</h2><p>上午走完集集大山南峰後，回到投27鄉道取左往水里，接台16線後，於水里再轉接台21線往日月潭，於台21線69.1km處右轉進入協力巷(投62鄉道)往武登社區，約3.1km叉路依指標右轉，到達頭社佛堂停車場停車。</p><h2 id="後尖山-走走路線" tabindex="-1"><a class="header-anchor" href="#後尖山-走走路線" aria-hidden="true">#</a> 後尖山 走走路線</h2><p>→頭社佛堂停車場<br><img src="https://1013399.github.io/image-4/234/199001917_l.jpg" alt="後尖山 頭社佛堂停車場" loading="lazy"></p><p>→停車場旁導覽圖<br><img src="https://1013399.github.io/image-4/234/199001919_l.jpg" alt="後尖山 導覽圖" loading="lazy"></p><p>→後尖山步道B段起點，步道每100公尺設有里程碑，埋設於地上<br><img src="https://1013399.github.io/image-4/234/199001921_l.jpg" alt="後尖山" loading="lazy"></p><p>→需先緩上一段產業道路<br><img src="https://1013399.github.io/image-4/234/199001925_l.jpg" alt="後尖山" loading="lazy"></p><p>→左有叉路，續直行<br><img src="https://1013399.github.io/image-4/234/199001926_l.jpg" alt="後尖山" loading="lazy"></p><p>→叉路，取右過小橋<br><img src="https://1013399.github.io/image-4/234/199001930_l.jpg" alt="後尖山" loading="lazy"></p><p>→過小橋後已走了400公尺<br><img src="https://1013399.github.io/image-4/234/199001933_l.jpg" alt="後尖山" loading="lazy"></p><p>→路徑轉為枕木步道<br><img src="https://1013399.github.io/image-4/234/199001934_l.jpg" alt="後尖山" loading="lazy"></p><p>→進入竹林<br><img src="https://1013399.github.io/image-4/234/199001936_l.jpg" alt="後尖山" loading="lazy"></p><p>→出現扶手，表示步道開始轉趨陡上<br><img src="https://1013399.github.io/image-4/234/199001940_l.jpg" alt="後尖山" loading="lazy"></p><p>→路左涼亭，似乎有些荒廢<br><img src="https://1013399.github.io/image-4/234/199001942_l.jpg" alt="後尖山" loading="lazy"></p><p>→900M里程碑，剩下100公尺(實際上絕對不只100公尺)<br><img src="https://1013399.github.io/image-4/234/199001945_l.jpg" alt="後尖山" loading="lazy"></p><p>→續行竹林間步道<br><img src="https://1013399.github.io/image-4/234/199001947_l.jpg" alt="後尖山" loading="lazy"></p><p>→叉路，直往福興宮可至頭社山，取右上<br><img src="https://1013399.github.io/image-4/234/199001949_l.jpg" alt="後尖山" loading="lazy"></p><p>→準備開始最後陡上，旁有警示牌<br><img src="https://1013399.github.io/image-4/234/199001952_l.jpg" alt="後尖山" loading="lazy"></p><p>→一路陡上<br><img src="https://1013399.github.io/image-4/234/199001955_l.jpg" alt="後尖山" loading="lazy"></p><p>→往回俯瞰步道<br><img src="https://1013399.github.io/image-4/234/199001958_l.jpg" alt="後尖山" loading="lazy"></p><p>→後尖山，山頂雙涼亭<br><img src="https://1013399.github.io/image-4/234/199001965_l.jpg" alt="後尖山 山頂雙涼亭" loading="lazy"></p><p>→後尖山，三等三角點No.5009<br><img src="https://1013399.github.io/image-4/234/199001982_l.jpg" alt="後尖山 三等三角點No.5009" loading="lazy"></p><p>→山頂視野極佳，眺望日月潭山水美景(右涼亭視野)<br><img src="https://1013399.github.io/image-4/234/199001970_l.jpg" alt="後尖山山頂視野 日月潭山水美景" loading="lazy"></p><p>→右涼亭解說牌<br><img src="https://1013399.github.io/image-4/234/199001976_l.jpg" alt="後尖山 解說牌" loading="lazy"></p><p>→不知哪座是水社大山(右涼亭視野)<br><img src="https://1013399.github.io/image-4/234/199002004_l.jpg" alt="後尖山" loading="lazy"></p><p>→這座應該是集集大山(左涼亭視野)<br><img src="https://1013399.github.io/image-4/234/199001995_l.jpg" alt="後尖山" loading="lazy"></p><p>→涼亭續行可往台21線69.5km的登山口(越過左涼亭欄杆可往雨社山)<br><img src="https://1013399.github.io/image-4/234/199001990_l.jpg" alt="後尖山" loading="lazy"></p><p>→賞景、休息後，原路折返<br> →回到頭社佛堂停車場</p><h2 id="後尖山-走走資訊" tabindex="-1"><a class="header-anchor" href="#後尖山-走走資訊" aria-hidden="true">#</a> 後尖山 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：上山約50分鐘、下山約40分鐘</p><p>高低落差：約300公尺</p></div><h2 id="後尖山-參考地圖" tabindex="-1"><a class="header-anchor" href="#後尖山-參考地圖" aria-hidden="true">#</a> 後尖山 參考地圖</h2>',33),d={href:"http://blog.xuite.net/laoweiz/blog/15849973",target:"_blank",rel:"noopener noreferrer"},m=t("br",null,null,-1),b=t("img",{src:"https://1013399.github.io/image-4/234/199002045_l.jpg",alt:"後尖山 參考地圖",loading:"lazy"},null,-1),_=t("h2",{id:"向山遊客中心-行車路線",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#向山遊客中心-行車路線","aria-hidden":"true"},"#"),i(" 向山遊客中心 行車路線")],-1),u=t("p",null,"回程走台21線往埔里，經過日月潭，停車逛逛向山遊客中心。",-1),z=t("p",null,[t("strong",null,"向山行政暨遊客中心"),i("，位處台21線水社隧道附近，鳥瞰建築物，猶如雙手環抱整個日月潭的湖光山色，其中兩個長度達三十四公尺高八公尺的超大跨度之頂版結構；日月潭的湖光山色穿過寬闊的跨距空間，直接映入遊客眼中，創造了人與自然間發展對話的舞台，雖為建築物，但若說它是一件藝術品也不為過。")],-1),j=t("p",null,"向山園區前棟為辦公處所，後棟則為遊客中心。遊客中心展場包含「悠遊水沙連-產業館」、「日昇月恆-藝廊」、「潭情日月-自然人文館」及「多媒體影音播放室」，分別展出當地的產業、工藝和旅遊資源，其中「產業館」網羅了大日月潭地區的陶藝、紙藝、工藝、酒類、紅茶及農特產等特色產業，並提供產業資訊；「藝廊」以潭區附近工藝名家作品展為主題；「自然人文館」以影音導覽認識日月潭自然人文與歷史。",-1),y={href:"http://www.sunmoonlake.gov.tw/TW/03002187.aspx",target:"_blank",rel:"noopener noreferrer"},f=g('<p>→向山遊客中心停車場<br><img src="https://1013399.github.io/image-4/234/199002010_l.jpg" alt="向山遊客中心 停車場" loading="lazy"></p><p>→向山遊客中心入口<br><img src="https://1013399.github.io/image-4/234/199002016_l.jpg" alt="向山遊客中心 入口" loading="lazy"></p><p>→遊客中心一角<br><img src="https://1013399.github.io/image-4/234/199002023_l.jpg" alt="向山遊客中心" loading="lazy"></p><p>→日月潭湖光山色<br><img src="https://1013399.github.io/image-4/234/199002027_l.jpg" alt="日月潭湖光山色" loading="lazy"></p><p>→產業館<br><img src="https://1013399.github.io/image-4/234/199001915_l.jpg" alt="向山遊客中心 產業館" loading="lazy"></p>',5);function x(k,N){const a=o("ExternalLinkIcon");return e(),r("div",null,[h,s(" more "),c,t("p",null,[i("原圖連結→"),t("a",d,[i("老尉子的部落格"),l(a)]),m,b]),_,u,z,j,t("p",null,[i("(參考資料："),t("a",y,[i("日月潭國家風景區管理處"),l(a)]),i(")")]),f])}const B=p(n,[["render",x],["__file","post-234-2011-10-06.html.vue"]]);export{B as default};
