import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as o,b as i,f as t,d as g,e}from"./app-2f66486a.js";const s={},h=i("p",null,[i("strong",null,"九份二山"),t("，海拔1174公尺，位於南投縣國姓鄉南港村，鄰近大茅埔雙冬斷層，為西部麓山帶著名中級山，該山名列台灣小百岳No.047。")],-1),n=i("p",null,"九份二山位在921震央北側，原本秀麗的山岳在震波猛烈衝擊下，北側山稜霎那間狂瀉而下，滑落澀子坑和韭菜湖溪，形成兩座堰塞湖和蒼茫險峻崩壁，殘破景象紀錄著百年地震的悲劇，九份二山一夕成名。",-1),m=i("p",null,"浩劫後的九份二山，有著崢嶸險巇的破碎大地，卻也因此擁有獨特的壯麗景緻，望之，依然令人怵目驚心！堰塞湖畔不遠處的震災紀念碑，以及往鳳鳴方向的震央驚爆點、磁場屋，共同構成獨具特色的震災自然公園。",-1),b={href:"http://www.taiwan100mt.com/mountain.php?mountain=47",target:"_blank",rel:"noopener noreferrer"},c=e('<p>走走日期： 2011/09/24</p><h2 id="九份二山-行車路線" tabindex="-1"><a class="header-anchor" href="#九份二山-行車路線" aria-hidden="true">#</a> 九份二山 行車路線</h2><p>國道3號南下接國道6號，下國姓交流道接台14線(中正路)往國姓方向，於台14線45km北山隧道附近，右轉北山橋接147縣道，147縣道2.4km處叉路有指標往九份二山不取(投60鄉道、龍南道路，由此會經過傾斜屋、震爆點，但往九份二山較遠)，續直行於147縣道4.5km處過南港國小後右轉進入長石巷(投67鄉道)，里程在此歸零...</p><p>→行駛1.9km遇叉路(左為投66鄉道往金瓜山)，取右上(依「無極聖極殿」指標方向)<br><img src="https://1013399.github.io/image-4/236/198385101_l.jpg" alt="九份二山" loading="lazy"></p><p>→2.3km抵達「石門觀景點」，設有停車場、廁所，停車參觀<br><img src="https://1013399.github.io/image-4/236/198385102_l.jpg" alt="九份二山 石門觀景點 停車場" loading="lazy"></p><p>→停車場旁九份二山國家地震紀念地導覽圖<br><img src="https://1013399.github.io/image-4/236/198385105_l.jpg" alt="九份二山國家地震紀念地導覽圖" loading="lazy"></p><p>→石門觀景點展望<br><img src="https://1013399.github.io/image-4/236/198385107_l.jpg" alt="九份二山 石門觀景點" loading="lazy"></p><p>→不遠處有土石流觀測站，取車續行<br><img src="https://1013399.github.io/image-4/236/198385110_l.jpg" alt="九份二山" loading="lazy"></p><p>→至3.1km叉路，先取右下水泥路往堰塞湖<br><img src="https://1013399.github.io/image-4/236/198385111_l.jpg" alt="九份二山" loading="lazy"></p><p>→叉路對面為「官氏養鹿場」<br><img src="https://1013399.github.io/image-4/236/198385113_l.jpg" alt="九份二山 官氏養鹿場" loading="lazy"></p><p>→堰塞湖也設有停車場、廁所，停車參觀<br><img src="https://1013399.github.io/image-4/236/198385124_l.jpg" alt="九份二山 堰塞湖" loading="lazy"></p><p>→堰塞湖(韭菜湖)<br><img src="https://1013399.github.io/image-4/236/198385117_l.jpg" alt="九份二山 堰塞湖" loading="lazy"></p><p>→仰望九份二山，續往前可至地震紀念碑(當時沒注意便往回續行)<br><img src="https://1013399.github.io/image-4/236/198385128_l.jpg" alt="九份二山" loading="lazy"></p><p>→至4.4km重要叉路，右接龍南道路，往震爆點、傾斜屋，取左續行(依「無極聖極殿」指標方向)<br><img src="https://1013399.github.io/image-4/236/198385130_l.jpg" alt="九份二山" loading="lazy"></p><p>→經過4.6km的「無極聖極殿」後，約於5.5km抵達登山口<br><img src="https://1013399.github.io/image-4/236/198385136_l.jpg" alt="九份二山" loading="lazy"></p><p>→登山口旁的「九份二山三角點生態休閒步道導覽圖」<br><img src="https://1013399.github.io/image-4/236/198385168_l.jpg" alt="九份二山" loading="lazy"></p><h2 id="九份二山-走走路線" tabindex="-1"><a class="header-anchor" href="#九份二山-走走路線" aria-hidden="true">#</a> 九份二山 走走路線</h2><p>→整裝後進入小產業道路，之字型一路陡上<br><img src="https://1013399.github.io/image-4/236/198385139_l.jpg" alt="九份二山" loading="lazy"></p><p>→進入竹林小產道<br><img src="https://1013399.github.io/image-4/236/198385141_l.jpg" alt="九份二山" loading="lazy"></p><p>→產道終點，取右進入步道，雜草稍多<br><img src="https://1013399.github.io/image-4/236/198385144_l.jpg" alt="九份二山" loading="lazy"></p><p>→依九份二山指示牌前進<br><img src="https://1013399.github.io/image-4/236/198385147_l.jpg" alt="九份二山" loading="lazy"></p><p>→進入樹林，路跡明顯，石頭較滑需小心<br><img src="https://1013399.github.io/image-4/236/198385150_l.jpg" alt="九份二山" loading="lazy"></p><p>→左側開始有護欄<br><img src="https://1013399.github.io/image-4/236/198385152_l.jpg" alt="九份二山" loading="lazy"></p><p>→步道或陡或緩(陡上居多)<br><img src="https://1013399.github.io/image-4/236/198385155_l.jpg" alt="九份二山" loading="lazy"></p><p>→第一顆省地政局基石<br><img src="https://1013399.github.io/image-4/236/198385157_l.jpg" alt="九份二山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-4/236/198385159_l.jpg" alt="九份二山" loading="lazy"></p><p>→第二顆省地政局基石<br><img src="https://1013399.github.io/image-4/236/198385161_l.jpg" alt="九份二山" loading="lazy"></p><p>→九份二山山頂，涼亭有視野但不佳<br><img src="https://1013399.github.io/image-4/236/198385162_l.jpg" alt="九份二山" loading="lazy"></p><p>→九份二山一等三角點<br><img src="https://1013399.github.io/image-4/236/198385165_l.jpg" alt="九份二山" loading="lazy"></p><p>→休息後原路折返<br> →回到登山口</p><h2 id="九份二山-走走資訊" tabindex="-1"><a class="header-anchor" href="#九份二山-走走資訊" aria-hidden="true">#</a> 九份二山 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：上山約50分鐘、下山約40分鐘</p><p>高低落差：約310公尺</p></div><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>取車後原路回，回到4.4km重要叉路，取左往震爆點、傾斜屋(接上龍南道路之前，道路又彎又窄、會車不易)...</p><p>→七號觀景台，停車參觀<br><img src="https://1013399.github.io/image-4/236/198385170_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→觀景台展望佳，右下為第二處堰塞湖(澀子坑)<br><img src="https://1013399.github.io/image-4/236/198385172_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→觀景台上解說牌<strong>─</strong>追根究柢，取車續行，之後遇兩處叉路皆取右<br><img src="https://1013399.github.io/image-4/236/198385174_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→巨石觀景區，停車參觀<br><img src="https://1013399.github.io/image-4/236/198385176_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→觀景台解說牌<br><img src="https://1013399.github.io/image-4/236/198385177_l.jpg" alt="九二一地震國家紀念地 觀景台解說牌" loading="lazy"></p><p>→前方不遠處叉路，取車往左後前往震爆點(此為往回拍攝)<br><img src="https://1013399.github.io/image-4/236/198385178_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→叉路旁有指標<br><img src="https://1013399.github.io/image-4/236/198385180_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→九份二山震爆點，旁有停車場、廁所及小店，停車參觀<br><img src="https://1013399.github.io/image-4/236/198385182_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→停車場旁解說牌<br><img src="https://1013399.github.io/image-4/236/198385181_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→觀景台上解說牌─<strong>地震天威</strong><br><img src="https://1013399.github.io/image-4/236/198385184_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→觀景台上展望，至小店品嚐一份「黑豆干夾酸菜」後，回往龍南道路續行<br><img src="https://1013399.github.io/image-4/236/198385185_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→「九份二山震災紀念園區」意象，拍照後續行<br><img src="https://1013399.github.io/image-4/236/198385186_l.jpg" alt="九份二山震災紀念園區" loading="lazy"></p><p>→九份二山國家地震紀念地，後方即為傾斜屋，旁有攤販，停車參觀<br><img src="https://1013399.github.io/image-4/236/198385188_l.jpg" alt="九二一地震國家紀念地" loading="lazy"></p><p>→傾斜屋，入內參觀，真的會頭暈，休息後開始回程<br><img src="https://1013399.github.io/image-4/236/198385097_l.jpg" alt="九二一地震國家紀念地 傾斜屋" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',49),d={href:"http://blog.xuite.net/laoweiz/blog/15440569",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-4/236/198385795_l.jpg",alt:"九份二山、九二一地震國家紀念地 參考地圖",loading:"lazy"},null,-1);function z(j,y){const a=p("ExternalLinkIcon");return r(),o("div",null,[h,n,m,i("p",null,[t("(參考資料："),i("a",b,[t("歐都納小百岳大百科"),g(a)]),t(")")]),c,i("p",null,[t("原圖連結→"),i("a",d,[t("老尉子的部落格"),g(a)]),_,u])])}const x=l(s,[["render",z],["__file","post-236-2011-09-27.html.vue"]]);export{x as default};
