import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as r,b as t,f as a,d as l,a as o,e as n}from"./app-91fc5112.js";const s={},h=t("p",null,[t("strong",null,"獅頭山風景區"),a("，屬於「參山國家風景區」，涵蓋新竹縣的竹東鎮、北埔鄉、峨眉鄉和苗栗縣的三灣鄉、南庄鄉，共5鄉鎮，面積約24,221公頃，獅頭山有許多寺廟、道觀依山勢而建，古樸而壯觀，如輔天宮、勸化堂、開善寺、獅巖洞(元光寺)等，周遭環境雅靜，早已是著名的朝聖地。")],-1),c={href:"http://www.trimt-nsa.gov.tw/cht/unit_04_1.aspx",target:"_blank",rel:"noopener noreferrer"},d=t("strong",null,"獅山古道",-1),m={href:"http://blog.xuite.net/shiun101/1013399/24669799",target:"_blank",rel:"noopener noreferrer"},b=n('<p><strong>獅頭山</strong>，海拔492公尺，行政區屬於苗栗縣南庄鄉，山頂有三等三角點No.25及氣象局雨量自動測報中繼站，展望佳。該山名列台灣小百岳No.029。</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>開車行駛台3線北上至86.5k處，左轉竹41鄉道往獅頭山，過獅山遊客服務中心(獅尾登山口)續行，接苗20鄉道後右轉往勸化堂(有明顯指標)，將車停於勸化堂立體停車場。</p><p>走走日期：2011/08/14</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→獅頭山步道口，左下往立體停車場，取右穿越攤販進入石階步道<br><img src="https://1013399.github.io/image-4/239/194926209_l.jpg" alt="獅頭山步道口 左下往立體停車場" loading="lazy"></p><p>→左右兩線皆可<br><img src="https://1013399.github.io/image-4/239/194926212_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→勸善亭<br><img src="https://1013399.github.io/image-4/239/194926214_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→叉路，取右續上石階<br><img src="https://1013399.github.io/image-4/239/194926216_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→紫陽門<br><img src="https://1013399.github.io/image-4/239/194926218_l.jpg" alt="獅山古道(南庄線)、獅頭山 紫陽門" loading="lazy"></p><p>→輔天宮前展望佳，眺望南庄地區<br><img src="https://1013399.github.io/image-4/239/194926221_l.jpg" alt="獅山古道(南庄線)、獅頭山 輔天宮" loading="lazy"></p><p>→輔天宮<br><img src="https://1013399.github.io/image-4/239/194926223_l.jpg" alt="獅山古道(南庄線)、獅頭山 輔天宮" loading="lazy"></p><p>→眺望西王金母舍利洞，取右續行<br><img src="https://1013399.github.io/image-4/239/194926229_l.jpg" alt="獅山古道(南庄線)、獅頭山 西王金母舍利洞" loading="lazy"></p><p>→通往勸化堂的石階<br><img src="https://1013399.github.io/image-4/239/194926234_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→清虛門<br><img src="https://1013399.github.io/image-4/239/194926239_l.jpg" alt="獅山古道(南庄線)、獅頭山 清虛門" loading="lazy"></p><p>→勸化堂，廟前展望也很好<br><img src="https://1013399.github.io/image-4/239/194926242_l.jpg" alt="獅山古道(南庄線)、獅頭山 勸化堂" loading="lazy"></p><p>→過勸化堂後續往望月亭<br><img src="https://1013399.github.io/image-4/239/194926243_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→道德門<br><img src="https://1013399.github.io/image-4/239/194926245_l.jpg" alt="獅山古道(南庄線)、獅頭山 道德門" loading="lazy"></p><p>→過道德門後叉路，左往舍利洞、開善寺，續直上往望月亭<br><img src="https://1013399.github.io/image-4/239/194926247_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→古道清幽，涼爽舒適<br><img src="https://1013399.github.io/image-4/239/194926249_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→獅山大石壁叉路，左後往觀日亭，續直行往望月亭<br><img src="https://1013399.github.io/image-4/239/194926251_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→「即心是佛」<br><img src="https://1013399.github.io/image-4/239/194926252_l.jpg" alt="獅山古道(南庄線)、獅頭山 即心是佛" loading="lazy"></p><p>→古道開始陡上<br><img src="https://1013399.github.io/image-4/239/194926254_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→望月亭，獅山古道越嶺點，於此休息<br><img src="https://1013399.github.io/image-4/239/194926255_l.jpg" alt="獅山古道(南庄線)、獅頭山 望月亭" loading="lazy"></p><p>→新竹縣、苗栗縣界碑<br><img src="https://1013399.github.io/image-4/239/194926257_l.jpg" alt="獅山古道(南庄線)、獅頭山 新竹縣、苗栗縣界碑" loading="lazy"></p><p>→取攤販旁山路往獅頭山三角點，約700公尺<br><img src="https://1013399.github.io/image-4/239/194926258_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→氣象局雨量自動測報中繼站<br><img src="https://1013399.github.io/image-4/239/194926260_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→獅頭山三等三角點No.25，拍照後原路折返<br><img src="https://1013399.github.io/image-4/239/194926204_l.jpg" alt="獅山古道(南庄線)、獅頭山" loading="lazy"></p><p>→回到望月亭<br> →回到勸化堂<br> →回到輔天宮<br> →回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約100分鐘(含休息)</p><p>高低落差：約200公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',32),_={href:"http://www.tonyhuang39.com/tony0456/tony0456.html",target:"_blank",rel:"noopener noreferrer"},u=t("br",null,null,-1),y=t("img",{src:"https://1013399.github.io/image-4/239/194926301_l.jpg",alt:"獅山古道(南庄線)、獅頭山 參考地圖",loading:"lazy"},null,-1);function j(z,f){const i=p("ExternalLinkIcon");return e(),r("div",null,[h,t("p",null,[a("(參考資料："),t("a",c,[a("參山國家風景區管理處"),l(i)]),a(")")]),o(" more "),t("p",null,[d,a("，「獅尾」是由峨眉鄉的獅山旅遊服務中心旁的獅山牌樓起登，現已變為平緩柏油路車道，路程較長，途經萬佛庵、金剛寺、靈霞洞、海會庵、元光寺至望月亭(曾於2009/06/07造訪過，相關旅記→"),t("a",m,[a("點此文章"),l(i)]),a(")。而「獅頭」則由南庄鄉的勸化堂停車場起登，全程皆為石階步道，路程較短但較陡，途經紫陽門、輔天宮、清虛門、勸化堂、道德門、大石壁至望月亭，途中還有叉路可往舍利洞、開善寺、凌雲洞、觀日亭等，獅頭山可說是著名的寺廟聖地。")]),b,t("p",null,[a("原圖連結→"),t("a",_,[a("Tony的自然人文旅記"),l(i)]),u,y])])}const N=g(s,[["render",j],["__file","post-239-2011-08-16.html.vue"]]);export{N as default};
