import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as o,o as e,c as r,a as s,b as t,e as i,d as l,f as g}from"./app-272e1b70.js";const h={},n=t("p",null,[t("strong",null,"砂卡噹步道"),i("原名神秘谷步道，入口位於砂卡礑隧道口，至終點三間屋，全長4.5公里，往返約3小時。步道十分平坦，適合一般老少婦孺健行活動。砂卡礑太魯閣語意為「臼齒」，因昔日建社時掘出臼齒而得名。")],-1),m=t("p",null,"步道沿砂卡礑溪開闢，沿途林蔭涼爽，設置許多觀景解說平台，可欣賞峽谷景觀及水石之美，尤其是溪水中的微酸溶解大理岩中碳酸鈣成分，使溪水呈現美麗的天藍色，加上大理岩壁的紋理變化多端，令人感受到造物之奇嘆為觀止。",-1),c=g('<h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號北上，於南港系統交流道轉國道5號，行至國道5號終點蘇澳，接蘇花公路(台9線)到達太魯閣國家公園遊客中心，休息、午餐後從遊客中心往西經砂卡礑隧道、過砂卡礑橋，於路左停車場停車。(若停車場已滿，則回遊客中心停車，再步行穿越砂卡礑隧道，也可達砂卡礑步道口，步行約15分鐘)</p><p>走走日期：2011/07/30</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→砂卡礑隧道(停妥車後往回走向橋頭步道口)<br><img src="https://1013399.github.io/image-4/241/194368068_l.jpg" alt="砂卡礑隧道" loading="lazy"></p><p>→砂卡礑步道入口，循鐵梯迴旋下至橋下<br><img src="https://1013399.github.io/image-4/241/194368072_l.jpg" alt="砂卡礑步道入口" loading="lazy"></p><p>→步道口解說牌<br><img src="https://1013399.github.io/image-4/241/194368076_l.jpg" alt="砂卡噹步道口解說牌" loading="lazy"></p><p>→由橋下往回望砂卡礑橋<br><img src="https://1013399.github.io/image-4/241/194368053_l.jpg" alt="望砂卡礑橋" loading="lazy"></p><p>→步道沿著砂卡礑溪而闢，平緩好走<br><img src="https://1013399.github.io/image-4/241/194368080_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→美麗的砂卡礑溪<br><img src="https://1013399.github.io/image-4/241/194368084_l.jpg" alt="砂卡礑溪" loading="lazy"></p><p>→伊亞峽谷解說牌<br><img src="https://1013399.github.io/image-4/241/194368089_l.jpg" alt="伊亞峽谷解說牌" loading="lazy"></p><p>→大石錯落於峽谷間<br><img src="https://1013399.github.io/image-4/241/194368093_l.jpg" alt="砂卡噹步道 伊亞峽谷" loading="lazy"></p><p>→漂亮的岩壁紋路<br><img src="https://1013399.github.io/image-4/241/194368097_l.jpg" alt="砂卡噹步道 岩壁紋路" loading="lazy"></p><p>→步道有些地方較低，高個子需注意<br><img src="https://1013399.github.io/image-4/241/194368100_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→漸漸進入精華段，岩壁宛如山水畫<br><img src="https://1013399.github.io/image-4/241/194470565_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→小觀景台，旁邊原有木棧階梯可下至溪底，但已封閉<br><img src="https://1013399.github.io/image-4/241/194368119_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→往回望步道也很美<br><img src="https://1013399.github.io/image-4/241/194368128_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→五間屋，太魯閣族斯維奇部落居地<br><img src="https://1013399.github.io/image-4/241/194368134_l.jpg" alt="砂卡噹步道 五間屋 太魯閣族斯維奇部落居地" loading="lazy"></p><p>→步道轉為碎石路<br><img src="https://1013399.github.io/image-4/241/194368136_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→巨大的大理石，在此下切溪邊休息、戲水<br><img src="https://1013399.github.io/image-4/241/194368140_l.jpg" alt="砂卡噹步道 大理石" loading="lazy"></p><p>→粗水管，立霧電廠的輸水管線<br><img src="https://1013399.github.io/image-4/241/194368143_l.jpg" alt="砂卡噹步道 立霧電廠的輸水管線" loading="lazy"></p><p>→此處溪中較多巨石<br><img src="https://1013399.github.io/image-4/241/194470563_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→對岸山腰細長的瀑布<br><img src="https://1013399.github.io/image-4/241/194368161_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→步道右側疑似房屋遺址，山蘇遍佈<br><img src="https://1013399.github.io/image-4/241/194368163_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→攔水壩解說牌<br><img src="https://1013399.github.io/image-4/241/194368167_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→攔砂壩前休息平台<br><img src="https://1013399.github.io/image-4/241/194368172_l.jpg" alt="砂卡噹步道 攔砂壩" loading="lazy"></p><p>→攔砂(水)壩<br><img src="https://1013399.github.io/image-4/241/194368181_l.jpg" alt="砂卡噹步道 攔砂壩" loading="lazy"></p><p>→續行步道往三間屋，此處炎熱無蔭<br><img src="https://1013399.github.io/image-4/241/194368183_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→進入檳榔園<br><img src="https://1013399.github.io/image-4/241/194368186_l.jpg" alt="砂卡噹步道 檳榔園" loading="lazy"></p><p>→小鐵橋<br><img src="https://1013399.github.io/image-4/241/194368193_l.jpg" alt="砂卡噹步道 小鐵橋" loading="lazy"></p><p>→里程3.9K處<br><img src="https://1013399.github.io/image-4/241/194368196_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→路左疑似房屋遺址，不知是否為三間屋<br><img src="https://1013399.github.io/image-4/241/194368202_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→叉路，右為大禮大同步道，往左禁止進入<br><img src="https://1013399.github.io/image-4/241/194368204_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→大禮大同步道簡介牌<br><img src="https://1013399.github.io/image-4/241/194368210_l.jpg" alt="大禮大同步道簡介牌" loading="lazy"></p><p>→取左試行一段後折返<br><img src="https://1013399.github.io/image-4/241/194368214_l.jpg" alt="砂卡噹步道" loading="lazy"></p><p>→回到攔砂壩<br> →回到粗水管<br> →回到五間屋</p>',36),d=t("br",null,null,-1),b=t("img",{src:"https://1013399.github.io/image-4/241/194368217_l.jpg",alt:"砂卡噹步道",loading:"lazy"},null,-1),_=t("br",null,null,-1),u=t("img",{src:"https://1013399.github.io/image-4/241/194368220_l.jpg",alt:"砂卡噹步道",loading:"lazy"},null,-1),j={href:"http://photo.xuite.net/_pic/shiun101/5063728/194368220_l.jpg/redir",target:"_blank",rel:"noopener noreferrer"},z=g('<p>→回到砂卡礑橋<br> →回到砂卡礑停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約3.5小時(含休息、戲水)</p><p>高低落差：約120公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',4),y={href:"http://www.taroko.gov.tw/zhTW/Content.aspx?tm=1&mm=6&sm=1&page=4&id=5#up",target:"_blank",rel:"noopener noreferrer"},f=t("br",null,null,-1),x=t("img",{src:"https://1013399.github.io/image-4/241/194368294_l.jpg",alt:"太魯閣國家公園 砂卡噹步道 參考地圖",loading:"lazy"},null,-1);function k(v,N){const a=o("ExternalLinkIcon");return e(),r("div",null,[n,m,s(" more "),c,t("p",null,[i("→步道回程也滿美的"),d,b,_,u,t("a",j,[l(a)])]),z,t("p",null,[i("原圖連結→"),t("a",y,[i("太魯閣國家公園"),l(a)]),f,x])])}const B=p(h,[["render",k],["__file","post-241-2011-08-09.html.vue"]]);export{B as default};
