import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o,c as p,a as r,b as t,e as a,d as g,f as s}from"./app-e9e80f6e.js";const n={},h=t("p",null,[t("strong",null,"東穗山"),a("，又名玉峰山，海拔1724公尺，位於新竹縣尖石鄉宇老附近，山頂有三等三角點No.6250，樹林間無展望。一般山友造訪此山，都會連走島田山、高台山，或石麻達山、屯野生台山，但是需考慮接駁問題，如果只單攻東穗山，一般來回只約需2小時左右。")],-1),c=s('<p>造訪東穗山，可在竹60鄉道22.8K附近右上產業道路，約5分鐘後進入第一段竹林，過竹林進入第二段雜木林開始一路陡上(缺少拉繩輔助，且今日非常濕滑難走)，上至稜線(宇老叉路)進入第三段繼續陡上(比前段好走)，攻頂前第四段箭竹林，則需撥開箭竹，才看得到路徑，故攀爬此山宜格外小心。<br>     近日來天氣炎熱，走訪郊山一定是酷熱難耐，便考慮往中級山走走，但又擔心午後雷陣雨，於是找個行程較短的東穗山走走，但受前幾日山區午後陣雨的影響，山徑又陡又濕滑，走來頗為辛苦，小心翼翼原路來回，原本約2小時的行程，竟花了約3個小時才走完。</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>由竹東走台3線往北，經橫山&quot;九讚頭&quot;右轉走竹35鄉道，過永豐橋接120縣道往尖石、內灣方向(如此可避開假日台3線往內灣的塞車路段)，過尖石鄉公所後，右轉過尖石大橋接竹60鄉道，至羅那溪與錦屏溪交會的三叉路口時，左轉過錦屏大橋，穿過&quot;泰雅勇士塑像&quot;的明隧道，過了&quot;宇老&quot;續行至竹60鄉道約22.8K附近，於路旁較寬、較平緩處停車。</p><p>走走日期：2011/06/12</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→竹60鄉道22.8K處，取右上產道<br><img src="https://1013399.github.io/image-4/245/189468358_l.jpg" alt="竹60鄉道22.8K處 東穗山" loading="lazy"></p><p>→叉路，右上為產道的盡頭，右側有間鐵皮工寮，續直行土路<br><img src="https://1013399.github.io/image-4/245/189468377_l.jpg" alt="東穗山" loading="lazy"></p><p>→東穗山登山口，進入竹林<br><img src="https://1013399.github.io/image-4/245/189468389_l.jpg" alt="東穗山登山口" loading="lazy"></p><p>→竹林路徑有些不明顯<br><img src="https://1013399.github.io/image-4/245/189468457_l.jpg" alt="東穗山" loading="lazy"></p><p>→走出竹林<br><img src="https://1013399.github.io/image-4/245/189468452_l.jpg" alt="東穗山" loading="lazy"></p><p>→進入雜木林，漸漸開始陡上<br><img src="https://1013399.github.io/image-4/245/189468396_l.jpg" alt="東穗山" loading="lazy"></p><p>→路旁小瀑布<br><img src="https://1013399.github.io/image-4/245/189468404_l.jpg" alt="東穗山 小瀑布" loading="lazy"></p><p>→跨過小溪<br><img src="https://1013399.github.io/image-4/245/189468449_l.jpg" alt="東穗山 小溪" loading="lazy"></p><p>→路旁特殊的植物<br><img src="https://1013399.github.io/image-4/245/189468445_l.jpg" alt="東穗山" loading="lazy"></p><p>→第二次跨過小溪<br><img src="https://1013399.github.io/image-4/245/189468410_l.jpg" alt="東穗山 小溪" loading="lazy"></p><p>→陡上的濕滑山徑，需注意落腳點，沿途只有一處有拉繩<br><img src="https://1013399.github.io/image-4/245/189468415_l.jpg" alt="東穗山 濕滑山徑 拉繩" loading="lazy"></p><p>→路旁兩朵超大的蕈類<br><img src="https://1013399.github.io/image-4/245/189468438_l.jpg" alt="東穗山 蕈類" loading="lazy"></p><p>→上稜線叉路，右往宇老，取左上<br><img src="https://1013399.github.io/image-4/245/189468418_l.jpg" alt="東穗山" loading="lazy"></p><p>→出現第一片箭竹林<br><img src="https://1013399.github.io/image-4/245/189468433_l.jpg" alt="東穗山 箭竹林" loading="lazy"></p><p>→第二片箭竹林，需撥開箭竹，才看得到路徑<br><img src="https://1013399.github.io/image-4/245/189468420_l.jpg" alt="東穗山 箭竹林" loading="lazy"></p><p>→東穗山，三等三角點No.6250<br><img src="https://1013399.github.io/image-4/245/189468423_l.jpg" alt="東穗山 三等三角點No.6250" loading="lazy"></p><p>→續行可往島田山、高台山，或石麻達山，原路折返<br><img src="https://1013399.github.io/image-4/245/189468430_l.jpg" alt="東穗山" loading="lazy"></p><p>→回到宇老叉路，取右下山<br> →回到登山口<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約3小時(含休息)</p><p>高低落差：約420公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',26),d=t("br",null,null,-1),m={href:"http://ms1.ctjh.tpc.edu.tw/~uank3/090823chat.htm",target:"_blank",rel:"noopener noreferrer"},b=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-4/245/189468831_l.jpg",alt:"東穗山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const i=e("ExternalLinkIcon");return o(),p("div",null,[h,r(" more "),c,t("p",null,[a("(裁剪過原圖)"),d,a(" 原圖連結→"),t("a",m,[a("江翠登山社(圓開前輩個人登山)"),g(i)]),b,_])])}const x=l(n,[["render",u],["__file","post-245-2011-06-14.html.vue"]]);export{x as default};
