import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,e as t,d as s,f as e}from"./app-3fad0725.js";const h={},n=i("p",null,[i("strong",null,"翠湖"),t("，位於新北市汐止區湖光里，湖水呈土黃色，為昔日益興煤礦北港二坑開礦時，廢土阻塞河道而形成之偃塞湖，是每年4、5月賞油桐花、享受幽靜的好去處。")],-1),m=i("p",null,[i("strong",null,"內溝山"),t("，又名大尖坪山，海拔137公尺，位於汐止與內湖交界，山頂有土地調查局圖根點基石，屬於五指山系，山頂視野極佳，可俯瞰翠湖。")],-1),b=e('<p><strong>內溝山北峰</strong>，海拔130公尺，山頂有聯勤測量隊僑五基石，但無解說牌、也無標示牌。</p><p><strong>老鷲尖</strong>，又稱老鷹尖，海拔193公尺，位於汐止與內湖交界，山頂有北市三角點No.55基石。此山因上方常有大型鳥獸盤旋而得名，山頂腹地不大，可眺望五指山一帶的山脈。</p><p><strong>老鷲尖南峰</strong>，海拔155公尺，介於老鷲尖與學頭坡山之間，山頂有聯勤測量隊僑六基石及解說牌。</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>上午走過新山、夢湖後，開車由夢湖路接汐萬路往汐止方向，遇康寧街指標左轉再左轉往內湖方向，見明峰街右轉，不久再右轉湖東街，進入湖東街5巷，直達翠湖步道入口(附近叉路口皆有指標)，停車於路旁。</p><p>走走日期： 2011/05/01</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→湖東街5巷路底停車<br><img src="https://1013399.github.io/image-4/248/185620531_l.jpg" alt="湖東街5巷路底停車 翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→翠湖步道入口<br><img src="https://1013399.github.io/image-4/248/185620527_l.jpg" alt="翠湖步道入口" loading="lazy"></p><p>→林間空地旁叉路，右上山路往學頭坡山與老鷲尖之間的稜線步道，續直行<br><img src="https://1013399.github.io/image-4/248/185620522_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→福德宮<br><img src="https://1013399.github.io/image-4/248/185620533_l.jpg" alt="福德宮 翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→北港二坑礦場遺址，設有解說牌<br><img src="https://1013399.github.io/image-4/248/185620537_l.jpg" alt="北港二坑礦場遺址" loading="lazy"></p><p>→林間大空地，左右各有步道，殊途同歸，取右木棧道<br><img src="https://1013399.github.io/image-4/248/185620540_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→叉路，右山路為往八連路的越嶺古道，續直行木棧道<br><img src="https://1013399.github.io/image-4/248/185620544_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→左右兩步道會合<br><img src="https://1013399.github.io/image-4/248/185620549_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→翠湖，叉路，左往內溝山，右往學頭坡山與老鷲尖之間的稜線步道<br><img src="https://1013399.github.io/image-4/248/185620553_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→翠湖解說牌，休息後取左往內溝山<br><img src="https://1013399.github.io/image-4/248/185620683_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→內溝山叉路，直行應為環湖步道，取左上往內溝山<br><img src="https://1013399.github.io/image-4/248/185620555_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→內溝山石階步道<br><img src="https://1013399.github.io/image-4/248/185620556_l.jpg" alt="內溝山石階步道" loading="lazy"></p><p>→休息平台叉路，直下山路可往康樂街，右上往內溝山，先取左休息平台<br><img src="https://1013399.github.io/image-4/248/185620558_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→休息平台<br><img src="https://1013399.github.io/image-4/248/185620565_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→俯瞰翠湖，休息後退回往內溝山<br><img src="https://1013399.github.io/image-4/248/185620562_l.jpg" alt="翠湖" loading="lazy"></p><p>→內溝山，土地調查局圖根點<br><img src="https://1013399.github.io/image-4/248/185620568_l.jpg" alt="內溝山 土地調查局圖根點" loading="lazy"></p><p>→眺望老鷲尖<br><img src="https://1013399.github.io/image-4/248/185620571_l.jpg" alt="老鷲尖" loading="lazy"></p><p>→垂直陡下的鋁梯與簡易木橋<br><img src="https://1013399.github.io/image-4/248/185620572_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→往回拍攝木橋，右側也可拉繩陡下<br><img src="https://1013399.github.io/image-4/248/185620574_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→叉路，右下可往翠湖，續直行<br><img src="https://1013399.github.io/image-4/248/185620577_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→油桐嶺叉路，左為真武步道往康樂街，取右往老鷲尖<br><img src="https://1013399.github.io/image-4/248/185620584_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→回望內溝山<br><img src="https://1013399.github.io/image-4/248/185620590_l.jpg" alt="內溝山" loading="lazy"></p><p>→內溝山北峰，聯勤測量隊僑五水泥基石<br><img src="https://1013399.github.io/image-4/248/185620594_l.jpg" alt="內溝山北峰" loading="lazy"></p><p>→滿地油桐花<br><img src="https://1013399.github.io/image-4/248/185620595_l.jpg" alt="油桐花 翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→鞍部叉路，左下路徑雜草叢生，右下往翠湖，續直行往老鷲尖<br><img src="https://1013399.github.io/image-4/248/185620598_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→步道又見滿地油桐花<br><img src="https://1013399.github.io/image-4/248/185620603_l.jpg" alt="油桐花 翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→往老鷲尖為長枕木步道<br><img src="https://1013399.github.io/image-4/248/185620608_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→老鷲尖叉路，右往翠湖、學頭坡山，先取左上往老鷲尖<br><img src="https://1013399.github.io/image-4/248/185620611_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→老鷲尖，北市三角點第55號，續行山路往八連路，原路退回往翠湖<br><img src="https://1013399.github.io/image-4/248/185620615_l.jpg" alt="老鷲尖，北市三角點第55號" loading="lazy"></p><p>→蜿蜒緩下枕木步道<br><img src="https://1013399.github.io/image-4/248/185620619_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→枕木步道結束，叉路，右下可回翠湖，續直行<br><img src="https://1013399.github.io/image-4/248/185620622_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→拉繩陡上<br><img src="https://1013399.github.io/image-4/248/185620625_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→老鷲尖南峰解說牌，聯勤測量隊僑六水泥基石<br><img src="https://1013399.github.io/image-4/248/185620630_l.jpg" alt="老鷲尖南峰解說牌" loading="lazy"></p><p>→再拉繩陡上<br><img src="https://1013399.github.io/image-4/248/185620633_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→左側有展望點<br><img src="https://1013399.github.io/image-4/248/185620635_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰 展望" loading="lazy"></p><p>→遠眺新山一帶<br><img src="https://1013399.github.io/image-4/248/185620637_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→直行垂直鋁梯上岩峰，取右繞腰路<br><img src="https://1013399.github.io/image-4/248/185620641_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→不久也要拉繩陡上會合(早知道爬鋁梯較快)<br><img src="https://1013399.github.io/image-4/248/185620646_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→岩峰展望佳<br><img src="https://1013399.github.io/image-4/248/185620648_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→步道再轉為平緩好走的枕木階梯<br><img src="https://1013399.github.io/image-4/248/185620653_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→叉路，直行往學頭坡山，取右下回翠湖<br><img src="https://1013399.github.io/image-4/248/185620658_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→叉路，左上稜線可往學頭坡山，取右回翠湖<br><img src="https://1013399.github.io/image-4/248/185620666_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→回到翠湖環湖步道<br><img src="https://1013399.github.io/image-4/248/185620672_l.jpg" alt="翠湖環湖步道" loading="lazy"></p><p>→回到翠湖<br><img src="https://1013399.github.io/image-4/248/185620678_l.jpg" alt="翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰" loading="lazy"></p><p>→回到翠湖步道口</p><p>→沿途看到的北市地測基石<br><img src="https://1013399.github.io/image-4/248/185620689_l.jpg" alt="北市地測基石" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約150公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',56),c={href:"http://www.yougoipay.com/kenny/w974/index.htm",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-4/248/185621316_l.jpg",alt:"翠湖、內溝山、內溝山北峰、老鷲尖、老鷲尖南峰 參考地圖",loading:"lazy"},null,-1);function u(y,j){const a=l("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),b,i("p",null,[t("原圖連結→"),i("a",c,[t("偶而獨步山林間(蕭郎)"),s(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-248-2011-05-05.html.vue"]]);export{x as default};
