import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as e,a as o,b as i,f as a,d as r,e as s}from"./app-a39bec1a.js";const n={},h=i("p",null,[i("strong",null,"夢湖"),a("，海拔約325公尺，位於新北市汐止區的山中美麗小湖，為一人工湖，在冬季的午後四周常會起霧，使得湖的周圍有如夢境一般因此以夢湖為名。夢湖的美景除了吸引人們前往，更是近年來熱門的婚紗拍攝地點，也是台灣蓋斑鬥魚的覆育地。")],-1),c=i("p",null,[i("strong",null,"新山"),a("，海拔499公尺，位於新北市汐止區的夢湖後方，山頂設有解說牌及中華山岳野牛隊73年9月所立野牛2號基石，基石埋設於巨岩之上。山頂為大石區，展望極佳，可俯瞰汐止、友蚋山區，眺望五指山。")],-1),m=s('<h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下汐止交流道，經新台五路遇南興路左轉，穿過大同路直行過江北二橋，接汐萬路(北29)左轉，至汐萬路三段遇夢湖路右轉(有新山夢湖指標)，直達路底新山夢湖入口(部分路段較窄難會車)，於入口附近停車。新山夢湖入口迴車不易，宜先將車調頭再停車。</p><p>走走日期： 2011/05/01</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→新山夢湖入口前已停滿許多車輛<br><img src="https://1013399.github.io/image-4/249/185619315_l.jpg" alt="新山夢湖入口前已停滿許多車輛" loading="lazy"></p><p>→新山夢湖入口，旁有涼亭<br><img src="https://1013399.github.io/image-4/249/185619330_l.jpg" alt="新山夢湖入口" loading="lazy"></p><p>→入口旁導覽圖<br><img src="https://1013399.github.io/image-4/249/185619326_l.jpg" alt="夢湖、新山導覽圖" loading="lazy"></p><p>→過涼亭後循石階步道往夢湖<br><img src="https://1013399.github.io/image-4/249/185619320_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→新山叉路，直行往新山，取左往夢湖<br><img src="https://1013399.github.io/image-4/249/185619336_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→夢湖美景，取湖右邊土徑至湖對面<br><img src="https://1013399.github.io/image-4/249/185619342_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→湖邊的新山登山口，取右上往新山<br><img src="https://1013399.github.io/image-4/249/185619348_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→陡上<br><img src="https://1013399.github.io/image-4/249/185619354_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→上至支稜，左可回夢湖，取右上往新山<br><img src="https://1013399.github.io/image-4/249/185619359_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-4/249/185619365_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→叉路，左上山路可往柯子林，取右往新山<br><img src="https://1013399.github.io/image-4/249/185619374_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→上至主稜，右往新山，先取左上大斜壁<br><img src="https://1013399.github.io/image-4/249/185619380_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→大斜壁<br><img src="https://1013399.github.io/image-4/249/185619389_l.jpg" alt="夢湖、新山 大斜壁" loading="lazy"></p><p>→大斜壁上展望佳，眺望新山<br><img src="https://1013399.github.io/image-4/249/185619394_l.jpg" alt="夢湖、新山 大斜壁 展望" loading="lazy"></p><p>→續循稜可下往柯子林，退回往新山<br><img src="https://1013399.github.io/image-4/249/185619401_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→新山，山頂解說牌及野牛2號基石，休息後續往前行<br><img src="https://1013399.github.io/image-4/249/185619409_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→叉路，右不知去向，依路條取左續行<br><img src="https://1013399.github.io/image-4/249/185619416_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→叉路，左往五指山、友蚋山，取右往上<br><img src="https://1013399.github.io/image-4/249/185619422_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→過木椅後有叉路，左上往新山東稜，取右下往夢湖<br><img src="https://1013399.github.io/image-4/249/185619427_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→攀繩岩壁(往回拍攝)<br><img src="https://1013399.github.io/image-4/249/185619430_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→叉路，左上往新山東稜，取右下往夢湖<br><img src="https://1013399.github.io/image-4/249/185619439_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→接長枕木階梯<br><img src="https://1013399.github.io/image-4/249/185619447_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→回到新山叉路(右邊即為夢湖)<br><img src="https://1013399.github.io/image-4/249/185619453_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→有數對新人正在夢湖取景拍照<br><img src="https://1013399.github.io/image-4/249/185619295_l.jpg" alt="夢湖、新山" loading="lazy"></p><p>→回到夢湖路底涼亭<br> →回到停車處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時(含休息)</p><p>高低落差：約200公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',32),d={href:"http://www.yougoipay.com/kenny/index836.html",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-4/249/185619589_l.jpg",alt:"夢湖 新山 參考地圖",loading:"lazy"},null,-1);function u(y,j){const t=g("ExternalLinkIcon");return p(),e("div",null,[h,c,o(" more "),m,i("p",null,[a("原圖連結→"),i("a",d,[a("偶而獨步山林間(蕭郎)"),r(t)]),b,_])])}const x=l(n,[["render",u],["__file","post-249-2011-05-03.html.vue"]]);export{x as default};
