import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as e,c as o,a as r,b as i,e as t,d as l,f as s}from"./app-157980c9.js";const n={},h=i("p",null,[i("strong",null,"惠蓀林場"),t("原名「能高林場」，是中興大學農學院所屬四大林場，也是全台最大規模的原始森林浴步道林，景觀與原生植物種類兼具教育與賞景功能。民國56年為紀念當時校長湯惠蓀在視察林場業務時不幸殉職，因而更名為「惠蓀林場」並於林場內豎立湯公碑、湯公亭作為紀念，林場幅地廣闊，步道規劃完善，環境清幽，值得一遊。")],-1),c=i("p",null,[i("strong",null,"小出山"),t("，位於南投縣仁愛鄉之惠蓀林場南方，海拔1709公尺，山頂有三等三角點No.6404、森林三角點、山字水泥柱及十字磐石。")],-1),d=i("p",null,"一般登臨小出山，最單純的路線是由惠蓀林場遊客中心販賣部起登，總上升高度落差約900公尺，有些山友從惠蓀林場收費站外的迎翠橋起登，可省去門票費用，但落差再加350公尺，另外也可從埔里關刀山縱走過來。",-1),m=i("p",null,"今日參加公司登山社活動，二部遊覽車一行八十餘人，從新竹出發，行約2個多小時，於惠蓀林場遊客中心下車，整裝及拍照後，開始今日行程。約1.5小時抵達湯公碑，休息、午餐後，約剩40人繼續攻頂小出山，也花了1.5小時，原路折返下山回到遊客中心則花了2小時。",-1),b=i("h2",{id:"行車路線",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#行車路線","aria-hidden":"true"},"#"),t(" 行車路線")],-1),_=i("p",null,"國道3號南下接國道6號，下國姓交流道接台14線省道(中正路)往國姓方向，到國姓老街(柑仔林)左轉133縣道(國姓路)再接台21線右轉，依指標接投80鄉道便可到達惠蓀林場收費站，過收費站後續行至遊客中心販賣部下車。",-1),u={href:"http://huisun.nchu.edu.tw/service/?parent_id=20",target:"_blank",rel:"noopener noreferrer"},z=s('<figure><img src="https://1013399.github.io/image-4/250/185013953_l.jpg" alt="惠蓀林場、小出山 交通指南地圖" tabindex="0" loading="lazy"><figcaption>惠蓀林場、小出山 交通指南地圖</figcaption></figure><p>走走日期： 2011/04/23</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→遊客中心<br><img src="https://1013399.github.io/image-4/250/184983701_l.jpg" alt="惠蓀林場遊客中心" loading="lazy"></p><p>→遊客中心對面即為入口<br><img src="https://1013399.github.io/image-4/250/184983693_l.jpg" alt="惠蓀林場遊客中心 小出山入口" loading="lazy"></p><p>→浩浩蕩蕩循枕木階梯往上行<br><img src="https://1013399.github.io/image-4/250/184983708_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→接上林道續往上行<br><img src="https://1013399.github.io/image-4/250/184983714_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→森林浴步道入口，森林浴步道長1.3K<br><img src="https://1013399.github.io/image-4/250/184983719_l.jpg" alt="森林浴步道入口 惠蓀林場、小出山" loading="lazy"></p><p>→森林浴步道沿途皆為筆直的杉木林<br><img src="https://1013399.github.io/image-4/250/184983726_l.jpg" alt="森林浴步道 惠蓀林場、小出山" loading="lazy"></p><p>→森林浴步道每200公尺設有里程木樁，0.2K至0.8K平緩好走<br><img src="https://1013399.github.io/image-4/250/184983733_l.jpg" alt="森林浴步道 惠蓀林場、小出山" loading="lazy"></p><p>→再接上林道，取右15公尺接湯公碑步道<br><img src="https://1013399.github.io/image-4/250/184983744_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→湯公碑步道入口，湯公碑步道長1.2K<br><img src="https://1013399.github.io/image-4/250/184983751_l.jpg" alt="湯公碑步道" loading="lazy"></p><p>→湯公碑步道採Z字型緩上<br><img src="https://1013399.github.io/image-4/250/184983756_l.jpg" alt="湯公碑步道" loading="lazy"></p><p>→湯公碑，合照紀念，在此休息、午餐約半小時<br><img src="https://1013399.github.io/image-4/250/184983764_l.jpg" alt="湯公碑" loading="lazy"></p><p>→休息後續行，跨越倒木<br><img src="https://1013399.github.io/image-4/250/184983897_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→倒木下發現水晶蘭<br><img src="https://1013399.github.io/image-4/250/184983902_l.jpg" alt="水晶蘭 惠蓀林場、小出山" loading="lazy"></p><p>→跨越駁坎續往上<br><img src="https://1013399.github.io/image-4/250/184983773_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→平緩好走的山徑<br><img src="https://1013399.github.io/image-4/250/184983778_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→往右上切，稍陡<br><img src="https://1013399.github.io/image-4/250/184983787_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→叉路，左下有簡易路障，可能是往林道，取右上<br><img src="https://1013399.github.io/image-4/250/184983798_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→路徑開始趨陡<br><img src="https://1013399.github.io/image-4/250/184983804_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→開始陡上<br><img src="https://1013399.github.io/image-4/250/184983816_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→繼續陡上，此段陡坡約走15分鐘<br><img src="https://1013399.github.io/image-4/250/184983823_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→又轉為平緩山徑<br><img src="https://1013399.github.io/image-4/250/184983829_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→沿途頗多巨木<br><img src="https://1013399.github.io/image-4/250/184983833_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→路徑有多處小陡下再小陡上，但都不長<br><img src="https://1013399.github.io/image-4/250/184983839_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→看到此巨木便已到達小出山了，三角點就在樹的後方<br><img src="https://1013399.github.io/image-4/250/184983844_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→巨木旁也有一些剛冒出來的水晶蘭<br><img src="https://1013399.github.io/image-4/250/184983852_l.jpg" alt="水晶蘭 惠蓀林場、小出山" loading="lazy"></p><p>→小出山，三等三角點No.6404、森林三角點、山字水泥柱及十字磐石<br><img src="https://1013399.github.io/image-4/250/184983873_l.jpg" alt="小出山 三等三角點No.6404 森林三角點 山字水泥柱 十字磐石" loading="lazy"></p><p>→再來張攻頂紀念合照<br><img src="https://1013399.github.io/image-4/250/184983863_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→山頂為叉路，右下往迎翠橋登山口，直行可往埔里關刀山，休息後原路折返<br><img src="https://1013399.github.io/image-4/250/184983884_l.jpg" alt="惠蓀林場、小出山" loading="lazy"></p><p>→回到湯公碑，續行湯公碑步道往下<br> →回到森林浴步道口，續行森林浴步道往下<br> →回到遊客中心</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約5.5小時(含休息、午餐)</p><p>高低落差：約920公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',35),j={href:"http://blog.xuite.net/laoweiz/blog/25740844",target:"_blank",rel:"noopener noreferrer"},y=i("br",null,null,-1),f=i("img",{src:"https://1013399.github.io/image-4/250/184984638_l.jpg",alt:"惠蓀林場、小出山 參考地圖",loading:"lazy"},null,-1);function x(N,k){const a=p("ExternalLinkIcon");return e(),o("div",null,[h,c,r(" more "),d,m,b,_,i("p",null,[t("(交通指南地圖連結："),i("a",u,[t("惠蓀林場網站"),l(a)]),t(")")]),z,i("p",null,[t("原圖連結→"),i("a",j,[t("老尉子的部落格"),l(a)]),y,f])])}const K=g(n,[["render",x],["__file","post-250-2011-04-26.html.vue"]]);export{K as default};
