import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as e,a as o,b as i,e as t,d as r,f as s}from"./app-272e1b70.js";const h={},n=i("p",null,[i("strong",null,"五分山"),t("，又名滴水大尖或粗坑口山，海拔757公尺，位於新北市平溪、瑞芳與基隆暖暖的界陵上，山頂有二等三角點No.1065及三等三角點No.1038基石各一顆，視野極佳，旁有中央氣象局五分山氣象雷達站，白色圓球為五分山的明顯地標，該山名列台灣小百岳No.010。")],-1),c=i("p",null,[i("strong",null,"頂子寮山"),t("，海拔550公尺，山頂有顆無名基石，位於十分古道接上五分山步道，越嶺鞍部的嶺頭福德宮後方，視野不佳。")],-1),m=s('<h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號北上接國道5號，下石碇交流道左轉接106縣道往平溪方向，過平溪十分車站後不久，於106縣道68.6k處左轉往台灣煤礦博物館，至博物館售票處停車場停車。</p><p>走走日期： 2011/04/03</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→台灣煤礦博物館售票處(無人售票)<br><img src="https://1013399.github.io/image-4/253/183062249_l.jpg" alt="台灣煤礦博物館售票處" loading="lazy"></p><p>→由博物館左後方起登<br><img src="https://1013399.github.io/image-4/253/183062254_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→步道口設有登山步道導覽圖<br><img src="https://1013399.github.io/image-4/253/183062260_l.jpg" alt="五分山步道步道 五分山步道導覽圖" loading="lazy"></p><p>→五分山步道<br><img src="https://1013399.github.io/image-4/253/183062265_l.jpg" alt="五分山步道" loading="lazy"></p><p>→小溪沖刷形成許多壺穴<br><img src="https://1013399.github.io/image-4/253/183062245_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→小拱橋<br><img src="https://1013399.github.io/image-4/253/183062270_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→小橋毀損，下切再上切<br><img src="https://1013399.github.io/image-4/253/183062274_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→步道被沖毀成溪谷<br><img src="https://1013399.github.io/image-4/253/183062284_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→過一小橋後開始陡上<br><img src="https://1013399.github.io/image-4/253/183062290_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→又一崩塌處<br><img src="https://1013399.github.io/image-4/253/183062296_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→步道歪七扭八，但還不難走<br><img src="https://1013399.github.io/image-4/253/183062393_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→嶺頭福德宮，由廟左方上切往頂子寮山<br><img src="https://1013399.github.io/image-4/253/183062318_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→叉路，左往望古山，取右行<br><img src="https://1013399.github.io/image-4/253/183062311_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→頂子寮山，無名基石，續行可接回步道，原路折返福德宮休息、午餐<br><img src="https://1013399.github.io/image-4/253/183062302_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→續行，不久遇叉路，左拉繩陡下往暖暖，取直步道往五分山<br><img src="https://1013399.github.io/image-4/253/183062322_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→叉路口設有淡蘭古道解說牌<br><img src="https://1013399.github.io/image-4/253/183062328_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→步道平緩，但不久後又再陡上<br><img src="https://1013399.github.io/image-4/253/183062334_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→叉路，左下可往水源處，續直行<br><img src="https://1013399.github.io/image-4/253/183062340_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→涼亭，休息<br><img src="https://1013399.github.io/image-4/253/183062345_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→大石如桌<br><img src="https://1013399.github.io/image-4/253/183062350_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→步道上眺望五分山瞭望亭<br><img src="https://1013399.github.io/image-4/253/183062357_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→叉路，左往龍門山，續直行<br><img src="https://1013399.github.io/image-4/253/183062359_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→五分山瞭望亭，已起濃霧，什麼都看不到<br><img src="https://1013399.github.io/image-4/253/183062366_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→五分山登山口，直走步道往五分山產業道路，取右山徑往三角點<br><img src="https://1013399.github.io/image-4/253/183062368_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→五分山，二等三角點#1065與三等三角點#1038基石<br><img src="https://1013399.github.io/image-4/253/183062373_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→穿越芒草小徑遇叉路，取左行<br><img src="https://1013399.github.io/image-4/253/183062376_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→氣象局雨量計，五分山中繼站，濃霧中連白色大圓球都看不到<br><img src="https://1013399.github.io/image-4/253/183062381_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→氣象局雷達站後門，取左山徑<br><img src="https://1013399.github.io/image-4/253/183062384_l.jpg" alt="頂子寮山、五分山" loading="lazy"></p><p>→接五分山產業道路，取左行<br><img src="https://1013399.github.io/image-4/253/183062389_l.jpg" alt="五分山產業道路" loading="lazy"></p><p>→五分山步道口，取左進入<br><img src="https://1013399.github.io/image-4/253/183062391_l.jpg" alt="五分山步道口" loading="lazy"></p><p>→回到五分山瞭望亭，休息後原路回<br> →回到嶺頭福德宮<br> →回到台灣煤礦博物館售票處</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：博物館售票亭起登約4.5小時(含休息、午餐)</p><p>高低落差：約520公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',38),b={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=23181",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-4/253/183062406_l.jpg",alt:"頂子寮山、五分山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=g("ExternalLinkIcon");return p(),e("div",null,[n,c,o(" more "),m,i("p",null,[t("原圖連結→"),i("a",b,[t("登山補給站-brownlee"),r(a)]),d,_])])}const x=l(h,[["render",u],["__file","post-253-2011-04-08.html.vue"]]);export{x as default};
