import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,f as t,d as e,e as s}from"./app-63889d57.js";const h={},n=i("p",null,[i("strong",null,"泰安瀑布"),t("，位於基隆市七堵南方的草濫山區，又稱草濫山瀑布，泰安瀑布高約20公尺，景致優美清麗，規畫有烤肉區、戲水區及環山步道，為一大眾化的踏青去處。")],-1),m=i("p",null,[i("strong",null,"姜子寮山"),t("，又名三腳架山，海拔729公尺，為姜子寮山列的主峰，山頂有三等三角點#1047基石及四座觀景台，視野極佳，陽明山系、四分尾山、姜南山、五分山、基隆山、基隆嶼等盡在眼底，但山頂無樹木遮蔭，非常炎熱，該山名列台灣小百岳No.011。")],-1),b=s('<p><strong>旗尾崙</strong>，又稱姜子山或大旗尾山，海拔389公尺，山頂有土地調查局圖根點，為泰安瀑布往姜子寮山步道途中的稜肩小山頭，樹林間展望不佳。</p><p>走走日期：2011/03/13</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道三號北上，下汐止交流道接新台五路(台5線)往汐止、基隆方向，至七堵後轉為明德二路，過六堵工業區後，於明德二路與泰安路叉路(台5線21.7K處)，右轉穿越鐵路涵洞進入泰安路，行駛約3km產業道路至泰安瀑布入口處停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→泰安瀑布入口，右側有二條石階步道，取右靠山邊步道<br><img src="https://1013399.github.io/image-4/255/180842473_l.jpg" alt="泰安瀑布入口" loading="lazy"></p><p>→陡上石階步道<br><img src="https://1013399.github.io/image-4/255/180842477_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→叉路，左有涼亭，右後有公廁，右前往旗尾崙、姜子寮山，先直行往泰安瀑布<br><img src="https://1013399.github.io/image-4/255/180842483_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→烤肉區<br><img src="https://1013399.github.io/image-4/255/180842490_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→環山步道叉路，取左過橋往泰安瀑布，左去右回<br><img src="https://1013399.github.io/image-4/255/180842500_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→泰安瀑布，賞瀑後續直走環山步道<br><img src="https://1013399.github.io/image-4/255/180842508_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→環山步道<br><img src="https://1013399.github.io/image-4/255/180842513_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→泰安瀑布頂端，續直走過橋後往下行<br><img src="https://1013399.github.io/image-4/255/180842523_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→泰安瀑布後方還有兩處小瀑布<br><img src="https://1013399.github.io/image-4/255/180842528_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→回到烤肉區，穿過烤肉區後取左階梯上行<br><img src="https://1013399.github.io/image-4/255/180842535_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→叉路，右回公廁、瀑布入口，續直行<br><img src="https://1013399.github.io/image-4/255/180842544_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→旗尾崙登山口，右也可回瀑布入口，取左泥土山徑<br><img src="https://1013399.github.io/image-4/255/180842549_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→叉路，左應該也可往旗尾崙，續直行<br><img src="https://1013399.github.io/image-4/255/180842557_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→石門山叉路，右往石門山，取左往旗尾崙<br><img src="https://1013399.github.io/image-4/255/180842563_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→叉路，右往姜子寮產業道路之福興宮，續直行<br><img src="https://1013399.github.io/image-4/255/180842572_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→旗尾崙，土地調查局圖根點<br><img src="https://1013399.github.io/image-4/255/180842579_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→十字叉路，左可往泰安產道，右不知去向，續直行往上<br><img src="https://1013399.github.io/image-4/255/180842584_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→小山頭，路旁石頭上寫著1600公尺，不知何意<br><img src="https://1013399.github.io/image-4/255/180842590_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-4/255/180842597_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→小山頭，途中經過數個小山頭，但起伏不大<br><img src="https://1013399.github.io/image-4/255/180842604_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→又一小山頭<br><img src="https://1013399.github.io/image-4/255/180842612_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→接上姜子寮山步道，左往姜子寮步道入口，於此休息、午餐<br><img src="https://1013399.github.io/image-4/255/180842615_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→休息後取右枕木步道往姜子寮山<br><img src="https://1013399.github.io/image-4/255/180842621_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→拔西猴山叉路，左往拔西猴山，續前行<br><img src="https://1013399.github.io/image-4/255/180842626_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→姜子寮山山頂<br><img src="https://1013399.github.io/image-4/255/180842628_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→姜子寮山三等三角點#1047<br><img src="https://1013399.github.io/image-4/255/180842632_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→遠眺基隆嶼、四分尾山、平溪地區、五分山<br><img src="https://1013399.github.io/image-4/255/180842635_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→右側芒草路徑往磐石嶺，休息賞景後原路下山<br><img src="https://1013399.github.io/image-4/255/180842636_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→回到旗尾崙叉路，續直行步道下山</p><p>→枕木步道結束，左有山路應可往旗尾崙，取右續行<br><img src="https://1013399.github.io/image-4/255/180842638_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→櫻花林步道(八重櫻)，櫻花大多已凋謝，冒出新葉<br><img src="https://1013399.github.io/image-4/255/180842644_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→姜子寮山步道入口，左不知去向，右產道可回泰安瀑布入口<br><img src="https://1013399.github.io/image-4/255/180842647_l.jpg" alt="姜子寮山步道入口" loading="lazy"></p><p>→姜子寮山步道入口意象<br><img src="https://1013399.github.io/image-4/255/180842653_l.jpg" alt="姜子寮山步道入口" loading="lazy"></p><p>→穿越產道至觀景涼亭休息，涼亭視野佳<br><img src="https://1013399.github.io/image-4/255/180842657_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→休息後回產道下山(回到泰安瀑布入口約4.5Km，走70分鐘)<br><img src="https://1013399.github.io/image-4/255/180842660_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→產道沿途頗多巴西野牡丹<br><img src="https://1013399.github.io/image-4/255/180842667_l.jpg" alt="巴西野牡丹 泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→產道沿途尚有盛開的櫻花<br><img src="https://1013399.github.io/image-4/255/180842669_l.jpg" alt="盛開的櫻花 泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→產道沿途多處有民眾在取水、洗車<br><img src="https://1013399.github.io/image-4/255/180842673_l.jpg" alt="泰安瀑布、旗尾崙、姜子寮山" loading="lazy"></p><p>→回到泰安瀑布入口<br><img src="https://1013399.github.io/image-4/255/180842454_l.jpg" alt="泰安瀑布入口" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約5小時(含休息、午餐)</p><p>高低落差：約590公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',47),c={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=24563",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-4/255/180842697_l.jpg",alt:"泰安瀑布、旗尾崙、姜子寮山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=l("ExternalLinkIcon");return p(),o("div",null,[n,m,r(" more "),b,i("p",null,[t("原圖連結→"),i("a",c,[t("登山補給站-brownlee"),e(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-255-2011-03-15.html.vue"]]);export{x as default};
