import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as e,a as h,b as a,f as i,d as o,e as t}from"./app-a39bec1a.js";const s={},n=a("p",null,[a("strong",null,"七星山健康步道"),i("，位於宜蘭縣蘇澳鎮，步道全長約4.7公里，入口一端在蘇澳運動公園附近，另一端則在蘇澳冷泉旁，步道兩處高點「七星嶺」及「星嶺公園」視野絕佳，可俯瞰蘇澳港、北方澳中正軍港、豆腐岬、南方澳漁港、蘇花公路、砲台山等景點，只是沿途較無遮蔭，炎炎夏日走來較為辛苦。")],-1),d=t('<p><strong>七星嶺</strong>，海拔229公尺，位於宜蘭蘇澳冷泉後方，原有二、三等三角點都遺失，現有內補二等三角點#023基石及海軍#1109基石，分別設於軍事設施、華視與台視轉播台內，探訪不易，一般人只抵達七星嶺觀景平台。</p><p>走走日期：2011/02/08</p><h2 id="七星山健康步道、七星嶺" tabindex="-1"><a class="header-anchor" href="#七星山健康步道、七星嶺" aria-hidden="true">#</a> 七星山健康步道、七星嶺</h2><h3 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h3><p>國道三號北上接國道五號，於蘇澳下交流道往蘇澳方向，接中山路一段(台九線)往蘇澳，過中原路(中原路為市場)後，下一路口左轉(路口有蘇澳冷泉指標)，進入便可見蘇澳冷泉入口，續行巷道於未劃紅線處停車。</p><h3 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h3><p>→天下第一奇泉-蘇澳冷泉<br><img src="https://1013399.github.io/image-5/260/177648637_l.jpg" alt="天下第一奇泉 蘇澳冷泉" loading="lazy"></p><p>→七星山健康步道入口<br><img src="https://1013399.github.io/image-5/260/177648641_l.jpg" alt="七星山健康步道入口" loading="lazy"></p><p>→沿冷泉右側圍牆進入<br><img src="https://1013399.github.io/image-5/260/177648716_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→蘇澳冷泉大眾池<br><img src="https://1013399.github.io/image-5/260/177648645_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→步道石階一路往上<br><img src="https://1013399.github.io/image-5/260/177648648_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→休憩平台<br><img src="https://1013399.github.io/image-5/260/177648651_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→步道轉陡<br><img src="https://1013399.github.io/image-5/260/177648655_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→第一座涼亭<br><img src="https://1013399.github.io/image-5/260/177648662_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→第二座涼亭，展望極佳，左叉路往白雲寺，右往七星嶺<br><img src="https://1013399.github.io/image-5/260/177648666_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→俯瞰蘇澳港，休息後取右上往七星嶺<br><img src="https://1013399.github.io/image-5/260/177648668_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→叉路，左水泥步道為環山步道，較平緩但濕滑，取右續走階梯<br><img src="https://1013399.github.io/image-5/260/177648674_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→叉路，環山步道在此接回，取右續行<br><img src="https://1013399.github.io/image-5/260/177648681_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→步道崩塌掏空，沿山側小心通過<br><img src="https://1013399.github.io/image-5/260/177648686_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→於步道崩塌處俯瞰蘇澳港<br><img src="https://1013399.github.io/image-5/260/177648688_l.jpg" alt="蘇澳港" loading="lazy"></p><p>→七星嶺觀景平台<br><img src="https://1013399.github.io/image-5/260/177648692_l.jpg" alt="七星嶺觀景平台" loading="lazy"></p><p>→俯瞰蘇澳港<br><img src="https://1013399.github.io/image-5/260/177648695_l.jpg" alt="蘇澳港" loading="lazy"></p><p>→俯瞰蘇花公路、砲台山<br><img src="https://1013399.github.io/image-5/260/177648697_l.jpg" alt="蘇花公路、砲台山" loading="lazy"></p><p>→七星嶺，山頂轉播站<br><img src="https://1013399.github.io/image-5/260/177648702_l.jpg" alt="七星嶺，山頂轉播站" loading="lazy"></p><p>→旁邊續行往星嶺公園<br><img src="https://1013399.github.io/image-5/260/177648706_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→步道為寬大土路，一路下坡，走了一段後原路折返<br><img src="https://1013399.github.io/image-5/260/177648709_l.jpg" alt="七星山健康步道、七星嶺" loading="lazy"></p><p>→眺望對面的星嶺公園<br><img src="https://1013399.github.io/image-5/260/177648711_l.jpg" alt="星嶺公園" loading="lazy"></p><p>→回到七星嶺觀景平台，休息、賞景、吃水果<br> →回到第二座涼亭<br> →回到第一座涼亭<br> →回到蘇澳冷泉入口，取車往白米木屐村參觀</p><h3 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息、賞景)</p><p>高低落差：約220公尺</p></div><h3 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h3>',31),c={href:"http://www.wayfarer.idv.tw/Taiwan/2010/0424/0424.html",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-5/260/177648948_l.jpg",alt:"七星山健康步道、七星嶺 參考地圖",loading:"lazy"},null,-1),_=t('<h2 id="白米木屐村" tabindex="-1"><a class="header-anchor" href="#白米木屐村" aria-hidden="true">#</a> 白米木屐村</h2><h3 id="行車路線-1" tabindex="-1"><a class="header-anchor" href="#行車路線-1" aria-hidden="true">#</a> 行車路線</h3><p>於蘇澳冷泉回至中山路往蘇澳港方向，遇中正路(白米橋前)右轉過平交道，於多叉路口取右前方成功路，不久轉為永春路，直行便可抵達白米木屐博物館，停車於博物館對面停車場。</p><h3 id="參觀資訊" tabindex="-1"><a class="header-anchor" href="#參觀資訊" aria-hidden="true">#</a> 參觀資訊</h3><p>參觀木屐博物館，須先登記安排場次，由於參觀時間尚早，先往豆腐岬17餐廳午餐再回。<br> 於參觀時間前15分鐘，工作人員會安排先參觀工藝教室，可DIY親手製作木屐裝飾品。</p><p>→工藝教室<br><img src="https://1013399.github.io/image-5/260/177648729_l.jpg" alt="白米木屐村 工藝教室" loading="lazy"></p><p>參觀完工藝教室，接著參觀木屐博物館。<br> →先到3樓欣賞介紹影片<br> →再到2樓，有專人解說及示範木屐製作過程</p><p>→電燒示範<br><img src="https://1013399.github.io/image-5/260/177648744_l.jpg" alt="白米木屐村 電燒示範" loading="lazy"></p><p>→裝飾品鞋面製作<br><img src="https://1013399.github.io/image-5/260/177648756_l.jpg" alt="白米木屐村 裝飾品鞋面製作" loading="lazy"></p><p>→裝飾品鞋面成品<br><img src="https://1013399.github.io/image-5/260/177648771_l.jpg" alt="白米木屐村 裝飾品鞋面成品" loading="lazy"></p><p>→鞋身半成品(尚未彩繪)<br><img src="https://1013399.github.io/image-5/260/177648783_l.jpg" alt="白米木屐村 鞋身半成品(尚未彩繪)" loading="lazy"></p><p>→木屐健康操<br><img src="https://1013399.github.io/image-5/260/177648815_l.jpg" alt="白米木屐村 木屐健康操" loading="lazy"></p><p>→電燒練習<br><img src="https://1013399.github.io/image-5/260/177648825_l.jpg" alt="白米木屐村 電燒練習" loading="lazy"></p><p>→協力木屐<br><img src="https://1013399.github.io/image-5/260/177648835_l.jpg" alt="白米木屐村 協力木屐" loading="lazy"></p><p>→健康木屐(包括按摩木屐、罰站木屐及瘦身木屐)<br><img src="https://1013399.github.io/image-5/260/177648844_l.jpg" alt="白米木屐村 健康木屐" loading="lazy"></p><p>→超大號木屐<br><img src="https://1013399.github.io/image-5/260/177648853_l.jpg" alt="白米木屐村 超大號木屐" loading="lazy"></p>',16);function u(y,j){const l=p("ExternalLinkIcon");return r(),e("div",null,[n,h(" more "),d,a("p",null,[i("原圖連結→ "),a("a",c,[i("Marktwain網站"),o(l)]),b,m]),_])}const x=g(s,[["render",u],["__file","post-260-2011-02-15.html.vue"]]);export{x as default};
