import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as g,c as r,a as o,b as i,f as a,d as e,e as s}from"./app-19acc492.js";const h={},n=i("p",null,[i("strong",null,"朝陽國家步道"),a("，位於宜蘭蘇澳朝陽里，全長2.3公里，沿途設有四座觀景台，可遠眺烏石鼻海岸、南澳漁港、神祕海灘、鴛鴦山、定情湖與田園景觀。步道沿途林相生態豐富，各種植物分別以十二生肖諺語命名，頗為有趣，並立有解說牌介紹。")],-1),m=s('<p><strong>南澳龜山</strong>，海拔181公尺，為宜蘭蘇澳朝陽社區南側的獨立山頭，東臨花東海岸，西接丘陵起伏之農漁村落，遠遠望去，好似一隻海龜停泊山海之間，遂得其名，山頂有三等三角點#4269基石，基石位於山頂平台，樹林間無視野。</p><p>走走日期：2011/02/07</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號北上接國道5號，蘇澳下交流道接台9線往花蓮，於新澳橋前(台9線128.5K)左轉娜娘路(有座牌樓)，遇朝陽路左轉往南澳朝陽漁港，停車於南澳漁港安檢所前方空地，步道入口就在安檢所旁邊。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→南澳朝陽漁港<br><img src="https://1013399.github.io/image-5/261/177646809_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→朝陽國家步道入口<br><img src="https://1013399.github.io/image-5/261/177646815_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→步道前段為枕木步道<br><img src="https://1013399.github.io/image-5/261/177646820_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→以十二生肖諺語命名植物解說牌<br><img src="https://1013399.github.io/image-5/261/177646823_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→步道每100公尺設有里程木樁<br><img src="https://1013399.github.io/image-5/261/177646829_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→第一座觀景台<br><img src="https://1013399.github.io/image-5/261/177646834_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→眺望烏石鼻岬角海岸<br><img src="https://1013399.github.io/image-5/261/177646837_l.jpg" alt="烏石鼻岬角海岸" loading="lazy"></p><p>→第二座觀景台<br><img src="https://1013399.github.io/image-5/261/177646846_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→眺望烏石鼻海岸及海面上的定置漁網<br><img src="https://1013399.github.io/image-5/261/177646853_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→定置漁網解說牌<br><img src="https://1013399.github.io/image-5/261/177646859_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→叉路，先取左往觀景台<br><img src="https://1013399.github.io/image-5/261/177646865_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→第三座觀景台<br><img src="https://1013399.github.io/image-5/261/177646871_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→往左眺望烏石鼻海岸<br><img src="https://1013399.github.io/image-5/261/177646874_l.jpg" alt="烏石鼻海岸" loading="lazy"></p><p>→往右眺望神祕海灘、定情湖<br><img src="https://1013399.github.io/image-5/261/177646880_l.jpg" alt="神祕海灘 定情湖" loading="lazy"></p><p>→原路退回叉路，取左續行</p><p>→第一座茅草涼亭，右有支線叉路可往朝陽社區(第二登山口)<br><img src="https://1013399.github.io/image-5/261/177646884_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→龜山山頂平台<br><img src="https://1013399.github.io/image-5/261/177646897_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→龜山頂三角點解說牌<br><img src="https://1013399.github.io/image-5/261/177646890_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→龜山三等三角點No.4269<br><img src="https://1013399.github.io/image-5/261/177646894_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→步道轉為往下行，進入大頭茶純林<br><img src="https://1013399.github.io/image-5/261/177646904_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→第二座茅草涼亭<br><img src="https://1013399.github.io/image-5/261/177646910_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→進入桂竹林<br><img src="https://1013399.github.io/image-5/261/177646917_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→第四座觀景台<br><img src="https://1013399.github.io/image-5/261/177646921_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→S形步道往下<br><img src="https://1013399.github.io/image-5/261/177646927_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→步道出口接建國路，取右沿山邊的柏油路<br><img src="https://1013399.github.io/image-5/261/177646932_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→步道出口旁步道圖<br><img src="https://1013399.github.io/image-5/261/177646939_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→朝陽社區田園景觀與行駛中的火車<br><img src="https://1013399.github.io/image-5/261/177646942_l.jpg" alt="朝陽社區田園景觀與行駛中的火車" loading="lazy"></p><p>→叉路，直行為朝陽路與娜娘路交叉口，取右往朝陽社區<br><img src="https://1013399.github.io/image-5/261/177646948_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p><p>→朝陽步道第二登山口<br><img src="https://1013399.github.io/image-5/261/177646957_l.jpg" alt="朝陽步道第二登山口" loading="lazy"></p><p>→接回朝陽路，取右回<br> →回到南澳朝陽漁港</p><h3 id="以十二生肖諺語命名植物如下" tabindex="-1"><a class="header-anchor" href="#以十二生肖諺語命名植物如下" aria-hidden="true">#</a> 以十二生肖諺語命名植物如下</h3><ul><li><p>一鼠-賊仔名(鼠刺)</p></li><li><p>二牛-駛犁仔兄(雙面刺，又稱入地金牛)</p></li><li><p>三虎-爬山崎(三叉虎，又稱三角鱉)</p></li><li><p>四兔-遊東京(菟絲子)<br><img src="https://1013399.github.io/image-5/261/177646960_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p></li><li><p>五龍-皇帝命(拎壁龍，又稱風不動)</p></li><li><p>六蛇-互人驚(筆筒樹，又稱蛇木)</p></li><li><p>七馬-跑兵營(白冇骨消，又稱假走馬風)</p></li><li><p>八羊-食草嶺(山黃麻，又稱山羊麻)<br><img src="https://1013399.github.io/image-5/261/177646967_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p></li><li><p>九猴-爬樹頭(九芎，又稱猴難爬)</p></li><li><p>十雞-啼三聲(光臘樹，又稱白雞油)</p></li><li><p>十一狗-吠契兄(菝契，又稱狗骨刺)</p></li><li><p>十二豬-菜刀命(稜果榕，又稱豬母乳舅)<br><img src="https://1013399.github.io/image-5/261/177646789_l.jpg" alt="朝陽國家步道、南澳龜山" loading="lazy"></p></li></ul><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時(含休息、賞景)</p><p>高低落差：約180公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',40),c={href:"http://www.tonyhuang39.com/tony0380.html",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),b=i("img",{src:"https://1013399.github.io/image-5/261/177647023_l.jpg",alt:"朝陽國家步道、南澳龜山 參考地圖",loading:"lazy"},null,-1);function _(u,y){const t=p("ExternalLinkIcon");return g(),r("div",null,[n,o(" more "),m,i("p",null,[a("原圖連結→ "),i("a",c,[a("Tony的自然人文旅記"),e(t)]),d,b])])}const f=l(h,[["render",_],["__file","post-261-2011-02-11.html.vue"]]);export{f as default};
