import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as r,a as o,b as t,f as i,d as e,e as s}from"./app-a01d9b9c.js";const n={},h=t("p",null,[t("strong",null,"羊稠森林步道"),i("，位於桃園縣蘆竹鄉羊稠坑，早期為放養羊群的坑谷，蘆竹鄉公所將此山區整理為休閒步道，步道全長約3.3公里，有五個出入口，是條不錯的休閒森林步道。")],-1),c=t("p",null,[t("strong",null,"拔崁腳山"),i("，海拔156公尺，一般稱之為七分山，藍天地圖稱此為拔崁腳山，有一無名水泥測量點(但今日未發現)，山頂為一處休憩平台，往北稍有展望。")],-1),m=s('<p><strong>尖山</strong>，海拔185公尺，山頂為三支高壓電塔組(No.065)，據稱此山頭有一圖根點，但未發現，山頂可遠眺五酒桶山。</p><p><strong>羊稠坑山</strong>，海拔225公尺，山頂有HSR高鐵基石No.41106，今日也未發現，但有「羊稠頂點」木頭指標，山頂展望不佳，但在山頂前後各有不錯的展望點，可俯瞰高鐵及高速公路。</p><p><strong>土地公頭山</strong>，海拔242公尺，圖根點基石已遺失，山頂為天線機房，就在桃2鄉道路旁，山頂無視野。</p><p>走走日期：2011/02/06</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高下南崁交流道，接中正路往南崁方向，遇Y叉路取右新南路，再右轉忠孝東路後接南祥路，再左轉中山路，即抵達羊稠巷口，停車於路旁停車格(今日為春節期間，不收費)。旁邊有「自己人餐廳」，對面有南崁國小。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→中山路羊稠巷口，進入巷內<br><img src="https://1013399.github.io/image-5/262/177504476_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→羊稠森林步道入口<br><img src="https://1013399.github.io/image-5/262/177504478_l.jpg" alt="羊稠森林步道入口" loading="lazy"></p><p>→拔崁腳山(七分山)，右有叉路可回羊稠巷，休息後取左往八分山<br><img src="https://1013399.github.io/image-5/262/177504480_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→八分山，右叉路往吳氏祠堂，取左往羊稠坑山<br><img src="https://1013399.github.io/image-5/262/177504481_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→叉路，右為繞腰路，取直上往尖山<br><img src="https://1013399.github.io/image-5/262/177504483_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→尖山山頂<br><img src="https://1013399.github.io/image-5/262/177504484_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→繞腰路在此接回，續直行<br><img src="https://1013399.github.io/image-5/262/177504487_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→叉路，右可往土地公，續直行<br><img src="https://1013399.github.io/image-5/262/177504490_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→十字叉路，右往土地公，左往仁愛路三段560巷，續直行<br><img src="https://1013399.github.io/image-5/262/177504492_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→簡易休憩棚<br><img src="https://1013399.github.io/image-5/262/177504494_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→九分山<br><img src="https://1013399.github.io/image-5/262/177504495_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→樹上釘有光碟標示<br><img src="https://1013399.github.io/image-5/262/177504497_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→步道轉為階梯<br><img src="https://1013399.github.io/image-5/262/177504499_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→展望點(階梯結束)<br><img src="https://1013399.github.io/image-5/262/177504501_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→展望點視野佳，俯瞰高鐵山洞<br><img src="https://1013399.github.io/image-5/262/177504502_l.jpg" alt="高鐵山洞" loading="lazy"></p><p>→羊稠坑山，「羊稠頂點」木頭指標<br><img src="https://1013399.github.io/image-5/262/177504509_l.jpg" alt="羊稠坑山，「羊稠頂點」木頭指標" loading="lazy"></p><p>→俯瞰高鐵出山洞<br><img src="https://1013399.github.io/image-5/262/177504512_l.jpg" alt="高鐵出山洞" loading="lazy"></p><p>→右叉路通往一座涼亭，似乎有點荒涼，續直行<br><img src="https://1013399.github.io/image-5/262/177504514_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→涼亭，前方不遠處即為羊稠步道另一入口<br><img src="https://1013399.github.io/image-5/262/177504517_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→羊稠步道入口解說牌<br><img src="https://1013399.github.io/image-5/262/177504519_l.jpg" alt="羊稠步道入口解說牌" loading="lazy"></p><p>→羊稠步道入口雙羊意象，接桃2鄉道(六福路)，取左行<br><img src="https://1013399.github.io/image-5/262/177504521_l.jpg" alt="羊稠步道入口雙羊" loading="lazy"></p><p>→行約數十公尺，右切穿過鐵絲網往土地公頭山<br><img src="https://1013399.github.io/image-5/262/177504526_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→土地公頭山頂天線機房，雜草叢生，原路退回桃2鄉道<br><img src="https://1013399.github.io/image-5/262/177504528_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→回到羊稠步道入口，原路折返<br> →回到羊稠坑山<br> →回到九分山<br> →回到十字叉路，續直行<br> →回到尖山叉路，取左繞腰路(不再上尖山)<br> →回到八分山</p><p>→沿途看到的基石<br><img src="https://1013399.github.io/image-5/262/177504482_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"><br><img src="https://1013399.github.io/image-5/262/177504530_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→回到拔崁腳山(七分山)，取左回較緩</p><p>→叉路，往左應該也可到中山路，續直行往羊稠巷<br><img src="https://1013399.github.io/image-5/262/177504533_l.jpg" alt="羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山" loading="lazy"></p><p>→回到羊稠森林步道入口<br> →回到中山路羊稠巷口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息、賞景)</p><p>高低落差：約160公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',38),b={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=6200",target:"_blank",rel:"noopener noreferrer"},d=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-5/262/177504557_l.jpg",alt:"羊稠坑森林步道、拔崁腳山、尖山、羊稠坑山、土地公頭山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=g("ExternalLinkIcon");return p(),r("div",null,[h,c,o(" more "),m,t("p",null,[i("原圖連結→ "),t("a",b,[i("登山補給站(藍天圖集)"),e(a)]),d,_])])}const x=l(n,[["render",u],["__file","post-262-2011-02-10.html.vue"]]);export{x as default};
