import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as l,c as r,b as a,f as i,d as o,a as e,e as s}from"./app-afda0674.js";const h={},n=a("p",null,[a("strong",null,"竹坑－南寮登山步道"),i("，位於台中龍井竹坑村、中二高橋下，入口處是跨越山渠洩洪道的木本橋，沿著步道而上可達大肚山之南寮村，步道沿途山峰層巒，視野遼闊；從景觀台可眺望北起台中港南至彰化線西鄉及大肚溪出海口，無論是薄霧晨喚或夕陽餘暉，狹長海岸線風情與觀山景致，盡收眼底。")],-1),m={href:"http://www.lungjing.gov.tw/lungjing_04-1.asp?viid=13",target:"_blank",rel:"noopener noreferrer"},b=s('<p>竹坑－南寮登山步道，由龍井鄉公所於96年開始整建，於99年10月竣工，除了原有的主線步道、A及B線步道，並增加了C線步道，讓原路折返的路線，變成O型路線，得以欣賞沿途不同的景觀。</p><p>農曆春節將至，為了到台中港採買新鮮魚貨，順便到此走走，原計畫由下往上走，即從竹坑入口走主步道，至終點南寮端後折返，再改走B步道回，但途中發現有新建C步道可繞回竹坑入口，於是決定改走C步道，不必走原路，又可避開B步道的墳墓區，輕鬆走完一圈僅需2小時左右。</p><p>走走日期：2011/01/30</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道三號南下，出龍井交流道接中興路往龍井方向，遇中興橋不上橋走右側平面道路後左轉沙田路六段，續行至沙田路四段過龍井區公所後約1K，便可見竹坑南寮登山古道指標，左轉進入後沿指標便可到達停車場(國道三號高架橋的底下)。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→停車場<br><img src="https://1013399.github.io/image-5/263/176729466_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→竹坑入口意象<br><img src="https://1013399.github.io/image-5/263/176729470_l.jpg" alt="竹坑入口" loading="lazy"></p><p>→木本橋(紀念龍井鄉第一屆民選鄉長-陳木本先生)<br><img src="https://1013399.github.io/image-5/263/176729471_l.jpg" alt="竹坑-南寮登山步道 木本橋" loading="lazy"></p><p>→仁者樂山(後方有廁所)<br><img src="https://1013399.github.io/image-5/263/176729472_l.jpg" alt="竹坑-南寮登山步道 仁者樂山" loading="lazy"></p><p>→步道竣工誌<br><img src="https://1013399.github.io/image-5/263/176729476_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→步道左側被燒得光禿禿，聽說將要規劃新步道<br><img src="https://1013399.github.io/image-5/263/176729477_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→大榕樹休憩處<br><img src="https://1013399.github.io/image-5/263/176729479_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→B步道叉路，步道800m處，左為B步道起點，取右續行<br><img src="https://1013399.github.io/image-5/263/176729480_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→步道轉為階梯<br><img src="https://1013399.github.io/image-5/263/176729481_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→A步道叉路，左為主線，先取右A步道往觀景平台<br><img src="https://1013399.github.io/image-5/263/176729483_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→A步道終點觀景平台<br><img src="https://1013399.github.io/image-5/263/176729485_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→俯瞰國道三號車流及遠眺台中火力發電廠，賞景後原路折返<br><img src="https://1013399.github.io/image-5/263/176729488_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→回到A步道叉路，續直行</p><p>→俯瞰山谷鯉魚山<br><img src="https://1013399.github.io/image-5/263/176729489_l.jpg" alt="竹坑-南寮登山步道 山谷鯉魚山" loading="lazy"></p><p>→鯉魚山解說牌<br><img src="https://1013399.github.io/image-5/263/176729491_l.jpg" alt="鯉魚山解說牌" loading="lazy"></p><p>→龍止觀花架涼亭，左側為公墓<br><img src="https://1013399.github.io/image-5/263/176729493_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→俯瞰C步道及對面的竹坑山<br><img src="https://1013399.github.io/image-5/263/176729495_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→叉路，B步道在此接回主步道<br><img src="https://1013399.github.io/image-5/263/176729496_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→步道叉路，右下可接C步道往古井(古井天梯)，續直行<br><img src="https://1013399.github.io/image-5/263/176729498_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→古井解說牌<br><img src="https://1013399.github.io/image-5/263/176729501_l.jpg" alt="竹坑-南寮登山步道 古井解說牌" loading="lazy"></p><p>→俯瞰天梯及古井<br><img src="https://1013399.github.io/image-5/263/176729503_l.jpg" alt="天梯及古井 竹坑-南寮登山步道" loading="lazy"></p><p>→步道1800m處，石板步道結束，接柏油路<br><img src="https://1013399.github.io/image-5/263/176729505_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→福德祠土地公及南寮入口停車場，右側有廁所<br><img src="https://1013399.github.io/image-5/263/176729507_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→南寮入口意象，後方為南寮健康公園<br><img src="https://1013399.github.io/image-5/263/176729508_l.jpg" alt="竹坑-南寮登山步道入口" loading="lazy"></p><p>→叉路，右往林家宗祠及C步道，先取左亨德公園<br><img src="https://1013399.github.io/image-5/263/176729515_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→亨德紀念公園，旁有圓形大池塘<br><img src="https://1013399.github.io/image-5/263/176729509_l.jpg" alt="亨德紀念公園 竹坑-南寮登山步道" loading="lazy"></p><p>→步道終點，福建宮，退回叉路往林家宗祠<br><img src="https://1013399.github.io/image-5/263/176729514_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→林家宗祠<br><img src="https://1013399.github.io/image-5/263/176729517_l.jpg" alt="林家宗祠 竹坑-南寮登山步道" loading="lazy"></p><p>→C步道起點，一開始即為陡下階梯(共121階)<br><img src="https://1013399.github.io/image-5/263/176729518_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→C步道建設於山谷中<br><img src="https://1013399.github.io/image-5/263/176729521_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→花架涼亭<br><img src="https://1013399.github.io/image-5/263/176729522_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→叉路，右經天梯回主步道<br><img src="https://1013399.github.io/image-5/263/176729523_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→仰望天梯，取左續行<br><img src="https://1013399.github.io/image-5/263/176729525_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→古井<br><img src="https://1013399.github.io/image-5/263/176729527_l.jpg" alt="古井 竹坑-南寮登山步道" loading="lazy"></p><p>→古井旁有兩座打水幫浦<br><img src="https://1013399.github.io/image-5/263/176729531_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→龍華樹休閒廣場<br><img src="https://1013399.github.io/image-5/263/176729533_l.jpg" alt="龍華樹休閒廣場" loading="lazy"></p><p>→龍華樹右前方即為鯉魚山<br><img src="https://1013399.github.io/image-5/263/176729534_l.jpg" alt="竹坑-南寮登山步道" loading="lazy"></p><p>→C步道終點，接柏油路，前方即為竹坑入口停車場<br><img src="https://1013399.github.io/image-5/263/176729535_l.jpg" alt="竹坑入口停車場 竹坑-南寮登山步道" loading="lazy"></p><p>→回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時(含休息、賞景)</p><p>高低落差：約180公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>拍攝自竹坑入口旁導覽圖<br><img src="https://1013399.github.io/image-5/263/176729546_l.jpg" alt="竹坑-南寮登山步道 導覽圖" loading="lazy"></p>',49);function c(d,_){const t=p("ExternalLinkIcon");return l(),r("div",null,[n,a("p",null,[i("(參考資料："),a("a",m,[i("臺中市龍井區公所"),o(t)]),i(")")]),e(" more "),b])}const y=g(h,[["render",c],["__file","post-263-2011-02-01.html.vue"]]);export{y as default};
