import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as e,a as o,b as a,f as i,d as r,e as s}from"./app-a9847bbb.js";const h={},n=a("p",null,[a("strong",null,"烏嘎彥山"),i("，位於苗栗泰安鄉大興村，海拔約1000公尺，無基石，附近有石水坊露營區及烏嘎彥露營區，山頂有座2層樓的觀景台，展望極佳，天氣晴朗時，可望層層無邊的山巒、天氣轉換時，可見變化萬千的雲海，尤其初春時節，李花與梅花盛開，滿山遍野非常漂亮。")],-1),c=s('<p>今日本想走走南勢山及烏嘎彥山，再到清安豆腐街買酸菜，但屋外濕霧細雨，猶豫了許久，以致於較晚出發，於是放棄了南勢山行程，直接往石水坊走走烏嘎彥，到達石水坊已經11點了，竟然出太陽，輕裝上路，O形一圈，1小時輕鬆走完。</p><p>走走日期：2011/01/23</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高下苗栗交流道往苗栗市方向，再左轉台72快速道路往汶水，快速道路終點後右轉台3線，於127.7K「7-11便利商店」前，左轉苗62鄉道往泰安溫泉方向，行至清安豆腐街右轉苗62-1線，過清安國小後，再過清安橋約20公尺遇叉路，取右轉往大坪方向(左往洗水山腦寮庄)，直行至「石水坊」於路旁停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→石水坊露營區，旁邊有雪霸草莓園，出發沿苗62-1線往上行<br><img src="https://1013399.github.io/image-5/264/176199270_l.jpg" alt="石水坊露營區 烏嘎彥山" loading="lazy"></p><p>→路旁櫻花快謝光了，拍張特寫<br><img src="https://1013399.github.io/image-5/264/176199273_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→烏嘎彥山登山口，取右上<br><img src="https://1013399.github.io/image-5/264/176199276_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→進入竹林產業道路<br><img src="https://1013399.github.io/image-5/264/176199279_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→叉路，取右行<br><img src="https://1013399.github.io/image-5/264/176199284_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→路旁不知名植物<br><img src="https://1013399.github.io/image-5/264/176199286_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→叉路，依指標取右，路徑轉為土石路<br><img src="https://1013399.github.io/image-5/264/176199288_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→彎竹景觀，滿漂亮的<br><img src="https://1013399.github.io/image-5/264/176199292_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→下至鞍部，轉為水泥路往上，路徑稍陡<br><img src="https://1013399.github.io/image-5/264/176199295_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→綠色巨大儲水桶<br><img src="https://1013399.github.io/image-5/264/176199296_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→原來這些水桶是大坪與大興村的供水系統<br><img src="https://1013399.github.io/image-5/264/176199298_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→叉路，左往烏嘎彥露營區，取右上<br><img src="https://1013399.github.io/image-5/264/176199301_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→烏嘎彥山，山頂觀景台<br><img src="https://1013399.github.io/image-5/264/176199302_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→登上觀景台，俯瞰右下方石水坊露營區及雪霸草莓園<br><img src="https://1013399.github.io/image-5/264/176199304_l.jpg" alt="石水坊露營區 雪霸草莓園" loading="lazy"></p><p>→眺望右方對面的熊佧山，後方的洗水山(冬瓜山)已被雲霧遮蔽<br><img src="https://1013399.github.io/image-5/264/176199307_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→左下方即為烏嘎彥露營區，遠眺山形特殊的鷂婆山，休息後續往前行<br><img src="https://1013399.github.io/image-5/264/176199308_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→烏嘎彥露營區叉路，取右下行<br><img src="https://1013399.github.io/image-5/264/176199310_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→開始陡下濕滑水泥路，必須小心碎步行走<br><img src="https://1013399.github.io/image-5/264/176199312_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→陡坡結束接產道，左往鑽石林露營區、鷂婆山，取右回石水坊<br><img src="https://1013399.github.io/image-5/264/176199315_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→不知是李子園還是梅子園<br><img src="https://1013399.github.io/image-5/264/176199316_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→李花、梅花，傻傻分不清楚<br><img src="https://1013399.github.io/image-5/264/176199318_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→回到苗62-1線，取右回石水坊<br><img src="https://1013399.github.io/image-5/264/176199263_l.jpg" alt="烏嘎彥山" loading="lazy"></p><p>→回到石水坊</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時(含休息、賞景)</p><p>高低落差：約110公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',31),d={href:"http://tw.myblog.yahoo.com/chien12.tw/archive?l=f&id=22",target:"_blank",rel:"noopener noreferrer"},m=a("br",null,null,-1),b=a("img",{src:"https://1013399.github.io/image-5/264/176199340_l.jpg",alt:"烏嘎彥山 參考地圖",loading:"lazy"},null,-1);function _(u,y){const t=g("ExternalLinkIcon");return p(),e("div",null,[n,o(" more "),c,a("p",null,[i("原圖連結→ "),a("a",d,[i("烏嘎彥景觀休閒露營區"),r(t)]),m,b])])}const f=l(h,[["render",_],["__file","post-264-2011-01-25.html.vue"]]);export{f as default};
