import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as g,c as l,a as p,b as i,f as a,e as r}from"./app-19acc492.js";const o={},s=i("p",null,[i("strong",null,"福德森林步道"),a("，位於新竹縣竹東鎮瑞峰里，沿途森林茂密，途中有座土地公廟-福德祠，步道可能因此而命名，步道不長、落差不大，約10分鐘就可走完。")],-1),e=i("p",null,[i("strong",null,"上瑞步道"),a("，位於新竹縣竹東鎮上瑞社區(上坪里與瑞峰里)中央寮溪畔，步道由「中央寮親水步道」、「糯米橋步道」及「瑞峰步道」串連一起而成，統稱上瑞步道，途中會經過二座糯米橋(中央橋、瑞峰橋)及客家伙房屋群，繞一圈僅需約1小時，為休閒散步的好去處。")],-1),h=r('<p>走走日期：2010/12/24</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>開車從竹東走122縣道往五峰方向，行至31.8k處(上坪派出所對面)右轉進入即可抵達廣惠宮，將車停於廣惠宮前廣場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→廣惠宮<br><img src="https://1013399.github.io/image-5/274/173921829_l.jpg" alt="廣惠宮" loading="lazy"></p><p>→廣惠宮旁解說牌，續往122縣道(南清公路)行<br><img src="https://1013399.github.io/image-5/274/173921834_l.jpg" alt="廣惠宮旁解說牌 福德森林步道、上瑞步道" loading="lazy"></p><p>→接122縣道，右為上坪老街往五峰方向，取左往竹東方向<br><img src="https://1013399.github.io/image-5/274/173921837_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→上坪老街<br><img src="https://1013399.github.io/image-5/274/173921840_l.jpg" alt="上坪老街" loading="lazy"></p><p>→隴西園叉路，取左東河產業道路往隴西園<br><img src="https://1013399.github.io/image-5/274/173921842_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→福德森林步道入口，取右進入步道<br><img src="https://1013399.github.io/image-5/274/173921845_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→福德森林步道<br><img src="https://1013399.github.io/image-5/274/173921849_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→叉路，左右皆可，往左會經過土地公廟，取左上<br><img src="https://1013399.github.io/image-5/274/173921852_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→土地公廟<br><img src="https://1013399.github.io/image-5/274/173921855_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→步道最高點，有一大石，右有明顯山徑不知往何處，取左下<br><img src="https://1013399.github.io/image-5/274/173921858_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→步道轉為陡下<br><img src="https://1013399.github.io/image-5/274/173921863_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→步道出口，接回東河產道<br><img src="https://1013399.github.io/image-5/274/173921866_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→出口旁指示牌，續往右行<br><img src="https://1013399.github.io/image-5/274/173921872_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→上瑞觀光果園叉路，續直行<br><img src="https://1013399.github.io/image-5/274/173921875_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→糯米橋叉路，左往糯米橋(中央橋)，續直行往隴西園<br><img src="https://1013399.github.io/image-5/274/173921880_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→惠民橋，右側有上瑞親水公園<br><img src="https://1013399.github.io/image-5/274/173921884_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→產道上仰望五指山(只能看到三峰)<br><img src="https://1013399.github.io/image-5/274/173921886_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→隴西園<br><img src="https://1013399.github.io/image-5/274/173921889_l.jpg" alt="隴西園" loading="lazy"></p><p>→隴西園停車場，楓樹正紅<br><img src="https://1013399.github.io/image-5/274/173921893_l.jpg" alt="隴西園停車場 楓樹正紅" loading="lazy"></p><p>→中央寮親水步道入口，但大門深鎖，折返回糯米橋叉路<br> →回到糯米橋叉路，取右下</p><p>→第一座糯米橋(中央橋)<br><img src="https://1013399.github.io/image-5/274/173921895_l.jpg" alt="糯米橋(中央橋) 福德森林步道、上瑞步道" loading="lazy"></p><p>→過橋後叉路，右為中央寮親水步道往隴西園，左為糯米橋步道<br><img src="https://1013399.github.io/image-5/274/173921897_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→中央寮親水步道雜草叢生，有點荒涼，取左糯米橋步道往廣惠宮<br><img src="https://1013399.github.io/image-5/274/173921903_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→糯米橋步道<br><img src="https://1013399.github.io/image-5/274/173921909_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→接上瑞峰步道<br><img src="https://1013399.github.io/image-5/274/173921915_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→第二座糯米橋(瑞峰橋)<br><img src="https://1013399.github.io/image-5/274/173921921_l.jpg" alt="糯米橋(瑞峰橋) 福德森林步道、上瑞步道" loading="lazy"></p><p>→瑞峰步道<br><img src="https://1013399.github.io/image-5/274/173921924_l.jpg" alt="瑞峰步道 福德森林步道、上瑞步道" loading="lazy"></p><p>→此處頗多台灣肖楠，非常漂亮<br><img src="https://1013399.github.io/image-5/274/173921928_l.jpg" alt="台灣肖楠 福德森林步道、上瑞步道" loading="lazy"></p><p>→經過客家夥房屋群(竟然忘了拍照)</p><p>→福德宮<br><img src="https://1013399.github.io/image-5/274/173921931_l.jpg" alt="福德森林步道、上瑞步道" loading="lazy"></p><p>→這棵楓樹也很漂亮<br><img src="https://1013399.github.io/image-5/274/173921933_l.jpg" alt="楓樹 福德森林步道、上瑞步道" loading="lazy"></p><p>→回到廣惠宮<br><img src="https://1013399.github.io/image-5/274/173921936_l.jpg" alt="廣惠宮" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時</p><p>高低落差：約60公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>拍攝自糯米橋叉路<br><img src="https://1013399.github.io/image-5/274/173921973_l.jpg" alt="福德森林步道、上瑞步道 參考地圖" loading="lazy"></p>',40);function m(n,b){return g(),l("div",null,[s,e,p(" more "),h])}const _=t(o,[["render",m],["__file","post-274-2010-12-28.html.vue"]]);export{_ as default};
