import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as o,a as r,b as i,f as g,d as s,e as h}from"./app-a9847bbb.js";const e={},m=i("p",null,"孝子山、慈母峰、普陀山位於台北縣平溪鄉，三座山峰相距不遠，周圍群峰環繞，大小山頭加總起來有20多座，而著名的孝子山，山勢不高，但石峰陡峻，可說是平溪最熱門的一座山。",-1),n=i("p",null,[i("strong",null,"孝子山"),g("，海拔360公尺，無基石，頂峰可眺望慈母峰、普陀山，遠眺石筍尖、薯榔尖、石底觀音山，俯瞰平溪地區，遠眺五分山與基隆河谷之中的嶺腳十分寮，由於山形酷似大陸黃山，故孝子山也有小黃山之稱。")],-1),b=h('<p><strong>慈母峰</strong>，海拔410公尺，原無基石，有登山隊豎立一不鏽鋼基點，晴字○○二號。展望極佳，對面為普陀山，可俯瞰孝子山，每年五月母親節時，此線最為應景而熱門。</p><p><strong>普陀山</strong>，海拔450公尺，無基石，展望絕佳，岩頂有兩處小空地， 前擺三寶佛(本師釋迦牟尼佛、東方藥師佛、西方阿彌陀佛)，後置仙女像。</p><p><strong>中央尖</strong>，海拔580公尺，無基石，山頂最高處有塊岩石可以站立，遠眺五分山區，層巒疊翠，視野極佳。</p><p><strong>臭頭山</strong>，又稱東勢格庄山，海拔476公尺，有顆三等三角點No.1008基石，周圍林木茂盛，展望不佳，臭頭山相較於今日其他四座山，顯得輕鬆容易。</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道3號北上接國道5號，下石碇交流道左轉接106縣道往平溪方向，至61.5K平溪國中，將車停於平溪國中斜對面停車場。</p><p>走走日期： 2010/12/19</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→平溪國中斜對面停車場，整裝後往平溪方向行<br><img src="https://1013399.github.io/image-5/275/173204631_l.jpg" alt="平溪國中斜對面停車場" loading="lazy"></p><p>→觀音禪寺產道叉路，取右上產道往孝子山<br><img src="https://1013399.github.io/image-5/275/173204634_l.jpg" alt="觀音禪寺產道叉路" loading="lazy"></p><p>→孝子山登山步道口，左下方有觀音禪寺<br><img src="https://1013399.github.io/image-5/275/173204641_l.jpg" alt="孝子山登山步道口" loading="lazy"></p><p>→沿石階上行，原始森林中景緻宜人<br><img src="https://1013399.github.io/image-5/275/173204645_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→平台三叉路，取左上先往孝子山<br><img src="https://1013399.github.io/image-5/275/173204650_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→陡上鑿石階梯<br><img src="https://1013399.github.io/image-5/275/173204653_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→鞍部平台叉路，左右皆可登上孝子山，左上右回，稍後出現叉路，續前行緩繞岩壁<br><img src="https://1013399.github.io/image-5/275/173204658_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→轉鐵梯登岩峰，此段最為驚險<br><img src="https://1013399.github.io/image-5/275/173204659_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→孝子山山頂，眺望慈母峰、普陀山<br><img src="https://1013399.github.io/image-5/275/173204662_l.jpg" alt="孝子山山頂 慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→可看見山友正在攀爬慈母峰，折返<br><img src="https://1013399.github.io/image-5/275/173204666_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→回到平台叉路，續行有叉路，取左轉往慈母峰<br><img src="https://1013399.github.io/image-5/275/173204669_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→通往慈母峰的鑿石階梯<br><img src="https://1013399.github.io/image-5/275/173204676_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→慈母峰山頂及基點<br><img src="https://1013399.github.io/image-5/275/173204678_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→眺望對面的普陀山<br><img src="https://1013399.github.io/image-5/275/173204680_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→俯瞰孝子山<br><img src="https://1013399.github.io/image-5/275/173204684_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→續行有叉路，直行往慈恩嶺方向，取右下往普陀山鞍部<br><img src="https://1013399.github.io/image-5/275/173204688_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→攀爬岩壁而下(由下往上拍攝)<br><img src="https://1013399.github.io/image-5/275/173204690_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→普陀山鞍部，左往慈恩嶺，取右往普陀山<br><img src="https://1013399.github.io/image-5/275/173204697_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，續型階梯回孝子山，取左土徑往普陀山<br><img src="https://1013399.github.io/image-5/275/173204706_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→通往普陀山的鑿石階梯<br><img src="https://1013399.github.io/image-5/275/173204708_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→普陀山山頂<br><img src="https://1013399.github.io/image-5/275/173204710_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→再俯瞰孝子山<br><img src="https://1013399.github.io/image-5/275/173204714_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→遠眺五分山，原路退回<br><img src="https://1013399.github.io/image-5/275/173204715_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→回到普陀山鞍部平台，休息、午餐後續行往中央尖</p><p>→叉路，右往虎嘴口停車場，取左往中央尖<br><img src="https://1013399.github.io/image-5/275/173204718_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→陡上石階<br><img src="https://1013399.github.io/image-5/275/173204724_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→大岩壁叉路，右上為鑿石階梯，取左山路，兩線幾乎平行<br><img src="https://1013399.github.io/image-5/275/173204729_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，右往慈恩嶺，取左往中央尖<br><img src="https://1013399.github.io/image-5/275/173204733_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→稜線，右往慈恩嶺，取左往中央尖<br><img src="https://1013399.github.io/image-5/275/173204738_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→路旁特殊的植物<br><img src="https://1013399.github.io/image-5/275/173204746_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→小山頭(沿路上上下下數個小山頭)<br><img src="https://1013399.github.io/image-5/275/173204751_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，左往紫萊山，取右往中央尖<br><img src="https://1013399.github.io/image-5/275/173204758_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→途中仰望中央尖<br><img src="https://1013399.github.io/image-5/275/173204762_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→開始出現攀岩，難度還不高<br><img src="https://1013399.github.io/image-5/275/173204769_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→幾乎垂直攀岩，踏腳點難尋，比孝子山還驚險<br><img src="https://1013399.github.io/image-5/275/173204774_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→最後垂直攀岩，身體後仰，用手的力量將身體上拉<br><img src="https://1013399.github.io/image-5/275/173204780_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→上到稜線取左即為中央尖山頂<br><img src="https://1013399.github.io/image-5/275/173204784_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→退回上稜處，循稜而行<br><img src="https://1013399.github.io/image-5/275/173204788_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→稜線展望佳<br><img src="https://1013399.github.io/image-5/275/173204793_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→路徑轉為一路陡下<br><img src="https://1013399.github.io/image-5/275/173204796_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→溪畔叉路，右往平溪國中，取左往臭頭山<br><img src="https://1013399.github.io/image-5/275/173204799_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，左往東勢格山，續直行往臭頭山<br><img src="https://1013399.github.io/image-5/275/173204803_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→臭頭山，三等三角點No.1008，續往前行<br><img src="https://1013399.github.io/image-5/275/173204805_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，左往東勢格山，取右下山<br><img src="https://1013399.github.io/image-5/275/173204808_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→進入竹林<br><img src="https://1013399.github.io/image-5/275/173204809_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，直行往東勢格古道草原，取右下<br><img src="https://1013399.github.io/image-5/275/173204813_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，左往峰頭尖，取右行<br><img src="https://1013399.github.io/image-5/275/173204815_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，右往中央尖，續直行往平溪國中<br><img src="https://1013399.github.io/image-5/275/173204817_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→路旁小瀑布<br><img src="https://1013399.github.io/image-5/275/173204818_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→下至平緩山徑，過小橋<br><img src="https://1013399.github.io/image-5/275/173204824_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→廢礦坑，坑內已積滿水<br><img src="https://1013399.github.io/image-5/275/173204828_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，右不知往何處，取左下<br><img src="https://1013399.github.io/image-5/275/173204834_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→礦場遺址<br><img src="https://1013399.github.io/image-5/275/173204837_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→轉為平坦的碎石步道，像是舊的運礦台車路線<br><img src="https://1013399.github.io/image-5/275/173204840_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，右往慈恩嶺、孝子山，續直行往平溪國中<br><img src="https://1013399.github.io/image-5/275/173204845_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，直行碎石步道往觀音禪寺產道，取左下階梯<br><img src="https://1013399.github.io/image-5/275/173204848_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→叉路，左往石燭尖，取右下，旁為瓜寮坑2號民宅<br><img src="https://1013399.github.io/image-5/275/173204852_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→接106縣道，往右回停車場<br><img src="https://1013399.github.io/image-5/275/173204628_l.jpg" alt="孝子山、慈母峰、普陀山、中央尖、臭頭山" loading="lazy"></p><p>→回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約6小時(含休息、午餐)</p><p>高低落差：</p><ul><li>106縣道    → 平台三叉路  上升約100公尺 (海拔310公尺)</li><li>平台三叉路 → 孝子山      上升約50公尺  (海拔360公尺)</li><li>孝子山     → 平台三叉路  下降約50公尺  (海拔310公尺)</li><li>平台三叉路 → 慈母峰      上升約100公尺 (海拔410公尺)</li><li>慈母峰     → 普陀山鞍部  下降約20公尺  (海拔390公尺)</li><li>普陀山鞍部 → 普陀山      上升約60公尺  (海拔450公尺)</li><li>普陀山     → 普陀山鞍部  下降約60公尺  (海拔390公尺)</li><li>普陀山鞍部 → 中央尖      上升約190公尺 (海拔580公尺)</li><li>中央尖     → 溪畔叉路    下降約130公尺 (海拔450公尺)</li><li>溪畔叉路   → 臭頭山      上升約30公尺  (海拔480公尺)</li><li>臭頭山     → 106縣道     下降約270公尺 (海拔210公尺)</li></ul></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',70),c={href:"http://www.yougoipay.com/kenny/w876/index.htm",target:"_blank",rel:"noopener noreferrer"},d=i("figure",null,[i("img",{src:"https://1013399.github.io/image-5/275/173205617_l.jpg",alt:"孝子山、慈母峰、普陀山、中央尖、臭頭山 參考地圖",tabindex:"0",loading:"lazy"}),i("figcaption",null,"孝子山、慈母峰、普陀山、中央尖、臭頭山 參考地圖")],-1);function _(u,y){const t=l("ExternalLinkIcon");return p(),o("div",null,[m,n,r(" more "),b,i("p",null,[g("原圖連結→"),i("a",c,[g("偶而獨步山林間(蕭郎)"),s(t)])]),d])}const f=a(e,[["render",_],["__file","post-275-2010-12-21.html.vue"]]);export{f as default};
