import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as o,b as i,f as t,d as e,e as s}from"./app-7f62a6ff.js";const h={},n=i("p",null,[i("strong",null,"馬那邦山"),t("，也稱馬拉邦山，海拔1406公尺，位於苗栗縣大湖鄉和泰安鄉的交界，山頂有一等三角點基石、馬那邦山標示牌及兩塊亂裂的紀念碑，視野極佳，可遠眺大霸尖山至雪山之間的聖稜線，每年楓紅季節總是吸引許多山友造訪，該山名列台灣小百岳#037。")],-1),m=i("p",null,[i("strong",null,"細道邦山"),t("，海拔1064公尺，也位於苗栗縣大湖鄉和泰安鄉的交界，無基石，但有豐原清風嶺登山隊剛立不久的不鏽鋼標示牌，為一座斷崖崩壁、四方平坦，造型特殊的一座山，曾被稱為棺材山。另外在馬那邦山東北方，也有一座海拔1286公尺的細道邦山，但網路上較少提及此山。")],-1),b=s('<h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道一號下苗栗交流道，往苗栗市區方向，接72快速道路直走到底接台3線，右轉南下往汶水，至大湖酒莊叉路，不走右側外環道，取左進入大湖市區，於雪見遊憩區標示牌左轉，接苗61縣道經大湖國中往中興方向，過中興橋後取右上珠湖農路(左往雪見遊憩區，路口有指示)，一路蜿蜒而上4公里至終點獅子亭(雙層的大涼亭)停車場，免費停車(已貼告示：100年1月1日起收費)。</p><p>走走日期： 2010/12/12</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→獅子亭停車場<br><img src="https://1013399.github.io/image-5/276/172487390_l.jpg" alt="獅子亭停車場 馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→於停車場眺望細道邦山及遠方的耀婆山<br><img src="https://1013399.github.io/image-5/276/172487392_l.jpg" alt="耀婆山" loading="lazy"></p><p>→取獅子亭後方農路往上行<br><img src="https://1013399.github.io/image-5/276/172487394_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→農路轉為石階步道<br><img src="https://1013399.github.io/image-5/276/172487396_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→十字叉路，直往上湖登山口，右往細道邦山，取左往珠湖登山口<br><img src="https://1013399.github.io/image-5/276/172487399_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→珠湖登山口<br><img src="https://1013399.github.io/image-5/276/172487401_l.jpg" alt="珠湖登山口" loading="lazy"></p><p>→珠湖斷崖<br><img src="https://1013399.github.io/image-5/276/172487404_l.jpg" alt="珠湖斷崖" loading="lazy"></p><p>→1K處叉路，右往上湖登山口，續直行<br><img src="https://1013399.github.io/image-5/276/172487410_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→馬那邦山古戰場紀念碑，旁有解說牌<br><img src="https://1013399.github.io/image-5/276/172487411_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→軍營遺址及解說牌<br><img src="https://1013399.github.io/image-5/276/172487414_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→「楓」情萬種<br><img src="https://1013399.github.io/image-5/276/172487407_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→此棵楓樹尚未轉紅<br><img src="https://1013399.github.io/image-5/276/172487419_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→「楓」姿綽約<br><img src="https://1013399.github.io/image-5/276/172487433_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→馬那邦山林相解說牌<br><img src="https://1013399.github.io/image-5/276/172487425_l.jpg" alt="馬那邦山林相解說牌" loading="lazy"></p><p>→石門隘口<br><img src="https://1013399.github.io/image-5/276/172487436_l.jpg" alt="石門隘口 馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→石門上方的楓樹也很漂亮<br><img src="https://1013399.github.io/image-5/276/172487435_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→石門隘口解說牌<br><img src="https://1013399.github.io/image-5/276/172487438_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→開始陡上<br><img src="https://1013399.github.io/image-5/276/172487456_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→繼續陡上<br><img src="https://1013399.github.io/image-5/276/172487441_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→接上木棧道，右往天然湖登山口，取左上<br><img src="https://1013399.github.io/image-5/276/172487443_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→馬那邦山休憩平台，人山人海<br><img src="https://1013399.github.io/image-5/276/172487445_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→徐慶榮紀念碑<br><img src="https://1013399.github.io/image-5/276/172487455_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→馬那邦山山頂，展望極佳<br><img src="https://1013399.github.io/image-5/276/172487448_l.jpg" alt="馬那邦山山頂" loading="lazy"></p><p>→馬那邦山標示牌、斷裂紀念碑及一等三角點<br><img src="https://1013399.github.io/image-5/276/172487453_l.jpg" alt="馬那邦山標示牌、斷裂紀念碑及一等三角點" loading="lazy"></p><p>→休息、賞景後，原路折返(人太多了，沒法吃午餐)<br> →回到陡坡起點處午餐<br> →回到石門隘口<br> →回到軍營遺址<br> →回到古戰場<br> →回到1K處叉路，不走原路，取左往上湖登山口<br> →進入森林步道<br><img src="https://1013399.github.io/image-5/276/172487459_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→轉為竹林步道<br><img src="https://1013399.github.io/image-5/276/172487464_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→接產道，有收費接駁車至上湖之第一停車場(上山$150、下山$100)<br><img src="https://1013399.github.io/image-5/276/172487468_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→上湖登山口，左下往第一停車場，取右往珠湖登山口<br><img src="https://1013399.github.io/image-5/276/172487471_l.jpg" alt="上湖登山口 馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→登山口旁導覽圖<br><img src="https://1013399.github.io/image-5/276/172487473_l.jpg" alt="上湖登山導覽圖 馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→回到十字叉路，直走回獅子亭停車場，取左往細道邦山<br> →進入樹林後，在此依路條取右<br><img src="https://1013399.github.io/image-5/276/172487477_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→上至小山頭，芒草較多<br><img src="https://1013399.github.io/image-5/276/172487479_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→越過倒木<br><img src="https://1013399.github.io/image-5/276/172487481_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→又一個小山頭<br><img src="https://1013399.github.io/image-5/276/172487491_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→下至低處鞍部，林縫中仰望細道邦山岩壁<br><img src="https://1013399.github.io/image-5/276/172487493_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→陡上竹林<br><img src="https://1013399.github.io/image-5/276/172487494_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→由此取右接崖邊稜線<br><img src="https://1013399.github.io/image-5/276/172487500_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→再越過倒木<br><img src="https://1013399.github.io/image-5/276/172487504_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→細道邦山，拍照後原路折返<br><img src="https://1013399.github.io/image-5/276/172487388_l.jpg" alt="馬那邦山(珠湖線)、細道邦山" loading="lazy"></p><p>→回到十字叉路，取左回獅子亭停車場<br> →回到獅子亭停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約5小時(含休息、賞景、午餐)</p><p>高低落差：馬那邦山約500公尺、細道邦山約160公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',46),c={href:"http://tw.myblog.yahoo.com/designteng-go/article?mid=6201&next=6159&l=f&fid=7",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-5/276/172487722_l.jpg",alt:"馬那邦山(珠湖線)、細道邦山 參考地圖",loading:"lazy"},null,-1);function u(y,j){const a=l("ExternalLinkIcon");return p(),r("div",null,[n,m,o(" more "),b,i("p",null,[t("原圖連結→"),i("a",c,[t("小鄧的登山札記"),e(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-276-2010-12-15.html.vue"]]);export{x as default};
