import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as o,a as e,b as i,e as t,d as r,f as s}from"./app-9ec59630.js";const h={},n=i("p",null,[i("strong",null,"哈堪尼山"),t("，海拔1990公尺，三等三角點No.6283，位於苗栗縣南庄鄉，與加里山隔著一條風美溪，一般山友登哈堪尼山都會連走加里山(哈加縱走)，但需陡下至風美溪谷，再陡上攀爬一段幾乎垂直的峭壁，難度較高。")],-1),c=i("p",null,"今日到達登山口停車場，已停了約20輛車，都是爬加里山的山友，由於時間稍晚，所以只單攻哈堪尼山，原路來回。",-1),m=s('<p>走走日期：2010/11/21</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道一號下頭份交流道，往三灣、南庄方向行駛，經過三灣、南庄，過南庄橋後叉路，左轉苗21鄉道往鹿場部落，過「神仙谷」經風美橋上山，至苗21鄉道終點的鹿場部落、陸家派出所，取左進入狹窄的陡水泥產道，車行約2km抵達一葉蘭農場，續沿狹窄的陡俏水泥產道上行，遇松柏山莊叉路，取左到達加里山停車場，私人經營，每次收費100元。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→加里山登山口，左彎進入<br><img src="https://1013399.github.io/image-5/281/170667810_l.jpg" alt="加里山登山口" loading="lazy"></p><p>→杉木林水管路<br><img src="https://1013399.github.io/image-5/281/170667812_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→叉路，右下往加里山，取直沿水管路續行<br><img src="https://1013399.github.io/image-5/281/170667813_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→叉路，直行往風美溪，取左上哈堪尼山<br><img src="https://1013399.github.io/image-5/281/170667814_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→「登山平安」告示牌No.201<br><img src="https://1013399.github.io/image-5/281/170667817_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→緩坡結束，開始陡上<br><img src="https://1013399.github.io/image-5/281/170667818_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→優美的杉木林<br><img src="https://1013399.github.io/image-5/281/170667820_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→「登山平安」告示牌No.202<br><img src="https://1013399.github.io/image-5/281/170667822_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→巨石，可避雨<br><img src="https://1013399.github.io/image-5/281/170667824_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→轉折點，取左上行，漸漸轉為雜木林<br><img src="https://1013399.github.io/image-5/281/170667826_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→「登山平安」告示牌No.203<br><img src="https://1013399.github.io/image-5/281/170667827_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→坡度變為更陡<br><img src="https://1013399.github.io/image-5/281/170667830_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→繼續陡上，路旁樹上有「加油」紅色噴漆<br><img src="https://1013399.github.io/image-5/281/170667832_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→「加油」紅色噴漆(共有二處)<br><img src="https://1013399.github.io/image-5/281/170667834_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→二處噴漆間，路左有三棵巨木<br><img src="https://1013399.github.io/image-5/281/170667836_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→唯一一處拉繩路段<br><img src="https://1013399.github.io/image-5/281/170667838_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→叉路，左往鹿湖山，取直往哈堪尼山<br><img src="https://1013399.github.io/image-5/281/170667841_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→薄霧中的陽光<br><img src="https://1013399.github.io/image-5/281/170667845_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→路徑不明，依指示左彎往哈堪尼山<br><img src="https://1013399.github.io/image-5/281/170667848_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→「登山平安」告示牌No.204<br><img src="https://1013399.github.io/image-5/281/170667850_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→折斷的巨木<br><img src="https://1013399.github.io/image-5/281/170667853_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→另一棵巨木<br><img src="https://1013399.github.io/image-5/281/170667854_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→「登山平安」告示牌No.205<br><img src="https://1013399.github.io/image-5/281/170667856_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→進入箭竹林<br><img src="https://1013399.github.io/image-5/281/170667860_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→哈堪尼山，三等三角點No.6283<br><img src="https://1013399.github.io/image-5/281/170667865_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→山頂標示牌下有「登山平安」告示牌No.206<br><img src="https://1013399.github.io/image-5/281/170667870_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→續行有叉路，左往鹿場大山，右往加里山，在此休息、午餐<br><img src="https://1013399.github.io/image-5/281/170667875_l.jpg" alt="哈堪尼山" loading="lazy"></p><p>→原路折返<br> →回到鹿湖山叉路，續直行<br> →回到風美溪叉路，取右回<br> →回到加里山叉路，續直行<br> →回到登山口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4.5小時(含休息、午餐)</p><p>高低落差：約600公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',35),b={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=33721",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-5/281/170668238_l.jpg",alt:"哈堪尼山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=g("ExternalLinkIcon");return p(),o("div",null,[n,c,e(" more "),m,i("p",null,[t("原圖連結→"),i("a",b,[t("登山補給站-祥馬地圖集(編號 33721)"),r(a)]),d,_])])}const N=l(h,[["render",u],["__file","post-281-2010-11-25.html.vue"]]);export{N as default};
