import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as p,c as r,a as o,b as i,e as t,d as s,f as e}from"./app-39f3f472.js";const h={},n=i("p",null,[i("strong",null,"東滿步道"),t("，起點在東眼山森林遊樂區，終點在滿月圓森林遊樂區，是一條相當優美的森林浴步道，沿途植物生態豐富、鳥類繁多，深秋時節，更是賞楓的好去處，還可順賞滿月圓著名的瀑布區，享受大自然之美。")],-1),m=i("p",null,"東滿步道，全長約7.36公里，但再加上從東眼山收費站至東滿步道起點，及滿月圓森林遊樂區至停車場，全程約14公里，屬縱走行程，需考慮接駁問題。",-1),b=e('<p>今日參加公司登山社活動，兩部大巴共80人，行程預計連走拉卡山，因雨後濕滑及時間受限，只好作罷，但一路飽覽優美的林相、欣賞壯麗的瀑布，也算不虛此行。</p><p>走走日期：2010/11/07</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下大溪交流道，左轉台3線往大溪方向再接台7線，先後經大溪、頭寮、慈湖、三民、復興後，於台7線17.7K處左轉桃119縣道進入東眼山產業道路，119縣道終止後接東眼山林道，至東眼山森林遊樂區收費大門口下車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→東眼山森林遊樂區大門<br><img src="https://1013399.github.io/image-5/282/124320492_l.jpg" alt="東眼山森林遊樂區大門" loading="lazy"></p><p>→接東眼山林道(碎石路)<br><img src="https://1013399.github.io/image-5/282/169207446_l.jpg" alt="東眼山林道" loading="lazy"></p><p>→東眼山林道解說牌<br><img src="https://1013399.github.io/image-5/282/169207448_l.jpg" alt="東眼山林道解說牌" loading="lazy"></p><p>→涼亭，後方可往卡外山(5分鐘)<br><img src="https://1013399.github.io/image-5/282/169207451_l.jpg" alt="東滿步道" loading="lazy"></p><p>→生痕化石區<br><img src="https://1013399.github.io/image-5/282/169207455_l.jpg" alt="東滿步道" loading="lazy"></p><p>→生痕化石<br><img src="https://1013399.github.io/image-5/282/169207453_l.jpg" alt="東滿步道" loading="lazy"></p><p>→東滿步道起點<br><img src="https://1013399.github.io/image-5/282/169207459_l.jpg" alt="東滿步道" loading="lazy"></p><p>→貴妃椅大石<br><img src="https://1013399.github.io/image-5/282/169207461_l.jpg" alt="東滿步道" loading="lazy"></p><p>→觀瀑橋(東滿步道1K)<br><img src="https://1013399.github.io/image-5/282/169207467_l.jpg" alt="東滿步道" loading="lazy"></p><p>→開始上坡<br><img src="https://1013399.github.io/image-5/282/169207469_l.jpg" alt="東滿步道" loading="lazy"></p><p>→東滿步道2K<br><img src="https://1013399.github.io/image-5/282/169207473_l.jpg" alt="東滿步道" loading="lazy"></p><p>→優美的森林浴步道<br><img src="https://1013399.github.io/image-5/282/169207474_l.jpg" alt="東滿步道" loading="lazy"></p><p>→坡度變得更陡<br><img src="https://1013399.github.io/image-5/282/169207477_l.jpg" alt="東滿步道" loading="lazy"></p><p>→東眼山叉路，左後上可往東眼山，續直行<br><img src="https://1013399.github.io/image-5/282/169207479_l.jpg" alt="東滿步道" loading="lazy"></p><p>→東滿步道3K<br><img src="https://1013399.github.io/image-5/282/169207485_l.jpg" alt="東滿步道" loading="lazy"></p><p>→拉卡山第一登山口叉路，左上往拉卡山，續直行<br><img src="https://1013399.github.io/image-5/282/169207486_l.jpg" alt="東滿步道" loading="lazy"></p><p>→東滿步道4K<br><img src="https://1013399.github.io/image-5/282/169207490_l.jpg" alt="東滿步道" loading="lazy"></p><p>→拉卡山第二登山口叉路，左往拉卡山，續直行<br><img src="https://1013399.github.io/image-5/282/169207491_l.jpg" alt="東滿步道" loading="lazy"></p><p>→北插天山叉路(東滿步道4.5K前)，右往北插天山，左往滿月圓，在此休息午餐<br><img src="https://1013399.github.io/image-5/282/169207492_l.jpg" alt="東滿步道" loading="lazy"></p><p>→往滿月圓續行，開始陡下<br><img src="https://1013399.github.io/image-5/282/169207496_l.jpg" alt="東滿步道" loading="lazy"></p><p>→東滿步道5K<br><img src="https://1013399.github.io/image-5/282/169207499_l.jpg" alt="東滿步道" loading="lazy"></p><p>→過小溪，有山友在此取水<br><img src="https://1013399.github.io/image-5/282/169207503_l.jpg" alt="東滿步道" loading="lazy"></p><p>→空曠休憩地<br><img src="https://1013399.github.io/image-5/282/169207508_l.jpg" alt="東滿步道" loading="lazy"></p><p>→再陡下木棧階梯<br><img src="https://1013399.github.io/image-5/282/169207511_l.jpg" alt="東滿步道" loading="lazy"></p><p>→東滿步道6K，休息<br><img src="https://1013399.github.io/image-5/282/169207512_l.jpg" alt="東滿步道" loading="lazy"></p><p>→拉卡山第三登山口叉路，左上往拉卡山，續直行<br><img src="https://1013399.github.io/image-5/282/169207516_l.jpg" alt="東滿步道" loading="lazy"></p><p>→東滿步道7K<br><img src="https://1013399.github.io/image-5/282/169207517_l.jpg" alt="東滿步道" loading="lazy"></p><p>→到達滿月圓森林遊樂區，左為自導式步道，取右往滿月圓瀑布<br><img src="https://1013399.github.io/image-5/282/169207519_l.jpg" alt="滿月圓森林遊樂區 東滿步道" loading="lazy"></p><p>→叉路，左往遊客中心，取右往滿月圓瀑布<br><img src="https://1013399.github.io/image-5/282/169207522_l.jpg" alt="東滿步道" loading="lazy"></p><p>→滿月圓瀑布叉路，左往瀑布，取右往觀瀑亭<br><img src="https://1013399.github.io/image-5/282/169207526_l.jpg" alt="東滿步道" loading="lazy"></p><p>→滿月圓瀑布觀瀑亭<br><img src="https://1013399.github.io/image-5/282/169207525_l.jpg" alt="東滿步道" loading="lazy"></p><p>→滿月圓瀑布，折返往處女瀑布<br><img src="https://1013399.github.io/image-5/282/169207523_l.jpg" alt="東滿步道" loading="lazy"></p><p>→叉路，直往遊客中心，取右往處女瀑布<br><img src="https://1013399.github.io/image-5/282/169207530_l.jpg" alt="東滿步道" loading="lazy"></p><p>→過小橋，橋上往下拍攝美景<br><img src="https://1013399.github.io/image-5/282/169207533_l.jpg" alt="東滿步道" loading="lazy"></p><p>→陡上往處女瀑布<br><img src="https://1013399.github.io/image-5/282/169207535_l.jpg" alt="東滿步道" loading="lazy"></p><p>→處女瀑布觀瀑亭<br><img src="https://1013399.github.io/image-5/282/169207536_l.jpg" alt="處女瀑布觀瀑亭" loading="lazy"></p><p>→處女瀑布更為壯麗，原路折返往遊客中心<br><img src="https://1013399.github.io/image-5/282/169207537_l.jpg" alt="處女瀑布" loading="lazy"></p><p>→滿月圓DIY教室<br><img src="https://1013399.github.io/image-5/282/169207539_l.jpg" alt="滿月圓DIY教室" loading="lazy"></p><p>→滿月圓遊客中心<br><img src="https://1013399.github.io/image-5/282/169207540_l.jpg" alt="滿月圓遊客中心" loading="lazy"></p><p>→滿月圓森林遊樂區收費管理站<br><img src="https://1013399.github.io/image-5/282/169207542_l.jpg" alt="滿月圓森林遊樂區收費管理站" loading="lazy"></p><p>→收費管理站正面<br><img src="https://1013399.github.io/image-5/282/169207440_l.jpg" alt="東滿步道" loading="lazy"></p><p>→到達第二停車場<br> →到達第一停車場，大巴已等候多時，換裝賦歸</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約6小時(含休息)</p><p>高低落差：上升約300公尺再下降約750公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',50),c={href:"http://ms1.ctjh.tpc.edu.tw/~uank3/ltmap26.htm",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-5/282/169207619_l.jpg",alt:"東滿步道 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=l("ExternalLinkIcon");return p(),r("div",null,[n,m,o(" more "),b,i("p",null,[t("原圖連結→"),i("a",c,[t("江翠登山社(圓開前輩)"),s(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-282-2010-11-11.html.vue"]]);export{x as default};
