import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as g,c as e,a as r,b as i,f as t,d as o,e as s}from"./app-c5577115.js";const n={},h=i("p",null,[i("strong",null,"德芙蘭生態步道"),t("，是林務局及台電工作人員所踩踏出來的，東臨大甲溪，由中橫公路29.15K處進入，步道全長約3050M，海拔高度約700M-975M，植被為暖溫帶闊葉林，沿途佈滿蕨類、血藤等具熱帶叢林風味之植物，間植有五葉松…等針葉樹。")],-1),c=i("p",null,"德芙蘭是泰雅語，容易舀水的意思，指此區有許多湧泉，是好山好水而適合人居住的地方。德芙蘭生態步道另一登山口，位於谷關大道院往東卯山0.5K處。",-1),d=s('<p>登山日期：2010/10/31</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>開車行駛中山高，於台中系統交流道轉國道四號往豐原，至國道四號路底左轉接台3線(167K)左轉往東勢，過東勢大橋後右轉台8線往谷關，至台8線29.15公里處左轉水泥路(右側有紅色長青橋往松鶴部落)，不久即可達停車場。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→德芙蘭步道停車場，旁有電塔(卓蘭-天輪99號)、廁所和觀景台<br><img src="https://1013399.github.io/image-5/285/168780212_l.jpg" alt="德芙蘭步道停車場" loading="lazy"></p><p>→德芙蘭步道入口，前行仍為產道，取右德芙蘭步道<br><img src="https://1013399.github.io/image-5/285/168780213_l.jpg" alt="德芙蘭步道入口" loading="lazy"></p><p>→德芙蘭步道入口解說牌<br><img src="https://1013399.github.io/image-5/285/168780214_l.jpg" alt="德芙蘭步道入口解說牌" loading="lazy"></p><p>→步道一開始為緩下<br><img src="https://1013399.github.io/image-5/285/168780215_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→小橋<br><img src="https://1013399.github.io/image-5/285/168780217_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→步道轉為階梯陡上<br><img src="https://1013399.github.io/image-5/285/168780219_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→第一座吊橋<br><img src="https://1013399.github.io/image-5/285/168780220_l.jpg" alt="德芙蘭生態步道 吊橋" loading="lazy"></p><p>→步道轉趨平緩<br><img src="https://1013399.github.io/image-5/285/168780221_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→傾斜的巨木<br><img src="https://1013399.github.io/image-5/285/168780222_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→第二座吊橋<br><img src="https://1013399.github.io/image-5/285/168780224_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→沿山溝上行，轉為Z字型往上<br><img src="https://1013399.github.io/image-5/285/168780228_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→巨大勾藤<br><img src="https://1013399.github.io/image-5/285/168780230_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→德芙蘭步道1K木樁，山徑又轉為平緩<br><img src="https://1013399.github.io/image-5/285/168780231_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→電塔(卓蘭-天輪066號)<br><img src="https://1013399.github.io/image-5/285/168780233_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→涼亭，步道最高點，在此休息，遇到萬板登山隊一行四十餘人<br><img src="https://1013399.github.io/image-5/285/168780236_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→取左續行，山徑緩下好走<br><img src="https://1013399.github.io/image-5/285/168780240_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→叉路，右往谷關大道院、東卯山，取左下<br><img src="https://1013399.github.io/image-5/285/168780243_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→德芙蘭步道2K木樁，山徑Z字型陡下<br><img src="https://1013399.github.io/image-5/285/168780248_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→叉路，右接中橫公路，取左過小橋<br><img src="https://1013399.github.io/image-5/285/168780251_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→電塔(卓蘭-天輪100號)<br><img src="https://1013399.github.io/image-5/285/168780254_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→松樹苗圃<br><img src="https://1013399.github.io/image-5/285/168780258_l.jpg" alt="德芙蘭生態步道" loading="lazy"></p><p>→德芙蘭步道3K木樁，右彎後不久即到廁所後方<br><img src="https://1013399.github.io/image-5/285/168780211_l.jpg" alt="德芙蘭步道3K木樁" loading="lazy"></p><p>→回到停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時(含休息)</p><p>高低落差：約250公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',30),m={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=24343",target:"_blank",rel:"noopener noreferrer"},b=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-5/285/168780304_l.jpg",alt:"德芙蘭生態步道 參考地圖",loading:"lazy"},null,-1);function u(j,y){const a=p("ExternalLinkIcon");return g(),e("div",null,[h,c,r(" more "),d,i("p",null,[t("原圖連結→"),i("a",m,[t("登山補給站brownlee(小李)"),o(a)]),b,_])])}const x=l(n,[["render",u],["__file","post-285-2010-11-04.html.vue"]]);export{x as default};
