import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r,o,c as p,b as t,f as a,d as i,a as n,e as g}from"./app-19acc492.js";const s={},h=t("p",null,[t("strong",null,"阿罩霧山"),a("，海拔249公尺，位於台中縣霧峰鄉林家花園及朝陽科技大學的後山，山頂有二等三角點#1136基石。霧峰因早期晨昏迷霧朦朧，故名阿罩霧，山頂附近遍植鳳梨及龍眼樹，山頂雜草叢生，不過沿途的產業道路視野佳，該山名列台灣小百岳#046。")],-1),c={href:"http://gohiking.myweb.hinet.net/h6/TCHajhaowu.htm",target:"_blank",rel:"noopener noreferrer"},d=g('<p><strong>鳳山</strong>，又稱樟公山，也有人稱此山為阿罩霧山，海拔181公尺，位於台中縣霧峰鄉樟公廟附近，山頂有三等三角點#184基石，登山口平時大門深鎖，為尊重地主，不宜冒然闖入，能否造訪此山，一切隨緣。</p><p>登山日期：2010/10/24</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>霧峰中正路南下左轉萊園路，至路底接民生路，先右轉再馬上左轉成功路102巷，過林家花園後至102巷口，左轉後再馬上右轉樟公巷，一路上坡至「樟公廟」停車場停車。</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>(從樟公廟起登，走產業道路，途中叉路多，皆取主線)</p><p>→樟公廟<br><img src="https://1013399.github.io/image-5/287/167992275_l.jpg" alt="樟公廟" loading="lazy"></p><p>→樟公廟停車場<br><img src="https://1013399.github.io/image-5/287/167992289_l.jpg" alt="樟公廟停車場" loading="lazy"></p><p>→停車場旁老樟樹<br><img src="https://1013399.github.io/image-5/287/167992267_l.jpg" alt="阿罩霧山、鳳山" loading="lazy"></p><p>→紫明宮<br><img src="https://1013399.github.io/image-5/287/167992295_l.jpg" alt="紫明宮" loading="lazy"></p><p>→鳳山登山口，電桿上有噴漆指示<br><img src="https://1013399.github.io/image-5/287/167992316_l.jpg" alt="鳳山登山口" loading="lazy"></p><p>→鐵門深鎖，不冒然闖入，放棄造訪，續直行<br><img src="https://1013399.github.io/image-5/287/167992327_l.jpg" alt="阿罩霧山、鳳山" loading="lazy"></p><p>→小平台休息處<br><img src="https://1013399.github.io/image-5/287/167992336_l.jpg" alt="阿罩霧山、鳳山" loading="lazy"></p><p>→沿途展望甚佳<br><img src="https://1013399.github.io/image-5/287/167992348_l.jpg" alt="阿罩霧山、鳳山" loading="lazy"></p><p>→俯瞰朝陽科技大學<br><img src="https://1013399.github.io/image-5/287/167992357_l.jpg" alt="朝陽科技大學" loading="lazy"></p><p>→叉路，左不知往何處，依噴漆指示取右上<br><img src="https://1013399.github.io/image-5/287/167992371_l.jpg" alt="阿罩霧山、鳳山" loading="lazy"></p><p>→阿罩霧山登山口，取右土徑上行<br><img src="https://1013399.github.io/image-5/287/167992385_l.jpg" alt="阿罩霧山登山口" loading="lazy"></p><p>→阿罩霧山二等三角點，原路折返<br><img src="https://1013399.github.io/image-5/287/167992260_l.jpg" alt="阿罩霧山、鳳山" loading="lazy"></p><p>→回到樟公廟停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時</p><p>高低落差：約90公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>(原圖明台高中誤植為明德家商，已修正，感謝Jerry顏指正)</p>',23),m={href:"http://blog.xuite.net/laoweiz/blog/15438828",target:"_blank",rel:"noopener noreferrer"},_=t("br",null,null,-1),b=t("img",{src:"https://1013399.github.io/image-5/287/168234518_l.jpg",alt:"阿罩霧山、鳳山 參考地圖",loading:"lazy"},null,-1);function u(f,y){const e=r("ExternalLinkIcon");return o(),p("div",null,[h,t("p",null,[a("(摘自"),t("a",c,[a("「優遊山林溪」網站"),i(e)]),a(")")]),n(" more "),d,t("p",null,[a("原圖連結→"),t("a",m,[a("老尉子的部落格"),i(e)]),_,b])])}const x=l(s,[["render",u],["__file","post-287-2010-10-27.html.vue"]]);export{x as default};
