import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as p,c as o,a as r,b as t,f as a,d as l,e as n}from"./app-a01d9b9c.js";const s={},h=t("p",null,[t("strong",null,"大混山"),a("，海拔1541公尺，位於新竹縣尖石鄉，三等三角點編號6270，山頂展望不佳。")],-1),c=t("p",null,[t("strong",null,"牛欄坑山"),a("，海拔1400公尺，無基石，有古道翁前輩釘牌，山頂為一休息小平台，無視野。")],-1),d=t("p",null,"一般人攀登大混山，都是安排與李棟山縱走，或是從道下山(大混山西南峰)走上來，但是落差大、耗時且有接駁問題，今日出發較晚，選擇較為輕鬆的路線，從「八五山越嶺道路」起登，全程路條多、叉路少、路跡明顯，無迷路之虞，只是蚊子稍多。",-1),m={href:"http://tw.myblog.yahoo.com/designteng-go/article?mid=7109&prev=7151&next=7092",target:"_blank",rel:"noopener noreferrer"},b=n('<p>走走日期： 2010/10/03</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>北二高下芎林交流道往內灣尖石方向，至內灣後續行120縣道往尖石，於39.6K處右轉，沿著數碼天空的指標上山，一路陡上必須小心駕駛，過數碼天空後，再行約2KM水泥路結束轉為碎石路，於越嶺高點路旁停車(續行陡下碎石路，行車恐會打滑)，登山口就在前方不遠處左側。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→大混山登山口(右往竹60鄉道那羅地區，取左產道上)<br><img src="https://1013399.github.io/image-5/290/166056303_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→登山口藍天隊指標<br><img src="https://1013399.github.io/image-5/290/166056307_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→產道終止接竹林陡上<br><img src="https://1013399.github.io/image-5/290/166056311_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→上稜線<br><img src="https://1013399.github.io/image-5/290/166056327_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→路旁可愛小野菇<br><img src="https://1013399.github.io/image-5/290/166056332_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→巨木<br><img src="https://1013399.github.io/image-5/290/166056334_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→木製雙電桿，可能為日據時期所使用<br><img src="https://1013399.github.io/image-5/290/166056339_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→巨木上長有鳥巢蕨<br><img src="https://1013399.github.io/image-5/290/166056342_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→開始起霧了<br><img src="https://1013399.github.io/image-5/290/166056346_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→亂石路徑<br><img src="https://1013399.github.io/image-5/290/166056350_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→道下山叉路，右可往道下山(大混山西南峰)，續直行<br><img src="https://1013399.github.io/image-5/290/166056356_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→叉路口藍天隊指標<br><img src="https://1013399.github.io/image-5/290/166056364_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→又開始陡上，但不長<br><img src="https://1013399.github.io/image-5/290/166056373_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→鐵製窯爐遺址，石塊下有門<br><img src="https://1013399.github.io/image-5/290/166056382_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→大混山叉路，直行可往李棟山，右方小丘可賞景，但已霧茫茫一片，取左上<br><img src="https://1013399.github.io/image-5/290/166056387_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→大混山，三等三角點No.6270，續行可往李棟山，原路折返<br><img src="https://1013399.github.io/image-5/290/166056389_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→路旁特殊的植物<br><img src="https://1013399.github.io/image-5/290/166056390_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→回到大混山叉路<br> →回到道下山叉路，順訪附近牛欄坑山，取左下</p><p>→牛欄坑山，為一小平台，叉路至此約5分鐘可達<br><img src="https://1013399.github.io/image-5/290/166056399_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→牛欄坑山古道翁前輩釘牌，原路折返<br><img src="https://1013399.github.io/image-5/290/166056395_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→回到道下山叉路，取左回</p><p>→路旁闊葉根節蘭<br><img src="https://1013399.github.io/image-5/290/166056403_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→霧更濃了，雷聲大作，速速下山<br><img src="https://1013399.github.io/image-5/290/166056405_l.jpg" alt="大混山、牛欄坑山" loading="lazy"></p><p>→回到大混山登山口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：上山約75分鐘、下山約50分鐘</p><p>高低落差：約350公尺</p></div><p>→煤源山登山口(回程拍攝，不久後下起大雷雨)<br><img src="https://1013399.github.io/image-5/290/166056409_l.jpg" alt="煤源山登山口" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',32),_={href:"http://tw.myblog.yahoo.com/designteng-go/article?mid=7151&prev=-1&next=7109",target:"_blank",rel:"noopener noreferrer"},u=t("br",null,null,-1),y=t("img",{src:"https://1013399.github.io/image-5/290/166056501_l.jpg",alt:"大混山、牛欄坑山 參考地圖",loading:"lazy"},null,-1);function j(z,f){const i=e("ExternalLinkIcon");return p(),o("div",null,[h,c,d,r(" more "),t("p",null,[a("原計畫回程順登煤源山(拉洛山東北峰)，依「"),t("a",m,[a("小鄧的登山札記"),l(i)]),a("」所述，全程約3.2K，落差約110M，來回行約100分鐘，但即將下大雷雨，安全起見，只好放棄，今日「沒緣」去煤源山，改天有緣再訪吧。")]),b,t("p",null,[a("原圖連結→"),t("a",_,[a("小鄧的登山札記"),l(i)]),u,y])])}const k=g(s,[["render",j],["__file","post-290-2010-10-06.html.vue"]]);export{k as default};
