import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as p,c as g,a as r,b as t,f as a,d as o,e as s}from"./app-afda0674.js";const n={},h=t("p",null,[t("strong",null,"貓囒山自然步道"),a("，沿途環境清幽、視野展望極佳，可欣賞滿山遍谷的阿薩姆(台18號)紅茶園、柳杉及日月潭全景，是日月潭觀賞日出的最佳景點。貓囒山步道有兩處入口，東入口位於竹石園生態池旁，西入口位於明潭國中旁之台21公路。")],-1),c=t("p",null,[t("strong",null,"貓囒山"),a("，海拔1020公尺，位於南投縣魚池鄉日月潭旁，原立有三等三角點5005與14號基石及三等內補216號基石皆遺失，山頂有氣象站風力塔、涼亭等設施，氣象設施花圃內有一顆日月潭氣象站的基石。山頂展望佳，西面可眺望集集大山、九份二山；北面俯瞰埔里地區、中台禪寺清晰可見，眺望白葉、桃米坑等山，該山名列台灣小百岳#049。")],-1),d=s('<p>走走日期： 2010/07/30</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>走完樟空崙山後，往回走杉林溪公路接151縣道回鹿谷，過鹿谷後接131縣道往水里，於水里接台16線再轉台21線往日月潭，於台21線63K左轉(明潭國中前，路口設有鐵鍊路障，但車輛可通行)上至茶葉改良場，將車停於茶業改良場停車場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→茶葉改良場(99年2月起管制，除公務及親友，不得進入)<br><img src="https://1013399.github.io/image-5/295/160766714_l.jpg" alt="茶葉改良場" loading="lazy"></p><p>→新井耕吉郎紀念亭<br><img src="https://1013399.github.io/image-5/295/160766713_l.jpg" alt="新井耕吉郎紀念亭" loading="lazy"></p><p>→登山口，循階梯而上<br><img src="https://1013399.github.io/image-5/295/160766717_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>→階梯結束，轉為平緩碎石路，途中俯瞰<br><img src="https://1013399.github.io/image-5/295/160766737_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>→接上茶改場車道，右為茶葉改良場後門，取左上，此車道稱為紅葉步道<br><img src="https://1013399.github.io/image-5/295/160766718_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>→步道沿途展望不錯<br><img src="https://1013399.github.io/image-5/295/160766719_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>→觀景台<br><img src="https://1013399.github.io/image-5/295/160766721_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>→涼亭，右側為改良過的台18紅茶園<br><img src="https://1013399.github.io/image-5/295/160766722_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>→小型觀景台<br><img src="https://1013399.github.io/image-5/295/160766724_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>→貓囒山步道2K木樁<br><img src="https://1013399.github.io/image-5/295/160766725_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>→日月潭氣象站，可入內參觀<br><img src="https://1013399.github.io/image-5/295/160766727_l.jpg" alt="日月潭氣象站" loading="lazy"></p><p>→日月潭貓囒山大石<br><img src="https://1013399.github.io/image-5/295/160766728_l.jpg" alt="日月潭貓囒山大石" loading="lazy"></p><p>→貓囒山平台展望甚佳<br><img src="https://1013399.github.io/image-5/295/160766729_l.jpg" alt="貓囒山平台展望" loading="lazy"></p><p>→氣象站風力塔<br><img src="https://1013399.github.io/image-5/295/160766731_l.jpg" alt="氣象站風力塔" loading="lazy"></p><p>→日月潭氣象站基石<br><img src="https://1013399.github.io/image-5/295/160766735_l.jpg" alt="日月潭氣象站基石" loading="lazy"></p><p>→雨量儀、雨量計<br><img src="https://1013399.github.io/image-5/295/160766736_l.jpg" alt="雨量儀、雨量計" loading="lazy"></p><p>→原路下山<br> →回到茶葉改良場停車場<br> →夜宿中華電信日月潭會館(位於伊達紹碼頭附近)<br><img src="https://1013399.github.io/image-5/295/160766739_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><p>隔日清晨於會館房間遠眺貓囒山<br><img src="https://1013399.github.io/image-5/295/160766743_l.jpg" alt="貓囒山自然步道、貓囒山" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2小時(含休息)</p><p>高低落差：約180公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',25),m={href:"http://www.keepon.com.tw/ActiveSite/Article/One.asp?ArticleID=25842",target:"_blank",rel:"noopener noreferrer"},b=t("br",null,null,-1),_=t("img",{src:"https://1013399.github.io/image-5/295/160766761_l.jpg",alt:"貓囒山自然步道、貓囒山 參考地圖",loading:"lazy"},null,-1);function u(j,y){const i=e("ExternalLinkIcon");return p(),g("div",null,[h,c,r(" more "),d,t("p",null,[a("原圖連結→"),t("a",m,[a("登山補給站(brownlee小李)"),o(i)]),b,_])])}const x=l(n,[["render",u],["__file","post-295-2010-08-06.html.vue"]]);export{x as default};
