import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as r,a as e,b as i,f as a,d as o,e as s}from"./app-a9847bbb.js";const h={},n=i("p",null,[i("strong",null,"紫南宮"),a("，位於竹山鎮社寮里大公街，建於清乾隆10年(1745年)，供奉福德正神及石頭公，共二個香爐，雖然只是小土地公廟，據傳非常靈驗，前來紫南宮求發財金(福德金)聞名全國。紫南宮有名的除了發財金之外，更有全台獨一無二、號稱七星級的金筍造型廁所。")],-1),b=s('<p>走走日期： 2010/07/29</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>國道三號下竹山交流道，接集山路二段左轉(台3線)，經過竹山秀傳醫院不久續行集山路一段(台3丙線)，遇大公街左轉不久即可抵達(沿途皆有指標)，紫南宮設有公有免費停車場。</p><p>紫南宮<br><img src="https://1013399.github.io/image-5/297/160765667_l.jpg" alt="紫南宮" loading="lazy"></p><p>從金雞母底下鑽過，就不會坐吃山空<br><img src="https://1013399.github.io/image-5/297/160765672_l.jpg" alt="紫南宮" loading="lazy"></p><p>號稱七星級的廁所<br><img src="https://1013399.github.io/image-5/297/160765674_l.jpg" alt="紫南宮 七星級的廁所" loading="lazy"></p><p>拜拜完接著前往溪頭森林遊樂區...</p><p><strong>溪頭森林遊樂區</strong>(溪頭自然教育園區)，位於南投縣鹿谷鄉，為台灣大學農學院實驗林七個營林區之一，園區內遍植杉、柏、紅檜、銀杏與孟宗竹等，並規劃大學池木屑步道、神木、沿溪、觀景、銀杏林、賞鳥等多條森林步道系統，可供遊玩及森林浴，景點有大學池、空中走廊、地震巨石、神木、苗圃、銀杏林、孟宗竹林、陳列館、竹類標本園等，為國內知名的避暑和森林健行聖地。</p><h2 id="行車路線-1" tabindex="-1"><a class="header-anchor" href="#行車路線-1" aria-hidden="true">#</a> 行車路線</h2><p>開車回頭往竹山方向，再轉151線經鹿谷即抵達溪頭森林遊樂區。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→第二停車場<br> →出入口售票處<br> →森林生態展示中心<br> →大學池步道入口<br><img src="https://1013399.github.io/image-5/297/160765678_l.jpg" alt="大學池步道入口 紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→翠虹橋<br><img src="https://1013399.github.io/image-5/297/160765680_l.jpg" alt="翠虹橋 紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→大學池步道<br><img src="https://1013399.github.io/image-5/297/160765683_l.jpg" alt="大學池步道 紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→大學池<br><img src="https://1013399.github.io/image-5/297/160765685_l.jpg" alt="大學池 紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→過竹橋往涼亭休息<br><img src="https://1013399.github.io/image-5/297/160765690_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→往觀景步道<br><img src="https://1013399.github.io/image-5/297/160765695_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→觀景步道入口<br><img src="https://1013399.github.io/image-5/297/160765699_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→步道旁這棵樹與雲霧搭配的美景<br><img src="https://1013399.github.io/image-5/297/160765704_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→步道上涼亭<br><img src="https://1013399.github.io/image-5/297/160765710_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→涼亭展望<br><img src="https://1013399.github.io/image-5/297/160765713_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→轉為木屑步道有點泥濘<br><img src="https://1013399.github.io/image-5/297/160765718_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→接上柏油車道<br> →草坪區露天音樂會場<br> →林間遊憩場<br> →柏油車道仰望空中走廊<br><img src="https://1013399.github.io/image-5/297/160765726_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→空中走廊<br><img src="https://1013399.github.io/image-5/297/160765733_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→空中走廊<br><img src="https://1013399.github.io/image-5/297/160765739_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→空中走廊解說牌<br><img src="https://1013399.github.io/image-5/297/160765781_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→空中走廊出入口<br><img src="https://1013399.github.io/image-5/297/160765789_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→走在空中走廊上<br><img src="https://1013399.github.io/image-5/297/160765746_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→空中走廊俯視車道<br><img src="https://1013399.github.io/image-5/297/160765754_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→空中走廊高約22公尺<br><img src="https://1013399.github.io/image-5/297/160765765_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→空中走廊旁一棵N型樹<br><img src="https://1013399.github.io/image-5/297/160765773_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→續行車道往神木<br> →水量充沛、有如瀑布，也淹蓋了車道<br><img src="https://1013399.github.io/image-5/297/160765795_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→神木(此行最高點，海拔約1330公尺)<br><img src="https://1013399.github.io/image-5/297/160765801_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→神木全貌<br><img src="https://1013399.github.io/image-5/297/160765806_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→折返往神木步道<br> →陰暗的神木步道<br><img src="https://1013399.github.io/image-5/297/160765810_l.jpg" alt="神木步道 紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→接銀杏步道往銀杏林<br> →銀杏步道於過溪處中斷，改走沿溪步道<br> →沿溪步道<br> →沿溪步道過小橋後不久接柏油車道<br><img src="https://1013399.github.io/image-5/297/160765813_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→植物進化探索園<br><img src="https://1013399.github.io/image-5/297/160765816_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→植物進化探索園<br><img src="https://1013399.github.io/image-5/297/160765665_l.jpg" alt="紫南宮、溪頭森林遊樂區" loading="lazy"></p><p>→回到森林生態展示中心<br> →回到出入口售票處<br> →回到第二停車場<br> →夜宿中華電信溪頭會館</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約3小時(含休息)</p><p>高低落差：約190公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',42),d=i("br",null,null,-1),c={href:"http://www.exfo.ntu.edu.tw/sitou/cht/15download/#03",target:"_blank",rel:"noopener noreferrer"},m=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-5/297/160765876_l.jpg",alt:"溪頭森林遊樂區 參考地圖",loading:"lazy"},null,-1);function u(j,y){const t=g("ExternalLinkIcon");return p(),r("div",null,[n,e(" more "),b,i("p",null,[a("(下圖標示路線，點圖可放大，綠色為步道、紅色為車道)"),d,a(" 原圖連結請至→"),i("a",c,[a("溪頭森林遊樂區"),o(t)]),m,_])])}const x=l(h,[["render",u],["__file","post-297-2010-08-04.html.vue"]]);export{x as default};
