import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as t,o as p,c as h,a as o,b as i,d as g,w as n,f as a,e as s}from"./app-7f62a6ff.js";const d={},c=i("p",null,[i("strong",null,"品田山前峰"),a("，海拔3442公尺，無基石，約在品田步道0.35K處，該山不在百岳之列，品田前峰和品田山間隔著品田前峰V型斷崖，登臨品田山必須小心。")],-1),b=i("p",null,[i("strong",null,"品田山"),a("，海拔3524公尺，又稱波秦西崙山，為武陵四秀之首，擁有台灣特殊的摺曲地質構造，遠觀品田山山形，猶如「品」和「田」兩字的堆疊，原有森林三角點已遺失，山頂視野無敵，山勢為百岳十峻之一，名列台灣百岳No.22(舊序)。")],-1),m=s('<p>走走日期：2019/04/07</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><h3 id="→05-10輕裝出發往品田山" tabindex="-1"><a class="header-anchor" href="#→05-10輕裝出發往品田山" aria-hidden="true">#</a> →05:10輕裝出發往品田山</h3><figure><img src="https://1013399.github.io/image-1/3/1209591203_l.jpg" alt="出發往品田山)" tabindex="0" loading="lazy"><figcaption>出發往品田山)</figcaption></figure><p>→途中回望池有山，天空已經開始漸漸泛紅<br><img src="https://1013399.github.io/image-1/3/1209592173_l.jpg" alt="回望池有山，天空已經開始漸漸泛紅" loading="lazy"></p><p>→緩步行走在箭竹草坡步道上<br><img src="https://1013399.github.io/image-1/3/1209590706_l.jpg" alt="箭竹草坡步道)" loading="lazy"></p><h3 id="→回望池有山、桃山、喀拉業山" tabindex="-1"><a class="header-anchor" href="#→回望池有山、桃山、喀拉業山" aria-hidden="true">#</a> →回望池有山、桃山、喀拉業山</h3><figure><img src="https://1013399.github.io/image-1/3/1209590613_l.jpg" alt="回望池有山、桃山、喀拉業山)" tabindex="0" loading="lazy"><figcaption>回望池有山、桃山、喀拉業山)</figcaption></figure><p>→進入樹林<br><img src="https://1013399.github.io/image-1/3/1209590707_l.jpg" alt="進入樹林)" loading="lazy"></p><p>→眺望雪山及圈谷<br><img src="https://1013399.github.io/image-1/3/1209591205_l.jpg" alt="眺望雪山及圈谷)" loading="lazy"></p><p>→品田山前峰，約在品田步道0.35K處，後方山頭即為品田山<br><img src="https://1013399.github.io/image-1/3/1209591594_l.jpg" alt="品田山前峰)" loading="lazy"></p><p>→小心翼翼下V型斷崖<br><img src="https://1013399.github.io/image-1/3/1209592174_l.jpg" alt="小心翼翼下V型斷崖)" loading="lazy"></p><h3 id="→下抵斷崖底部-約在步道0-28k處" tabindex="-1"><a class="header-anchor" href="#→下抵斷崖底部-約在步道0-28k處" aria-hidden="true">#</a> →下抵斷崖底部，約在步道0.28K處</h3><figure><img src="https://1013399.github.io/image-1/3/1209591206_l.jpg" alt="斷崖底部，約在步道0.28K處)" tabindex="0" loading="lazy"><figcaption>斷崖底部，約在步道0.28K處)</figcaption></figure><p>→往上攀爬上品田山<br><img src="https://1013399.github.io/image-1/3/1209591207_l.jpg" alt="往上攀爬上品田山)" loading="lazy"></p><h3 id="→到達品田山山頂" tabindex="-1"><a class="header-anchor" href="#→到達品田山山頂" aria-hidden="true">#</a> →到達品田山山頂</h3><figure><img src="https://1013399.github.io/image-1/3/1209591208_l.jpg" alt="到達品田山山頂)" tabindex="0" loading="lazy"><figcaption>到達品田山山頂)</figcaption></figure><p>→品田山山頂有360度視野，賞景、拍照後原路回<br><img src="https://1013399.github.io/image-1/3/1209591209_l.jpg" alt="品田山山頂有360度視野)" loading="lazy"></p><p>→回到V型斷崖<br><img src="https://1013399.github.io/image-1/3/1209591595_l.jpg" alt="V型斷崖)" loading="lazy"></p><p>→回到品田山前峰<br><img src="https://1013399.github.io/image-1/3/1209592079_l.jpg" alt="品田山前峰)" loading="lazy"></p><p>→回到箭竹草坡，池有山、桃山在正前方<br><img src="https://1013399.github.io/image-1/3/1209592176_l.jpg" alt="箭竹草坡，池有山、桃山在正前方)" loading="lazy"></p><h3 id="→活水源叉路-距離新達山屋約50公尺" tabindex="-1"><a class="header-anchor" href="#→活水源叉路-距離新達山屋約50公尺" aria-hidden="true">#</a> →活水源叉路，距離新達山屋約50公尺</h3><p>若新達山屋儲水桶無水可用時，可由此下切取水<br><img src="https://1013399.github.io/image-1/3/1209591596_l.jpg" alt="活水源叉路)" loading="lazy"></p><h3 id="→回到新達山屋-休息、整裝" tabindex="-1"><a class="header-anchor" href="#→回到新達山屋-休息、整裝" aria-hidden="true">#</a> →回到新達山屋，休息、整裝</h3><figure><img src="https://1013399.github.io/image-1/3/1209591211_l.jpg" alt="新達山屋)" tabindex="0" loading="lazy"><figcaption>新達山屋)</figcaption></figure><p>→休息30分鐘後，重裝下山<br><img src="https://1013399.github.io/image-1/3/1209590889_l.jpg" alt="重裝下山)" loading="lazy"></p><p>→回到池有名樹<br><img src="https://1013399.github.io/image-1/3/1209590890_l.jpg" alt="池有名樹" loading="lazy"></p><p>→回到池有山第二登山口，取右回<br><img src="https://1013399.github.io/image-1/3/1209592080_l.jpg" alt="池有山第二登山口)" loading="lazy"></p><p>→回到池有山第一登山口，續直行<br><img src="https://1013399.github.io/image-1/3/1209590708_l.jpg" alt="池有山第一登山口)" loading="lazy"></p><p>→回到三叉路口，直往桃山，取右池有步道下山<br><img src="https://1013399.github.io/image-1/3/1209591597_l.jpg" alt="三叉路口)" loading="lazy"></p><h3 id="→回到三叉營地-休息、點心" tabindex="-1"><a class="header-anchor" href="#→回到三叉營地-休息、點心" aria-hidden="true">#</a> →回到三叉營地，休息、點心</h3><figure><img src="https://1013399.github.io/image-1/3/1209592177_l.jpg" alt="三叉營地)" tabindex="0" loading="lazy"><figcaption>三叉營地)</figcaption></figure><p>→續行下山<br><img src="https://1013399.github.io/image-1/3/1209590614_l.jpg" alt="下山)" loading="lazy"></p><p>→過了步道3K後，步道趨緩<br><img src="https://1013399.github.io/image-1/3/1209592276_l.jpg" alt="品田山過了步道3K後，步道趨緩)" loading="lazy"></p><p>→於步道2.1K休息、午餐，續行過了1.9K後，連續下急陡坡<br><img src="https://1013399.github.io/image-1/3/1209591291_l.jpg" alt="品田山步道2.1K)" loading="lazy"></p><p>→回到步道1.2K平坦空地，休息<br><img src="https://1013399.github.io/image-1/3/1209591598_l.jpg" alt="品田山步道1.2K)" loading="lazy"></p><p>→到達步道1K里程樁，進入緩坡，過步道0.7K後，轉為之字型步道<br><img src="https://1013399.github.io/image-1/3/1209590891_l.jpg" alt="步道1K里程樁)" loading="lazy"></p><p>→到達池有山登山口，接上瀑布步道(約3.6K處)，取右回<br><img src="https://1013399.github.io/image-1/3/1209592277_l.jpg" alt="到達池有山登山口，接上瀑布步道(約3.6K處)" loading="lazy"></p><h3 id="→回到步道1-5k松濤亭" tabindex="-1"><a class="header-anchor" href="#→回到步道1-5k松濤亭" aria-hidden="true">#</a> →回到步道1.5K松濤亭</h3><figure><img src="https://1013399.github.io/image-1/3/1209592081_l.jpg" alt="品田山步道1.5K松濤亭" tabindex="0" loading="lazy"><figcaption>品田山步道1.5K松濤亭</figcaption></figure><p>→回到步道1.1K桃山叉路<br><img src="https://1013399.github.io/image-1/3/1209591292_l.jpg" alt="品田山步道1.1K桃山叉路" loading="lazy"></p><h3 id="→回到武陵吊橋" tabindex="-1"><a class="header-anchor" href="#→回到武陵吊橋" aria-hidden="true">#</a> →回到武陵吊橋</h3><figure><img src="https://1013399.github.io/image-1/3/1209592178_l.jpg" alt="武陵吊橋" tabindex="0" loading="lazy"><figcaption>武陵吊橋</figcaption></figure><p>→回到武陵山莊<br><img src="https://1013399.github.io/image-1/3/1209592082_l.jpg" alt="武陵山莊" loading="lazy"></p><p>→回到停車場，梳洗換裝<br><img src="https://1013399.github.io/image-1/3/1209592083_l.jpg" alt="停車場" loading="lazy"></p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約9小時(含休息、午餐)</p><p>高低落差：(不含上下起伏)</p><ul><li>新達山屋 → 品田山前峰 約上升260公尺</li><li>品田山前峰 → 品田山 約下降20公尺後上升100公尺</li><li>新達山屋 → 武陵山莊 約下降1280公尺</li></ul></div><h2 id="上一篇相關文章" tabindex="-1"><a class="header-anchor" href="#上一篇相關文章" aria-hidden="true">#</a> 上一篇相關文章</h2>',48),u=i("h2",{id:"參考地圖",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#參考地圖","aria-hidden":"true"},"#"),a(" 參考地圖")],-1),_={href:"https://www.spnp.gov.tw/Site/Hiking/WulingFour#lg=1&slide=0",target:"_blank",rel:"noopener noreferrer"},f=i("figure",null,[i("img",{src:"https://1013399.github.io/image-1/3/1209593196_l.jpg",alt:"武陵四秀-參考地圖)",tabindex:"0",loading:"lazy"}),i("figcaption",null,"武陵四秀-參考地圖)")],-1);function j(y,z){const e=t("RouterLink"),l=t("ExternalLinkIcon");return p(),h("div",null,[c,b,o(" more "),m,i("p",null,[g(e,{to:"/posts/post-4-2019-05-01.html"},{default:n(()=>[a("武陵四秀另二秀(DAY-1 池有山)")]),_:1})]),u,i("p",null,[a("原圖連結→"),i("a",_,[a("雪霸國家公園管理處"),g(l)])]),f])}const k=r(d,[["render",j],["__file","post-3-2019-05-02.html.vue"]]);export{k as default};
