import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as e,o as p,c as g,a as o,b as a,f as t,d as r,e as s}from"./app-c5577115.js";const n={},h=a("p",null,[a("strong",null,"劍潭古道"),t("，位於苗栗縣造橋鄉大西村劍潭水庫對面，為早期大西村、大龍村居民往來之古道，經造橋鄉長張雙旺爭取農委會水保局專案補助，並利用擴大公共就業人力、物力，重新整修該古道，讓古道得以重見天日。")],-1),c=a("p",null,"劍潭古道，全長1.76公里(設有里程碑)，全程為石階、石板或原木的健康步道，設有歇腳、觀景、斜陽三座涼亭，沿途山巒層層，視野遼闊，造橋、竹南、頭份一帶的山巒、田野景致入目可見，鄉公所為打造古道的桐花風情，沿古道兩側廣植油桐樹和桂花樹，已成為每年四、五月賞桐的好去處。",-1),d=s('<p>走走日期： 2010/06/06</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高下頭份交流道接台1線(中華路)往南行駛，過中港溪橋後不久至尖山叉路(右側有菜市場)，左轉尖豐路(台13線)往苗栗，至大西村左轉苗14鄉道後(有紅綠燈、路口右側有7-11便利商店)，直行即可到達，將車停於劍潭古道入口路旁。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→劍潭古道入口(一開始就是陡上401階)<br><img src="https://1013399.github.io/image-5/302/155959708_l.jpg" alt="劍潭古道入口" loading="lazy"></p><p>→古道入口旁路線圖<br><img src="https://1013399.github.io/image-5/302/155959711_l.jpg" alt="劍潭古道入口路線圖" loading="lazy"></p><p>→歇腳亭<br><img src="https://1013399.github.io/image-5/302/155959716_l.jpg" alt="劍潭古道 歇腳亭" loading="lazy"></p><p>→歇腳亭內的牌匾<br><img src="https://1013399.github.io/image-5/302/155959743_l.jpg" alt="劍潭古道 歇腳亭內的牌匾" loading="lazy"></p><p>→歇腳亭旁省府地政處測量局四等控制點<br><img src="https://1013399.github.io/image-5/302/155959766_l.jpg" alt="劍潭古道 省府地政處測量局四等控制點" loading="lazy"></p><p>→歇腳亭前展望佳<br><img src="https://1013399.github.io/image-5/302/155959771_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→歇腳亭旁視野對照圖<br><img src="https://1013399.github.io/image-5/302/155959793_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→沿途蝴蝶數量多，種類也多<br><img src="https://1013399.github.io/image-5/302/155959818_l.jpg" alt="劍潭古道 蝴蝶" loading="lazy"></p><p>→觀景台，有360°的視野<br><img src="https://1013399.github.io/image-5/302/155959827_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→叉路，殊途同歸，取左上小山頭<br><img src="https://1013399.github.io/image-5/302/155959868_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→叉路，左下往大龍村，取右續行<br><img src="https://1013399.github.io/image-5/302/155959891_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→斜陽亭，視野也不錯<br><img src="https://1013399.github.io/image-5/302/155959904_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→觀景平台(古道最高點)<br><img src="https://1013399.github.io/image-5/302/155959910_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→8字形環狀的道路，不知是否為賽車道<br><img src="https://1013399.github.io/image-5/302/155959947_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→苗栗縣政府地政局四等控制點<br><img src="https://1013399.github.io/image-5/302/155959990_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→山坡被覆蓋了一層混凝土<br><img src="https://1013399.github.io/image-5/302/155960006_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→劍潭古道出口(踢800公尺馬路回劍潭古道入口)<br><img src="https://1013399.github.io/image-5/302/155959699_l.jpg" alt="劍潭古道" loading="lazy"></p><p>→回到劍潭古道入口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1小時30分鐘(含休息)</p><p>高低落差：約100公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',25),m={href:"http://blog.xuite.net/yang5757/blog/32394524",target:"_blank",rel:"noopener noreferrer"},_=a("br",null,null,-1),b=a("img",{src:"https://1013399.github.io/image-5/302/155960187_l.jpg",alt:"劍潭古道 參考地圖",loading:"lazy"},null,-1);function u(y,j){const i=e("ExternalLinkIcon");return p(),g("div",null,[h,c,o(" more "),d,a("p",null,[t("原圖連結→"),a("a",m,[t("重新站起&一步一腳印"),r(i)]),_,b])])}const x=l(n,[["render",u],["__file","post-302-2010-06-09.html.vue"]]);export{x as default};
