import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o,c as r,a as s,b as i,f as t,d as l,e}from"./app-6513a715.js";const h={},n=i("p",null,[i("strong",null,"鳳凰山步道"),t("，位於台中縣后里鄉仁里村鳳凰山農場後方，原名仁里村休閒步道，而仁里村原名叫「貓仔坑」，步道設有木棧橋、彩色壓花步道、紅磚步道、枕木步道、景石拼花步道等、PC混凝土步道等，全長約1600公尺，這裡是一農村綜合發展規劃，沿途景觀種植有喬木、灌木等，約有200種台灣的原生植物。")],-1),m=i("p",null,[i("strong",null,"觀音山步道"),t("，位於后里火車站東側之丘陵地帶，沿途清爽幽靜、視野寬廣，步道最高點之觀音山視野極佳，可與鳳凰山步道串聯同遊，步道區穿越柑桔、高接梨、柿子、甜桃等果園，為登山健行、休閒採果的最佳去處。")],-1),b=i("p",null,[i("strong",null,"觀音山"),t("，海拔496公尺，位於台中縣后里鄉仁里村，為后里鄉最高峰，無基石，山頂設有涼亭(貓仔亭)及休閒廣場(小吃店)，展望極佳，可遠眺前方大貢山、火炎山，及北方的關刀山與大安溪。")],-1),c=i("hr",null,null,-1),d={href:"http://tw.myblog.yahoo.com/sporting-softball/article?mid=7852",target:"_blank",rel:"noopener noreferrer"},_=e('<p>走走日期： 2010/06/05</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高南下出三義交流道(少過一個收費站省40元)，走尖豐公路(台13線)往后里方向，上義里大橋至后里鄉後為三豐路，遇安眉路左轉至福美路右轉，接福興路、永興路至圳寮路左轉，於圳寮路47巷右轉往鳳凰山農場，至朝崑宮叉路取右過鳳凰山農場牌樓，於鳳凰山農場前停車場停車(有三處停車場)。</p><p>朝崑宮叉路(取右過牌樓)<br><img src="https://1013399.github.io/image-5/303/155796357_l.jpg" alt="朝崑宮叉路" loading="lazy"></p><p>叉路旁水井(好像是假井)<br><img src="https://1013399.github.io/image-5/303/155796367_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山 行車路線" loading="lazy"></p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→鳳凰山農場停車場(由上往下拍攝)<br><img src="https://1013399.github.io/image-5/303/155796456_l.jpg" alt="鳳凰山農場停車場" loading="lazy"></p><p>→鳳凰山登山步道口，試走一段看看<br> (此步道通往剛才的鳳凰山農場牌樓三叉路，不是往鳳凰山)<br><img src="https://1013399.github.io/image-5/303/155796480_l.jpg" alt="鳳凰山登山步道口" loading="lazy"></p><p>→木棧橋有些傾斜<br><img src="https://1013399.github.io/image-5/303/155796546_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→柿子已經結果<br><img src="https://1013399.github.io/image-5/303/155796574_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→路徑至此應往右，但已被雜草淹沒，原路折返<br><img src="https://1013399.github.io/image-5/303/155796586_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→路旁夏蟬鳴聲大作<br><img src="https://1013399.github.io/image-5/303/155796604_l.jpg" alt="夏蟬 鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→鳳凰山農場門口<br><img src="https://1013399.github.io/image-5/303/155796469_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→鳳凰山步道登山口<br><img src="https://1013399.github.io/image-5/303/155796690_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→登山口旁也可停數輛車<br><img src="https://1013399.github.io/image-5/303/155796618_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→登山口旁土地公<br><img src="https://1013399.github.io/image-5/303/155796701_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→一開始是長長的枕木步道<br><img src="https://1013399.github.io/image-5/303/155796726_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→觀景台(往回拍攝，還拍到兩粒柚子)<br><img src="https://1013399.github.io/image-5/303/155796783_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→雅亭<br><img src="https://1013399.github.io/image-5/303/155796794_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→雅亭旁步道導覽圖<br><img src="https://1013399.github.io/image-5/303/155796859_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→路徑轉為鋪石步道<br><img src="https://1013399.github.io/image-5/303/155796873_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→慧亭<br><img src="https://1013399.github.io/image-5/303/155796924_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→路徑轉為拼花步道<br><img src="https://1013399.github.io/image-5/303/155796939_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→竹林隧道<br><img src="https://1013399.github.io/image-5/303/155796951_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→鳳凰亭<br><img src="https://1013399.github.io/image-5/303/155796962_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→叉路(左往朝崑宮，取右往觀音山)<br><img src="https://1013399.github.io/image-5/303/155796998_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→觀景平台(遠眺火炎山、鐵砧山)<br><img src="https://1013399.github.io/image-5/303/155797007_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→鞍部十字叉路(左往圳寮路33巷，續直行)<br><img src="https://1013399.github.io/image-5/303/155797033_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→叉路(右往牛稠坑步道，續直行)<br><img src="https://1013399.github.io/image-5/303/155797037_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→改走木棧道<br><img src="https://1013399.github.io/image-5/303/155797044_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→沒了屋頂的涼亭<br><img src="https://1013399.github.io/image-5/303/155797071_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→自此開始陡上(落差約80M)<br><img src="https://1013399.github.io/image-5/303/155797119_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→坡度趨緩(落葉鋪地還滿美的)<br><img src="https://1013399.github.io/image-5/303/155797123_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→最後一段陡坡<br><img src="https://1013399.github.io/image-5/303/155797138_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→觀音山(貓仔亭)<br><img src="https://1013399.github.io/image-5/303/155797149_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→觀音山好視野<br><img src="https://1013399.github.io/image-5/303/155797153_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→觀音山小廣場(往前續行，開始下山)<br><img src="https://1013399.github.io/image-5/303/155797157_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→觀景台<br><img src="https://1013399.github.io/image-5/303/155797159_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→接產業道路叉路(取左往圳寮路)<br><img src="https://1013399.github.io/image-5/303/155797164_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→叉路(仍取左)<br><img src="https://1013399.github.io/image-5/303/155797168_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→叉路(右往觀音山停車場，續直行)<br><img src="https://1013399.github.io/image-5/303/155797175_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→滿山的高接梨<br><img src="https://1013399.github.io/image-5/303/155797196_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→觀音堂<br><img src="https://1013399.github.io/image-5/303/155797200_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→圳寮路33巷叉路(右出圳寮路，取左回鳳凰山步道)<br><img src="https://1013399.github.io/image-5/303/155797214_l.jpg" alt="圳寮路33巷叉路" loading="lazy"></p><p>→叉路旁的筆柿<br><img src="https://1013399.github.io/image-5/303/155797217_l.jpg" alt="筆柿" loading="lazy"></p><p>→回到鞍部十字叉路(取右回)<br> →回到鳳凰亭叉路(取右往朝崑宮)</p><p>→鳳凰亭附近發現一隻長約10cm的毛毛蟲<br><img src="https://1013399.github.io/image-5/303/155797220_l.jpg" alt="毛毛蟲 鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→路旁涼亭<br><img src="https://1013399.github.io/image-5/303/155797236_l.jpg" alt="鳳凰山步道、觀音山步道、觀音山" loading="lazy"></p><p>→朝崑宮<br><img src="https://1013399.github.io/image-5/303/155797239_l.jpg" alt="朝崑宮" loading="lazy"></p><p>→鳳凰山步道出口旁「老君騎貔貅」<br><img src="https://1013399.github.io/image-5/303/155796350_l.jpg" alt="鳳凰山步道出口旁「老君騎貔貅」" loading="lazy"></p><p>→回到鳳凰山農場牌樓三叉路(取左回停車場)<br> →回到鳳凰山農場停車場</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時(含休息)</p><p>高低落差：約200公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',54),u=i("br",null,null,-1),y={href:"http://emmm.tw/L3_content.php?L3_id=945",target:"_blank",rel:"noopener noreferrer"},j=i("br",null,null,-1),z=i("img",{src:"https://1013399.github.io/image-5/303/155810563_l.jpg",alt:"鳳凰山步道、觀音山步道、觀音山 參考地圖",loading:"lazy"},null,-1);function f(x,k){const a=p("ExternalLinkIcon");return o(),r("div",null,[n,s(" more "),m,b,c,i("p",null,[t("連續下了好久的雨，今日一早仍是綿綿細雨，相信山區路徑一定濕滑泥濘，不宜從事登山活動，在旅聯網看到了「山好雲芬芳」的"),i("a",d,[t("台中鳳凰山步道"),l(a)]),t("一文，得知該步道全程無泥土路，輕鬆好走，而且台中的天氣應該會比較好吧，於是決定前往活動一下筋骨，果然今日不算炎熱、清風徐徐，非常適合上山走走，走來輕鬆愉快，回程順道去梧棲漁港買些漁貨，結束一天美好的行程。")]),_,i("p",null,[t("(紫色為走走路線)"),u,t(" 原圖連結→"),i("a",y,[t("美美美旅遊聯盟"),l(a)]),j,z])])}const V=g(h,[["render",f],["__file","post-303-2010-06-07.html.vue"]]);export{V as default};
