import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as p,c as l,a as h,b as a,f as i,d as g,e as n}from"./app-7f62a6ff.js";const s={},o=a("p",null,[a("strong",null,"大分林山"),i("，海拔334公尺，位於新竹縣北埔鄉，三頂有一等三角點、三等三角點九一號兩顆基石，另有一等衛星控制點標示牌，為輕鬆易行的大眾化健行路線之一。")],-1),d=n('<p><strong>小分林山</strong>，海拔235公尺，位於新竹縣北埔鄉，三頂有建設廳礦務課六百一號基石，位於竹林中路旁。</p><p><strong>二寮山</strong>，又名二寮岡頂山，海拔234公尺，位於新竹縣北埔鄉，山頂有土地調查局圖根點基石，樹林間無展望。</p><p><strong>二寮神木</strong>，位於新竹縣北埔鄉大林村，於西元一八三五年大隘開墾之際即已成巨木，神木樹齡約五百以上，現今為本省平地最大的樟樹，曾經因為遭受病蟲害侵襲及榕樹寄生纏繞而差點無法存活，經北埔鄉公所延請樹醫楊甘陵先生醫治才得以繼續生存。</p><p>走走日期： 2010/05/22</p><h2 id="往大分林山" tabindex="-1"><a class="header-anchor" href="#往大分林山" aria-hidden="true">#</a> 往大分林山</h2><h3 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h3><p>竹東行駛台3線往北埔，至北埔續行外環道往峨眉，遇T字路口左轉往北埔國小(右往峨眉)，過北埔國小見中華電信右轉竹37鄉道往北埔冷泉，過大林橋後不遠遇老頭擺客家菜餐廳右上餐廳停車場，與餐廳老闆打聲招呼後停車。</p><h3 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h3><p>→老頭擺餐廳停車場<br><img src="https://1013399.github.io/image-5/304/154577032_l.jpg" alt="老頭擺餐廳停車場" loading="lazy"></p><p>→老頭擺餐廳廁所<br><img src="https://1013399.github.io/image-5/304/154577068_l.jpg" alt="老頭擺餐廳廁所" loading="lazy"></p><p>→廁所旁大分林步道圖<br><img src="https://1013399.github.io/image-5/304/154577075_l.jpg" alt="大分林步道圖" loading="lazy"></p><p>→寬大的石階步道一路往上<br><img src="https://1013399.github.io/image-5/304/154577086_l.jpg" alt="大分林山" loading="lazy"></p><p>→產道叉路，左側有條產道，續行石階步道開始陡上<br><img src="https://1013399.github.io/image-5/304/154577096_l.jpg" alt="大分林山" loading="lazy"></p><p>→遠眺鵝公髻山<br><img src="https://1013399.github.io/image-5/304/154577129_l.jpg" alt="鵝公髻山" loading="lazy"></p><p>→遠眺五指山<br><img src="https://1013399.github.io/image-5/304/154577172_l.jpg" alt="五指山" loading="lazy"></p><p>→休憩涼亭(快到山頂了)<br><img src="https://1013399.github.io/image-5/304/154577190_l.jpg" alt="大分林山" loading="lazy"></p><p>→大分林山<br><img src="https://1013399.github.io/image-5/304/154577204_l.jpg" alt="大分林山" loading="lazy"></p><p>→大分林山一等三角點<br><img src="https://1013399.github.io/image-5/304/154577226_l.jpg" alt="大分林山" loading="lazy"></p><p>→大分林山三等三角點九一號<br><img src="https://1013399.github.io/image-5/304/154577325_l.jpg" alt="大分林山" loading="lazy"></p><p>→續行有枕木步道可能往番婆坑，原路折返<br><img src="https://1013399.github.io/image-5/304/154577399_l.jpg" alt="大分林山" loading="lazy"></p><p>→回到老頭擺餐廳</p><h3 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間(來回)：約40分鐘</p><p>高低落差：約160公尺</p></div><h2 id="往小分林山" tabindex="-1"><a class="header-anchor" href="#往小分林山" aria-hidden="true">#</a> 往小分林山</h2><h3 id="行車路線-1" tabindex="-1"><a class="header-anchor" href="#行車路線-1" aria-hidden="true">#</a> 行車路線</h3><p>自老頭擺餐廳行駛竹37鄉道回往北埔方向，於小分林叉路右轉產道往二寮神木(路口有威爾斯咖啡及神木指標)，不久接竹39鄉道，過神木產道叉路續行，見二寮高幹50電線桿後於路旁較寬處停車，登山口就在電線桿對面。</p><h3 id="走走路線-1" tabindex="-1"><a class="header-anchor" href="#走走路線-1" aria-hidden="true">#</a> 走走路線</h3><p>→二寮高幹50電線桿<br><img src="https://1013399.github.io/image-5/304/154577425_l.jpg" alt="小分林山 二寮高幹50電線桿" loading="lazy"></p><p>→小分林山登山口<br><img src="https://1013399.github.io/image-5/304/154577437_l.jpg" alt="小分林山登山口" loading="lazy"></p><p>→上稜線後取左進入竹林<br><img src="https://1013399.github.io/image-5/304/154577476_l.jpg" alt="小分林山" loading="lazy"></p><p>→小分林山礦務課六百一號基石，原路折返<br><img src="https://1013399.github.io/image-5/304/154577498_l.jpg" alt="小分林山" loading="lazy"></p><p>→回到登山口</p><h3 id="走走資訊-1" tabindex="-1"><a class="header-anchor" href="#走走資訊-1" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間(來回)：約10分鐘</p><p>高低落差：約20公尺</p></div><h2 id="往二寮山" tabindex="-1"><a class="header-anchor" href="#往二寮山" aria-hidden="true">#</a> 往二寮山</h2><h3 id="行車路線-2" tabindex="-1"><a class="header-anchor" href="#行車路線-2" aria-hidden="true">#</a> 行車路線</h3><p>調頭行駛竹39鄉道回往北埔方向，於神木產道取右不到10公尺，見二寮高幹36電線桿後於路旁較寬處停車。</p><h3 id="走走路線-2" tabindex="-1"><a class="header-anchor" href="#走走路線-2" aria-hidden="true">#</a> 走走路線</h3><p>→二寮山登山口，寬大的泥土產道<br><img src="https://1013399.github.io/image-5/304/154577582_l.jpg" alt="二寮山登山口" loading="lazy"></p><p>→叉路，續行泥土產道<br><img src="https://1013399.github.io/image-5/304/154577619_l.jpg" alt="二寮山" loading="lazy"></p><p>→產道終點，續直行<br><img src="https://1013399.github.io/image-5/304/154577627_l.jpg" alt="二寮山" loading="lazy"></p><p>→叉路，取左上往電塔<br><img src="https://1013399.github.io/image-5/304/154577660_l.jpg" alt="二寮山" loading="lazy"></p><p>→電塔#056<br><img src="https://1013399.github.io/image-5/304/154577702_l.jpg" alt="二寮山" loading="lazy"></p><p>→電塔左側稜線山路已被雜草淹沒<br><img src="https://1013399.github.io/image-5/304/154577723_l.jpg" alt="二寮山" loading="lazy"></p><p>→從旁邊缺口攀爬邊坡上稜線，無路徑、無踏腳處，有點危險<br><img src="https://1013399.github.io/image-5/304/154577769_l.jpg" alt="二寮山" loading="lazy"></p><p>→二寮山<br><img src="https://1013399.github.io/image-5/304/154577794_l.jpg" alt="二寮山" loading="lazy"></p><p>→二寮山土地調查局圖根點，原路折返<br><img src="https://1013399.github.io/image-5/304/154577823_l.jpg" alt="二寮山" loading="lazy"></p><p>→邊坡下切危險，強行穿越雜草路徑回電塔<br> →回到登山口</p><h3 id="走走資訊-2" tabindex="-1"><a class="header-anchor" href="#走走資訊-2" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間(來回)：約30分鐘</p><p>高低落差：約60公尺</p></div><h2 id="往二寮神木" tabindex="-1"><a class="header-anchor" href="#往二寮神木" aria-hidden="true">#</a> 往二寮神木</h2><h3 id="行車路線-3" tabindex="-1"><a class="header-anchor" href="#行車路線-3" aria-hidden="true">#</a> 行車路線</h3><p>續行駛產道便可到達二寮神木，於路旁停車格停車。</p><h3 id="走走路線-3" tabindex="-1"><a class="header-anchor" href="#走走路線-3" aria-hidden="true">#</a> 走走路線</h3><p>→二寮神木<br><img src="https://1013399.github.io/image-5/304/154577835_l.jpg" alt="二寮神木" loading="lazy"></p><p>→二寮古步道導覽圖<br><img src="https://1013399.github.io/image-5/304/154577841_l.jpg" alt="二寮古步道導覽圖" loading="lazy"></p><p>→二寮神木<br><img src="https://1013399.github.io/image-5/304/154577846_l.jpg" alt="二寮神木" loading="lazy"></p><p>→搶救二寮神木醫治過程<br><img src="https://1013399.github.io/image-5/304/154577848_l.jpg" alt="二寮神木" loading="lazy"></p><p>→二寮神木後方土地公廟<br><img src="https://1013399.github.io/image-5/304/154577857_l.jpg" alt="二寮神木" loading="lazy"></p><p>→二寮木棧步道<br><img src="https://1013399.github.io/image-5/304/154577860_l.jpg" alt="二寮木棧步道" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',61),c={href:"http://www.yougoipay.com/kenny/w925/index.htm",target:"_blank",rel:"noopener noreferrer"},b=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-5/304/154577963_l.jpg",alt:"大分林山、小分林山、二寮山、二寮神木 參考地圖",loading:"lazy"},null,-1);function u(_,y){const t=r("ExternalLinkIcon");return p(),l("div",null,[o,h(" more "),d,a("p",null,[i("原圖連結→"),a("a",c,[i("蕭郎：獨步山林間"),g(t)]),b,m])])}const f=e(s,[["render",u],["__file","post-304-2010-05-24.html.vue"]]);export{f as default};
