import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as r,a as e,b as a,e as i,d as h,f as n}from"./app-9ec59630.js";const o={},s=a("p",null,[a("strong",null,"金獅古道"),i("，因緊臨金獅寺而聞名，位於新竹湖口鄉湖口村6鄰。昔日是新埔到湖口農產品運輸之重要古道，原為羊腸小徑，經水保局第一工程所於86年6月規劃成為一排排大圓石砌成的步道。此一步道特點為沿途除了夾道的原始林及竹林外，該地尚保留豐富的生態環境，如螢火蟲棲息地等可供各界研究之用。")],-1),d=n('<p><strong>仁和步道</strong>，入口為寬敞的水泥道轉為石塊步道，沿途遺留有許多過去裝甲兵演練用的壕溝及碉堡，沿途綠意盎然。</p><p><strong>糞箕湖山</strong>，海拔250公尺，高速#216基石，基石位於農地邊緣相思樹林內的廢棄碉堡上。另有土地調查局圖根點，據說已經遺失，芒草與樹林交界附近有廢棄小蓄水池，林間無展望。</p><p><strong>漢卿步道</strong>，為紀念湖口鄉著名的書畫家葉漢卿先生而命名，其所繪之畫風格與其為人個性相當，豪爽不拘而瀟灑流暢，步道的風格也同是深隧幽遠，出入瀟灑。</p><p>走走日期： 2010/05/01</p><h2 id="往金獅古道、仁和步道" tabindex="-1"><a class="header-anchor" href="#往金獅古道、仁和步道" aria-hidden="true">#</a> 往金獅古道、仁和步道</h2><h3 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h3><p>中山高下湖口交流道往新竹工業區方向行駛，遇T叉路(光復路)左轉，遇第一個紅綠燈右轉永順街(旁有萊爾富便利商店)，接117縣道後左轉，直行接台1線往北，於台1線58K處紅綠燈(旁有7-11便利商店)右轉竹13-1鄉道往金獅寺，過中山高涵洞後續駛，途經漢卿步道入口，至竹13-1鄉道3K附近金獅寺公車站牌，取左產道往金獅寺，將車停於金獅寺停車場，金獅寺位於停車場右側，停車場左側有金獅寺眺望台，眺望台旁有兩條步道，左側為金獅古道入口(鵝卵石步道)，右側為金獅寺步道(需穿越眺望台，為花崗石步道)。</p><h3 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h3><p>→金獅寺停車場<br><img src="https://1013399.github.io/image-5/306/152671927_l.jpg" alt="金獅寺停車場" loading="lazy"></p><p>→金獅寺<br><img src="https://1013399.github.io/image-5/306/152671933_l.jpg" alt="金獅寺" loading="lazy"></p><p>→取左進入金獅古道(右前方為金獅寺眺望台)<br><img src="https://1013399.github.io/image-5/306/152671952_l.jpg" alt="金獅古道" loading="lazy"></p><p>→竹林夾道的大圓石步道<br><img src="https://1013399.github.io/image-5/306/152671961_l.jpg" alt="金獅古道" loading="lazy"></p><p>→逸鶴山莊(直走仍為金獅古道往湖口老街，為無趣產道，取右往拱橋)<br><img src="https://1013399.github.io/image-5/306/152671992_l.jpg" alt="逸鶴山莊 金獅古道" loading="lazy"></p><p>→拱橋叉路(右為金獅寺步道，直走林間山徑往仁和步道)<br><img src="https://1013399.github.io/image-5/306/152672023_l.jpg" alt="金獅古道" loading="lazy"></p><p>→飄落滿地的油桐花<br><img src="https://1013399.github.io/image-5/306/152672096_l.jpg" alt="油桐花 金獅古道" loading="lazy"></p><p>→土地伯公叉路(右上方有開山土地伯公，續直行)<br><img src="https://1013399.github.io/image-5/306/152672146_l.jpg" alt="金獅古道" loading="lazy"></p><p>→叉路(左往湖口老街，取右往仁和步道)<br><img src="https://1013399.github.io/image-5/306/152672204_l.jpg" alt="金獅古道" loading="lazy"></p><p>→可愛的賞桐指標<br><img src="https://1013399.github.io/image-5/306/152672369_l.jpg" alt="金獅古道" loading="lazy"></p><p>→姑娘廟<br><img src="https://1013399.github.io/image-5/306/152672443_l.jpg" alt="姑娘廟 金獅古道" loading="lazy"></p><p>→民宅左轉往仁和步道<br><img src="https://1013399.github.io/image-5/306/152672507_l.jpg" alt="金獅古道" loading="lazy"></p><p>→仁和步道入口(左側臨近中山高與台灣高鐵，取右仁和步道)<br><img src="https://1013399.github.io/image-5/306/152672518_l.jpg" alt="仁和步道入口" loading="lazy"></p><p>→仁和步道口賞桐導覽圖<br><img src="https://1013399.github.io/image-5/306/152672535_l.jpg" alt="仁和步道口賞桐導覽圖" loading="lazy"></p><p>→仁和步道石階陡上<br><img src="https://1013399.github.io/image-5/306/152672555_l.jpg" alt="仁和步道" loading="lazy"></p><p>→石階旁波羅蜜樹<br><img src="https://1013399.github.io/image-5/306/152672572_l.jpg" alt="仁和步道" loading="lazy"></p><p>→石桌椅休息區(陡坡結束)<br><img src="https://1013399.github.io/image-5/306/152672587_l.jpg" alt="仁和步道" loading="lazy"></p><p>→叉路(右雜草小徑不知去向，取左水泥路)<br><img src="https://1013399.github.io/image-5/306/152672598_l.jpg" alt="仁和步道" loading="lazy"></p><p>→金獅寺車道(左往竹13-1鄉道，取右回金獅寺)<br><img src="https://1013399.github.io/image-5/306/152672657_l.jpg" alt="金獅寺車道" loading="lazy"></p><h3 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：O型一圈約1小時</p><p>高低落差：約100公尺</p></div><h2 id="往糞箕湖山" tabindex="-1"><a class="header-anchor" href="#往糞箕湖山" aria-hidden="true">#</a> 往糞箕湖山</h2><h3 id="行車路線-1" tabindex="-1"><a class="header-anchor" href="#行車路線-1" aria-hidden="true">#</a> 行車路線</h3><p>回到金獅寺停車場取車，接竹13-1鄉道後取左往新埔方向，見「箕山親子露營區」指示後路邊停車，旁有綠藻站公車站牌，取左產道往糞箕湖山，見到左側茶園後進入茶園，沿著駁坎行走不久，便可見登山路條，由此切入樹林。</p><p>→箕山親子露營區路旁停車<br><img src="https://1013399.github.io/image-5/306/152672673_l.jpg" alt="箕山親子露營區路旁停車" loading="lazy"></p><p>→小蓄水池<br><img src="https://1013399.github.io/image-5/306/152672715_l.jpg" alt="糞箕湖山" loading="lazy"></p><p>→廢棄碉堡(爬上碉堡)<br><img src="https://1013399.github.io/image-5/306/152672837_l.jpg" alt="糞箕湖山" loading="lazy"></p><p>→糞箕湖山高速#216基石<br><img src="https://1013399.github.io/image-5/306/152672853_l.jpg" alt="糞箕湖山" loading="lazy"></p><p>→續行，走出樹林接回產道(往回拍攝)<br><img src="https://1013399.github.io/image-5/306/152672869_l.jpg" alt="糞箕湖山" loading="lazy"></p><h3 id="走走資訊-1" tabindex="-1"><a class="header-anchor" href="#走走資訊-1" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：來回約10分鐘</p><p>高低落差：約2公尺</p></div><h2 id="往漢卿步道、湖口老街" tabindex="-1"><a class="header-anchor" href="#往漢卿步道、湖口老街" aria-hidden="true">#</a> 往漢卿步道、湖口老街</h2><h3 id="行車路線-2" tabindex="-1"><a class="header-anchor" href="#行車路線-2" aria-hidden="true">#</a> 行車路線</h3><p>回到竹13-1鄉道取車，調頭回往湖口，見「牛車驛站」後路邊停車。</p><h3 id="走走路線-1" tabindex="-1"><a class="header-anchor" href="#走走路線-1" aria-hidden="true">#</a> 走走路線</h3><p>→牛車驛站<br><img src="https://1013399.github.io/image-5/306/152673027_l.jpg" alt="牛車驛站" loading="lazy"></p><p>→驛站旁戰車<br><img src="https://1013399.github.io/image-5/306/152672935_l.jpg" alt="null" loading="lazy"></p><p>→桐花祭活動(國樂演奏)<br><img src="https://1013399.github.io/image-5/306/152672915_l.jpg" alt="null" loading="lazy"></p><p>→驛站旁裝甲車及海軍丹陽艦5&quot;38倍雙管艦砲(感謝yang兄指正)<br><img src="https://1013399.github.io/image-5/306/152672888_l.jpg" alt="null" loading="lazy"></p><p>→漢卿步道入口(牛車驛站旁)<br><img src="https://1013399.github.io/image-5/306/152673108_l.jpg" alt="null" loading="lazy"></p><p>→緩下石階步道<br><img src="https://1013399.github.io/image-5/306/152673121_l.jpg" alt="null" loading="lazy"></p><p>→涵洞叉路(右往金獅古道，取左過涵洞往湖口老街)<br><img src="https://1013399.github.io/image-5/306/152673150_l.jpg" alt="null" loading="lazy"></p><p>→桐花祭活動(客家山歌演唱)<br><img src="https://1013399.github.io/image-5/306/152673156_l.jpg" alt="null" loading="lazy"></p><p>→桐花祭活動攤位及停車場<br><img src="https://1013399.github.io/image-5/306/152673166_l.jpg" alt="null" loading="lazy"></p><p>→三元宮<br><img src="https://1013399.github.io/image-5/306/152673176_l.jpg" alt="null" loading="lazy"></p><p>→湖口老街(逛逛後原路折返)<br><img src="https://1013399.github.io/image-5/306/152673182_l.jpg" alt="null" loading="lazy"></p><p>→回到牛車驛站(換一個可愛美眉正在演唱英文老歌「It&#39;s so easy」)<br><img src="https://1013399.github.io/image-5/306/152671770_l.jpg" alt="null" loading="lazy"></p><h3 id="走走資訊-2" tabindex="-1"><a class="header-anchor" href="#走走資訊-2" aria-hidden="true">#</a> 走走資訊</h3><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：單程約10分鐘</p><p>高低落差：約60公尺</p></div><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',58),c={href:"http://www.yougoipay.com/kenny/w691/index.htm",target:"_blank",rel:"noopener noreferrer"},m=a("figure",null,[a("img",{src:"https://1013399.github.io/image-5/306/152679200_l.jpg",alt:"金獅古道、仁和步道、糞箕湖山、漢卿步道、湖口老街 參考地圖",tabindex:"0",loading:"lazy"}),a("figcaption",null,"金獅古道、仁和步道、糞箕湖山、漢卿步道、湖口老街 參考地圖")],-1);function b(u,_){const t=g("ExternalLinkIcon");return p(),r("div",null,[s,e(" more "),d,a("p",null,[i("原圖連結→"),a("a",c,[i("蕭郎：獨步山林間"),h(t)])]),m])}const z=l(o,[["render",b],["__file","post-306-2010-05-03.html.vue"]]);export{z as default};
