import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as r,c as e,a as o,b as i,e as a,d as s,f as t}from"./app-39f3f472.js";const h={},n=i("p",null,[i("strong",null,"面天山"),a("和"),i("strong",null,"向天山"),a("為一對雙生火山，位於台北市陽明山國家公園的西區，兩座山噴發形成時間大約相同，是由熔岩流從大屯山的旁邊流出所形成。")],-1),b=t('<p><strong>面天山</strong>，海拔977公尺，山頂有顆三等三角點#1100，另建有的二座導航用的反射板，為一座標準的鐘狀火山，曲線圓滑，山形極優美，山頂的火山口呈平頂狀，登頂後可觀賞270度的大視野景觀，從金山、三芝、淡水河出海口、觀音山、關渡、社子島至圓山，盡可一覽無遺。</p><p><strong>向天山</strong>，海拔949公尺，無基石，也具有鐘狀火山的外形，火山口在東南側，形狀相當完整，大雨之後常積水成火口湖，稱為「向天池」。</p><p><strong>向天池山</strong>，海拔881公尺，無基石，山頂有標示牌，樹林間無展望。有些地圖也稱此山為向天山，不過一般還是以大縱走拓牌木樁所在之山頭為向天山。</p><hr><p>有多條路線可登遊面天山、向天山、向天池：</p><p>巴拉卡公路的二子坪步道、淡水復興三路清天宮的三聖宮步道、淡水興福寮古道、淡水北新路天元宮的山子頂古道及陽明山東西縱走西半段。</p><p>走走日期： 2010/03/28</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高下重慶北路交流道往北，至中正路右轉過百齡橋左轉承德路五段往淡水，於承德路七段接大度路及大業路三叉路右轉大業路往北投，遇大同街左轉，直走過中和街後為復興四路，經復興高中後循復興三路至清天宮站路旁停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→清天宮(循右側車道上登)<br> →登山口(石階步道，有指標)<br> →清水宮<br> →叉路(左下往清天宮，續上石階步道)<br> →叉路(左可往興福寮，路口有人在賣菜)<br> →三聖宮<br> →叉路(右往太上老君道祖宮，續上行)<br> →向天池叉路(左上往向天池，取直往二子坪)<br> →面天坪(有座涼亭，涼亭旁有路往大屯西峰，往二子坪方向續行)<br> →叉路(直行寬土徑往二子坪，取左石階往面天山)<br> →面天山(有足印草山拓牌之「自」字及三等三角點基石，登上觀景平台，視野極佳，賞景完轉往向天池續行)<br> →向天山(有足印草山拓牌之「然」字)<br> →叉路(左往清天宮，取右環池步道)<br> →山子頂叉路(續行為環池步道，取右上山路)<br> →叉路(直行往山子頂古道，取左往向天池山)<br> →向天池山(林中無視野，原路折返)<br> →回到山子頂叉路(不走環池步道，回往清天宮)<br> →叉路(右環池步道可往興福寮，先取右往向天池一探)<br> →向天池(無水，折返回清天宮)<br> →叉路(左往二子坪，另有小路往樹林裡的涼亭，取右回清天宮)<br> →太子碑(裕仁皇太子殿下行啟紀念碑)<br> →回到向天池叉路<br> →回到三聖宮<br> →回到清天宮(驅車前往無極天元宮一遊)</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約4小時20分鐘</p><p>高低落差：約540公尺(清天宮至面天坪落差約330公尺；面天坪至面天山落差約210公尺)</p></div><div class="hint-container note"><p class="hint-container-title">後記</p><p>淡水無極天元宮，是近年來的熱門賞櫻景點，網路上得知，今年3月12日是吉野櫻最為盛開的時候，相信今日來此為時已晚，不過又想想自己從未來過，便順道到此一遊，看看還有沒吉野櫻的尾巴可賞，果然櫻花樹已全然換上嫩綠新妝，僅剩幾朵點綴其中，只好明年趁早了！</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>清天宮<br><img src="https://1013399.github.io/image-5/312/149461400_l.jpg" alt="清天宮" loading="lazy"></p><p>登山口<br><img src="https://1013399.github.io/image-5/312/149461445_l.jpg" alt="面天山登山口" loading="lazy"></p><p>清水宮<br><img src="https://1013399.github.io/image-5/312/149461490_l.jpg" alt="清水宮" loading="lazy"></p><p>陡上石階<br><img src="https://1013399.github.io/image-5/312/149461495_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>叉路(左下往清天宮，續上石階步道)<br><img src="https://1013399.github.io/image-5/312/149461557_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>叉路(左可往興福寮，路口有人在賣菜)<br><img src="https://1013399.github.io/image-5/312/149461563_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>三聖宮<br><img src="https://1013399.github.io/image-5/312/149461633_l.jpg" alt="三聖宮" loading="lazy"></p><p>台灣省政府水源涵養保安林石柱<br><img src="https://1013399.github.io/image-5/312/149461643_l.jpg" alt="台灣省政府水源涵養保安林石柱" loading="lazy"></p><p>叉路(右往太上老君道祖宮，續上行)<br><img src="https://1013399.github.io/image-5/312/149461779_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>向天池叉路(左上往向天池，取直往二子坪)<br><img src="https://1013399.github.io/image-5/312/149461857_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>面天坪<br><img src="https://1013399.github.io/image-5/312/149461869_l.jpg" alt="面天坪" loading="lazy"></p><p>面天坪旁之陽明山西區步道時程簡圖<br><img src="https://1013399.github.io/image-5/312/149461879_l.jpg" alt="面天坪旁之陽明山西區步道時程簡圖" loading="lazy"></p><p>面天坪涼亭旁叉路可往大屯西峰<br><img src="https://1013399.github.io/image-5/312/149461888_l.jpg" alt="面天山" loading="lazy"></p><p>路旁不知名蘭花<br><img src="https://1013399.github.io/image-5/312/149461895_l.jpg" alt="蘭花" loading="lazy"></p><p>往二子坪山路寬大平緩<br><img src="https://1013399.github.io/image-5/312/149461908_l.jpg" alt="面天山 往二子坪山路寬大平緩" loading="lazy"></p><p>叉路(直行往二子坪，取左石階往面天山)<br><img src="https://1013399.github.io/image-5/312/149461919_l.jpg" alt="面天山" loading="lazy"></p><p>面天山反射板與觀景平台<br><img src="https://1013399.github.io/image-5/312/149461978_l.jpg" alt="面天山反射板與觀景平台" loading="lazy"></p><p>面天山三等三角點<br><img src="https://1013399.github.io/image-5/312/149461990_l.jpg" alt="面天山三等三角點" loading="lazy"></p><p>面天山觀景平台展望<br><img src="https://1013399.github.io/image-5/312/149462000_l.jpg" alt="面天山觀景平台展望" loading="lazy"></p><p>火紅的金毛杜鵑<br><img src="https://1013399.github.io/image-5/312/149462008_l.jpg" alt="火紅的金毛杜鵑" loading="lazy"></p><p>向天山足印草山拓牌<br><img src="https://1013399.github.io/image-5/312/149462077_l.jpg" alt="向天山足印草山拓牌" loading="lazy"></p><p>叉路(左往清天宮，先取右環池步道)<br><img src="https://1013399.github.io/image-5/312/149462164_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>山子頂叉路(續行為環池步道，取右上山路往山子頂方向)<br><img src="https://1013399.github.io/image-5/312/149462173_l.jpg" alt="山子頂叉路" loading="lazy"></p><p>叉路(直行往山子頂古道，取左往向天池山)<br><img src="https://1013399.github.io/image-5/312/149462182_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>向天池山(原路折返回清天宮)<br><img src="https://1013399.github.io/image-5/312/149462187_l.jpg" alt="向天池山" loading="lazy"></p><p>叉路(右環池步道可往興福寮，先取右往向天池)<br><img src="https://1013399.github.io/image-5/312/149462193_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>向天池(無水，折返回清天宮)<br><img src="https://1013399.github.io/image-5/312/149462202_l.jpg" alt="向天池 無水" loading="lazy"></p><p>叉路(左往二子坪，另有小路往樹林裡的涼亭，取右回清天宮)<br><img src="https://1013399.github.io/image-5/312/149462306_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>太子碑<br><img src="https://1013399.github.io/image-5/312/149462322_l.jpg" alt="太子碑" loading="lazy"></p><p>回到向天池叉路<br><img src="https://1013399.github.io/image-5/312/149462332_l.jpg" alt="面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><p>淡水無極天元宮牌樓<br><img src="https://1013399.github.io/image-5/312/149462341_l.jpg" alt="淡水無極天元宮牌樓" loading="lazy"></p><p>淡水無極天元宮<br><img src="https://1013399.github.io/image-5/312/149462349_l.jpg" alt="淡水無極天元宮" loading="lazy"></p><p>淡水無極天元宮<br><img src="https://1013399.github.io/image-5/312/149462407_l.jpg" alt="淡水無極天元宮" loading="lazy"></p><p>無極天元宮一樓內部<br><img src="https://1013399.github.io/image-5/312/149462469_l.jpg" alt="無極天元宮一樓內部" loading="lazy"></p><p>僅存幾朵的吉野櫻<br><img src="https://1013399.github.io/image-5/312/149462461_l.jpg" alt="吉野櫻 面天山、向天山、向天池山，順遊無極天元宮" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',51),d={href:"http://www.yougoipay.com/kenny/w574/index.htm",target:"_blank",rel:"noopener noreferrer"},c=t('<h3 id="面天山、向天山、向天池山" tabindex="-1"><a class="header-anchor" href="#面天山、向天山、向天池山" aria-hidden="true">#</a> 面天山、向天山、向天池山</h3><figure><img src="https://1013399.github.io/image-5/312/149465576_l.jpg" alt="面天山、向天山、向天池山 路線圖" tabindex="0" loading="lazy"><figcaption>面天山、向天山、向天池山 路線圖</figcaption></figure><h3 id="往無極天元宮" tabindex="-1"><a class="header-anchor" href="#往無極天元宮" aria-hidden="true">#</a> 往無極天元宮</h3><figure><img src="https://1013399.github.io/image-5/312/149465574_l.jpg" alt="往無極天元宮 路線圖" tabindex="0" loading="lazy"><figcaption>往無極天元宮 路線圖</figcaption></figure>',4);function m(_,u){const g=l("ExternalLinkIcon");return r(),e("div",null,[n,o(" more "),b,i("p",null,[a("原圖連結→"),i("a",d,[a("蕭郎：獨步山林間"),s(g)])]),c])}const z=p(h,[["render",m],["__file","post-312-2010-03-31.html.vue"]]);export{z as default};
