import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as g,o as p,c as r,a as e,b as i,e as a,d as o,f as s}from"./app-4a074c75.js";const h={},n=i("p",null,[i("strong",null,"鳳崎落日觀景步道"),a("，位於新竹縣竹北市與新豐鄉交界，為連結「天德堂」至「蓮花寺」的登山步道，步道單程約3公里，竹北市自民國95年開始細部規劃，分四期工程打造獨特的步道系統，在維持相思樹自然林相的原貌下，鋪設花崗石、打造觀景涼亭、體健場及解說牌指標等，沿線視野遼闊、生態資源豐富，由於步道毗鄰軍用地，沿線還有坑道舊址。")],-1),b=s('<p>鳳崎落日觀景步道，沿線擁有豐富自然林相與生態資源，可眺望竹北新月沙灣、新竹市南寮漁港，甚至遠望香山全景，落日餘輝更與台灣海峽相互輝映，美不盛收，更以新竹八景之一「鳳崎晚霞」聞名。</p><p>竹北市公所於3月27日舉辦「鳳崎落日觀景步道落成啟用典禮暨市運健行活動」，上午9點在天德堂廣場集合，領取活動紀念毛巾、礦泉水以及集章卡，民眾走完全程並蓋滿集章卡，可在終點站蓮花寺前換取竹北台梗九號米(600公克包裝)，終點站並備有紅豆湯與風味美食(炒麵、魷魚羹、控肉及客家薺粑等)提供給民眾享用，主辦單位甚至貼心的準備接駁車供民眾返回起點。</p><p>走走日期： 2010/03/27</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>台1線從新竹北上，至明新科技大學左轉竹5線(康樂路)，行約1公里可見天德堂指標，左轉德日路直行即可到達天德堂前停車場，惟典禮會場交通管制，一般車輛不得進入，將車停於新竹貨運公司路旁。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→天德堂(先排隊領毛巾及礦泉水，後沿廟旁柏油路下行)<br> →登山口(領集章卡，人擠人)<br> →叉路(左應可接回步道，續行主線)<br> →觀景涼亭(視野佳，旁有鳳崎晚霞簡介)<br> →叉路(左山徑應可接回步道，今日均走主線)<br> →休憩平台(木板搭製，旁有涼亭，前有叉路)<br> →叉路(左木棧道可接回步道或回天德堂，取右水泥階梯)<br> →軍事碉堡<br> →戰備坑道<br> →涼亭(下方有卡拉OK餐廳及小販，旁有廁所，循泥土產道續行)<br> →叉路(主辦單位設有補水站、第二處蓋章處，左上階梯可回天德堂，續行泥土產道)<br> →叉路(續行往靜善宮，取右往蓮花寺)<br> →第二休憩平台(新唐人亞太電視台記者正在訪問遊客)<br> →涼亭<br> →步道主線終點(旁有主線3km指示，續行有涼亭，取右往蓮花寺)<br> →蓮花寺廣場(先排隊領紀念品，然後品嘗美食，再去排隊搭接駁車)<br> →回到天德堂</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約70分鐘(單程)</p><p>高低落差：約80公尺(步道最高點海拔約150公尺)</p></div><p>P.S.當日未帶數位相機，以下行旅照片是以手機拍攝(只有30萬畫素)，畫質較差！</p><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>天德堂(排隊領毛巾及礦泉水)<br><img src="https://1013399.github.io/image-5/313/149459442_l.jpg" alt="天德堂 排隊領毛巾及礦泉水 鳳崎落日觀景步道" loading="lazy"></p><p>登山口(領集章卡，人擠人)<br><img src="https://1013399.github.io/image-5/313/149459444_l.jpg" alt="鳳崎落日觀景步道登山口" loading="lazy"></p><p>叉路(續行主線)<br><img src="https://1013399.github.io/image-5/313/149459447_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>觀景涼亭<br><img src="https://1013399.github.io/image-5/313/149459450_l.jpg" alt="鳳崎落日觀景步道 觀景涼亭" loading="lazy"></p><p>觀景涼亭視野<br><img src="https://1013399.github.io/image-5/313/149459452_l.jpg" alt="鳳崎落日觀景步道 觀景涼亭" loading="lazy"></p><p>觀景涼亭旁鳳崎晚霞簡介<br><img src="https://1013399.github.io/image-5/313/149459456_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>叉路(續行主線)<br><img src="https://1013399.github.io/image-5/313/149459461_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>陡上石階<br><img src="https://1013399.github.io/image-5/313/149459465_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>休憩平台旁涼亭<br><img src="https://1013399.github.io/image-5/313/149459468_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>休憩平台<br><img src="https://1013399.github.io/image-5/313/149459471_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>過休憩平台後叉路(取右水泥階梯)<br><img src="https://1013399.github.io/image-5/313/149459474_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>陡下石階<br><img src="https://1013399.github.io/image-5/313/149459480_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>軍事碉堡<br><img src="https://1013399.github.io/image-5/313/149459492_l.jpg" alt="鳳崎落日觀景步道 軍事碉堡" loading="lazy"></p><p>戰備坑道<br><img src="https://1013399.github.io/image-5/313/149459497_l.jpg" alt="鳳崎落日觀景步道 戰備坑道" loading="lazy"></p><p>涼亭前展望<br><img src="https://1013399.github.io/image-5/313/149459499_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>涼亭<br><img src="https://1013399.github.io/image-5/313/149459506_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>涼亭下方卡拉OK餐廳及小販<br><img src="https://1013399.github.io/image-5/313/149459512_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>叉路(補水站、第二處蓋章處，左上階梯可回天德堂，續行泥土產道)<br><img src="https://1013399.github.io/image-5/313/149459516_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>叉路(續行往靜善宮，取右往蓮花寺)<br><img src="https://1013399.github.io/image-5/313/149459519_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>第二休憩平台<br><img src="https://1013399.github.io/image-5/313/149459522_l.jpg" alt="鳳崎落日觀景步道 第二休憩平台" loading="lazy"></p><p>涼亭<br><img src="https://1013399.github.io/image-5/313/149459528_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>步道主線終點(前有涼亭，取右往蓮花寺)<br><img src="https://1013399.github.io/image-5/313/149459538_l.jpg" alt="鳳崎落日觀景步道 步道主線終點" loading="lazy"></p><p>蓮花寺廣場(品嘗美食)<br><img src="https://1013399.github.io/image-5/313/149459544_l.jpg" alt="鳳崎落日觀景步道 蓮花寺廣場" loading="lazy"></p><p>紀念品兌換處<br><img src="https://1013399.github.io/image-5/313/149459552_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>蓮花寺廣場現場表演<br><img src="https://1013399.github.io/image-5/313/149459550_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>蓮花寺步道解說牌(蓮花寺至靜善宮)<br><img src="https://1013399.github.io/image-5/313/149459560_l.jpg" alt="鳳崎落日觀景步道 蓮花寺步道解說牌 蓮花寺至靜善宮" loading="lazy"></p><p>鳳崎步道導覽圖<br><img src="https://1013399.github.io/image-5/313/149459566_l.jpg" alt="鳳崎落日觀景步道 鳳崎步道導覽圖" loading="lazy"></p><p>排隊搭接駁車<br><img src="https://1013399.github.io/image-5/313/149459568_l.jpg" alt="鳳崎落日觀景步道" loading="lazy"></p><p>活動DM(左正面、右背面)<br><img src="https://1013399.github.io/image-5/313/149459438_l.jpg" alt="鳳崎落日觀景步道 活動DM" loading="lazy"></p><p>天德堂交通路線圖(取自活動DM)<br><img src="https://1013399.github.io/image-5/313/149459842_l.jpg" alt="鳳崎落日觀景步道 天德堂交通路線圖" loading="lazy"></p><h2 id="參考步道路線圖" tabindex="-1"><a class="header-anchor" href="#參考步道路線圖" aria-hidden="true">#</a> 參考步道路線圖</h2>',42),m={href:"http://tw.myblog.yahoo.com/fuli1961/article?mid=7024",target:"_blank",rel:"noopener noreferrer"},c=i("br",null,null,-1),d=i("img",{src:"https://1013399.github.io/image-5/313/149459835_l.jpg",alt:"鳳崎落日觀景步道 路線圖",loading:"lazy"},null,-1);function _(u,y){const t=g("ExternalLinkIcon");return p(),r("div",null,[n,e(" more "),b,i("p",null,[a("原圖連結→"),i("a",m,[a("山影狐蹤部落格"),o(t)]),c,d])])}const f=l(h,[["render",_],["__file","post-313-2010-03-29.html.vue"]]);export{f as default};
