import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as e,c as g,a as p,b as a,f as t,d as o,e as s}from"./app-7f62a6ff.js";const n={},h=a("p",null,[a("strong",null,"知高圳步道"),t("，位於台中縣烏日鄉與大肚鄉之間，台中縣政府交通旅遊局將逐年闢建健行步道。灌溉用的知高圳，取自筏子溪溪水，灌溉台中南屯、烏日、大肚的農田，由於溝渠兩岸腹地平坦，週遭都是稻田，田園風光相當迷人，晨昏時刻經常有民眾前往散步。")],-1),b=s('<p><strong>學田山</strong>，位於台中縣烏日鄉成功嶺西北方尾稜，海拔139公尺，原有一顆三等三角點#113已遺失，另外補立聯勤測量隊三等三角點陸補90，山頂往大肚俯瞰之展望極佳，但從知高圳步道好漢坡起登，沿途盡是芒草、酷熱無蔭。</p><p>走走日期： 2010/03/06</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高於王田下交流道走台1乙線(中山路)往烏日方向，過成功火車站後於中山路三段登寺巷左轉往善光寺(目前路口施工中，需往前開一點再迴轉)，至善光寺牌樓叉路左轉(右往旭光國小後門)，經過牌樓後叉路左轉(右上往善光寺)，再遇叉路右轉後不久即到達知高圳步道入口，於路旁較寬處停車。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→知高圳步道入口(沿水圳旁行走)<br> →圳旁巨榕(樹根盤踞，相當特殊)<br> →架高引水橋(走階梯棧道下至引水橋下方)<br> →第一座小橋<br> →第二座小橋<br> →叉路(右山路應可接回步道，續直走步道)<br> →第三座小橋<br> →步道結束(接產業道路續行)<br> →叉路(旁邊有“王田交流道精導點”鋼標，取右往學田山好漢波)<br> →好漢坡起點(旁有電桿「振興枝 5 G4638 BA74」，取右水泥路往上，沿途酷熱無蔭)<br> →高速公路三角點<br> →好漢坡終點(展望佳，遠眺彰化市)<br> →學田山(未發現基石，取右續行，基石位置可能取左才對)<br> →叉路(接上土石道路，取右續行找基石)<br> →廢棄矮牆<br> →接上柏油道路(仍未見基石，原路回)<br> →回到學田山、好漢坡<br> →回到知高圳步道入口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約1.5小時</p><p>高低落差：約80公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>知高圳步道入口<br><img src="https://1013399.github.io/image-5/315/147701548_l.jpg" alt="知高圳步道入口" loading="lazy"></p><p>步道沿水圳旁行走，不會迷路<br><img src="https://1013399.github.io/image-5/315/147701557_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>俯瞰中山高<br><img src="https://1013399.github.io/image-5/315/147701563_l.jpg" alt="知高圳步道 俯瞰中山高" loading="lazy"></p><p>巨榕樹根盤踞，相當特殊<br><img src="https://1013399.github.io/image-5/315/147701627_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>步道竹葉鋪地<br><img src="https://1013399.github.io/image-5/315/147702153_l.jpg" alt="知高圳步道、學田山 竹葉鋪地" loading="lazy"></p><p>架高引水橋(取右下階梯棧道)<br><img src="https://1013399.github.io/image-5/315/147701632_l.jpg" alt="知高圳步道、學田山 架高引水橋" loading="lazy"></p><p>由下往上看架高引水橋<br><img src="https://1013399.github.io/image-5/315/147701639_l.jpg" alt="知高圳步道、學田山 架高引水橋" loading="lazy"></p><p>叉路(續直走步道)<br><img src="https://1013399.github.io/image-5/315/147701644_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>第三座小橋<br><img src="https://1013399.github.io/image-5/315/147701736_l.jpg" alt="知高圳步道、學田山 第三座小橋" loading="lazy"></p><p>取水閘門<br><img src="https://1013399.github.io/image-5/315/147701793_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>步道結束，接產業道路<br><img src="https://1013399.github.io/image-5/315/147701855_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>好漢坡起點旁電桿「振興枝 5 G4638 BA74」<br><img src="https://1013399.github.io/image-5/315/147701881_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>好漢坡起點水泥路<br><img src="https://1013399.github.io/image-5/315/147701889_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>好漢坡輪胎階梯<br><img src="https://1013399.github.io/image-5/315/147701962_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>高速公路三角點<br><img src="https://1013399.github.io/image-5/315/147701976_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>好漢坡終點(學田山)展望<br><img src="https://1013399.github.io/image-5/315/147702040_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>叉路(接上土石道路)<br><img src="https://1013399.github.io/image-5/315/147702149_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>廢棄矮牆<br><img src="https://1013399.github.io/image-5/315/147702051_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><p>接上柏油道路(可能是成功西路，原路折返)<br><img src="https://1013399.github.io/image-5/315/147702059_l.jpg" alt="知高圳步道、學田山" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',29),c={href:"http://blog.yam.com/amjads/article/25281432",target:"_blank",rel:"noopener noreferrer"},d=a("br",null,null,-1),m=a("img",{src:"https://1013399.github.io/image-5/315/147702419_l.jpg",alt:"知高圳步道、學田山 參考地圖",loading:"lazy"},null,-1);function _(u,j){const i=l("ExternalLinkIcon");return e(),g("div",null,[h,p(" more "),b,a("p",null,[t("原圖連結→"),a("a",c,[t("永遠漫步在雲海上"),o(i)]),d,m])])}const f=r(n,[["render",_],["__file","post-315-2010-03-09.html.vue"]]);export{f as default};
