import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as l,o as r,c as p,a as o,b as i,f as t,d as s,e}from"./app-7f62a6ff.js";const h={},n=i("p",null,[i("strong",null,"南崁山"),t("，又名「崙尖」，海拔155公尺，位於五酒桶山稜線步道途中，山頂建有一座觀景台，居高臨下，展望甚佳，另有三等三角點九九九號基石。")],-1),b=i("p",null,[i("strong",null,"五酒桶山"),t("，海拔170公尺，位於桃園縣蘆竹鄉內厝村及外社村交界處，山頂有很多的酒甕，部份酒甕由五個一組堆疊而成。一般人所稱的五酒桶山為一廣大休憩區，山界所稱的五酒桶山在其東方山頂，又稱東峰，兩者皆無基石，五酒桶山步道有許多登山口，可由營盤坑、蕃子厝或南天宮進入。")],-1),m=e('<p><strong>山鼻山</strong>，海拔182公尺，位於五酒桶山休憩區附近，山頂有顆TPC654衛星定位基石及位置圖標示牌，展望佳。</p><p>走走日期： 2010/02/28</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高速公路下南崁交流道，右轉往南崁(台4線)接中正路，遇南山路(桃3) 右轉，再右轉六福路，遇長興路左轉，至長安路口前右轉產業道路往南天宮，將車停於南天宮停車場。</p><h2 id="走走路線" tabindex="-1"><a class="header-anchor" href="#走走路線" aria-hidden="true">#</a> 走走路線</h2><p>→南天宮停車場<br> →五酒桶山「海山之路」入口(水泥路，旁有泥土路也可往五酒桶山)<br> →迎廣愛心階梯(約170階)<br> →春秋亭(上到稜線，左有泥土路可回南天宮)<br> →叉路(左下山路亦可回南天宮，取右續走稜線水泥步道)<br> →<strong>南崁山</strong>(設有觀景台及涼亭，視野佳，左前方為六福步道入口，續直行)<br> →叉路(右山路往營盤坑，續直行)<br> →叉路(右後山路亦可往營盤坑，仍續直走稜線水泥步道)<br> →忘憂亭(為一簡易搭建亭子，附近有許多五個一組的酒甕)<br> →酒甕區叉路(先取右山路往東峰)<br> →叉路(右山路雜草多，取左往鞦韆搖搖椅)<br> →鞦韆搖搖椅(取右往東峰)<br> →<strong>五酒桶山東峰</strong>(林間無視野，未見標示牌，原路回)<br> →回到酒甕區叉路(取右續行)<br> →<strong>五酒桶山休憩區</strong>(卡拉OK歌聲稍嫌過吵)<br> →叉路(左往公埔，直行往崙頭土地公，取右走知性古道)<br> →土地公<br> →叉路(直走可往外社國小，取左進入福德古道)<br> →叉路(右山路不知往何處，續直行)<br> →三角公園叉路(右往崙頭土地公，取左回五酒桶山)<br> →叉路(左可回五酒桶山，續直行)<br> →叉路(右往義松小徑，取左往山鼻山)<br> →<strong>山鼻山</strong>(展望佳，有位置圖標示牌可供對照，叉路右往義松小徑、蕃子厝，取左回)<br> →叉路(左往崙頭土地公，取右回五酒桶山)<br> →回到五酒桶山休憩區<br> →叉路(直行為稜線水泥步道回南天宮，取右改走六福步道回)<br> →六福步道(沿途上上下下經過數個小山頭，至此才有爬山的感覺)<br> →回到南崁山(取右回南天宮)<br> →回到春秋亭<br> →回到南天宮</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>走走時間：約2.5小時</p><p>高低落差：約80公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>南天宮<br><img src="https://1013399.github.io/image-6/316/147028781_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>五酒桶山海山之路入口<br><img src="https://1013399.github.io/image-6/316/147028952_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>平緩好走水泥路<br><img src="https://1013399.github.io/image-6/316/147028965_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>迎廣愛心階梯<br><img src="https://1013399.github.io/image-6/316/147028978_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>迎廣愛心階梯紀念碑<br><img src="https://1013399.github.io/image-6/316/147029087_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>左叉路可回南天宮，前方即為「春秋亭」<br><img src="https://1013399.github.io/image-6/316/147029118_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>左下山路可回南天宮，取右續走稜線水泥步道<br><img src="https://1013399.github.io/image-6/316/147029128_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>南崁山觀景台及涼亭<br><img src="https://1013399.github.io/image-6/316/147029141_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>南崁山三等三角點九九九號<br><img src="https://1013399.github.io/image-6/316/147029154_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>左為六福步道入口，續直行<br><img src="https://1013399.github.io/image-6/316/147029165_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(右山路往營盤坑，續直行)<br><img src="https://1013399.github.io/image-6/316/147029172_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(右後山路往營盤坑，續直行)<br><img src="https://1013399.github.io/image-6/316/147029187_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>許多五個一組的酒甕，右側樹木後方即為「忘憂亭」<br><img src="https://1013399.github.io/image-6/316/147029332_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>酒甕區叉路(取右往東峰)<br><img src="https://1013399.github.io/image-6/316/147029383_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(取左往鞦韆搖搖椅)<br><img src="https://1013399.github.io/image-6/316/147029459_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>鞦韆搖搖椅<br><img src="https://1013399.github.io/image-6/316/147029531_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>五酒桶山東峰<br><img src="https://1013399.github.io/image-6/316/147029547_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>五酒桶山休憩區<br><img src="https://1013399.github.io/image-6/316/147029557_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>五酒桶山休憩區看台<br><img src="https://1013399.github.io/image-6/316/147029573_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>看台上的龍龜<br><img src="https://1013399.github.io/image-6/316/147029596_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(左往公埔，直行往崙頭土地公，取右走知性古道)<br><img src="https://1013399.github.io/image-6/316/147029608_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>知性古道<br><img src="https://1013399.github.io/image-6/316/147029614_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>土地公<br><img src="https://1013399.github.io/image-6/316/147029622_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(取左進入福德古道)<br><img src="https://1013399.github.io/image-6/316/147029633_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(續直行)<br><img src="https://1013399.github.io/image-6/316/147029717_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>三角公園(取左回五酒桶山)<br><img src="https://1013399.github.io/image-6/316/147029773_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(續直行)<br><img src="https://1013399.github.io/image-6/316/147029782_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(取左往山鼻山)<br><img src="https://1013399.github.io/image-6/316/147029817_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>山鼻山(視野佳，賞完景取左回)<br><img src="https://1013399.github.io/image-6/316/147029887_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>山鼻山位置圖標示牌<br><img src="https://1013399.github.io/image-6/316/147029871_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(取右回五酒桶山)<br><img src="https://1013399.github.io/image-6/316/147029896_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>叉路(取右改走六福步道回)<br><img src="https://1013399.github.io/image-6/316/147029932_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>六福步道有多處拉繩陡坡<br><img src="https://1013399.github.io/image-6/316/147029938_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><p>上上下下數個小山頭<br><img src="https://1013399.github.io/image-6/316/147028773_l.jpg" alt="南崁山、五酒桶山、山鼻山、知性古道、六福步道" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2><p>原圖有誤，自行修改過(紅線)</p>',45),c={href:"http://www.tonyhuang39.com/tony0683/tony0683.html",target:"_blank",rel:"noopener noreferrer"},d=i("br",null,null,-1),_=i("img",{src:"https://1013399.github.io/image-6/316/147030217_l.jpg",alt:"南崁山、五酒桶山、山鼻山、知性古道、六福步道 參考地圖",loading:"lazy"},null,-1);function u(y,j){const a=l("ExternalLinkIcon");return r(),p("div",null,[n,b,o(" more "),m,i("p",null,[t("原圖連結→"),i("a",c,[t("Tony的自然人文旅記"),s(a)]),d,_])])}const x=g(h,[["render",u],["__file","post-316-2010-03-02.html.vue"]]);export{x as default};
