import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{r as p,o as r,c as e,a as o,b as i,f as a,d as g,e as s}from"./app-a01d9b9c.js";const h={},b=i("p",null,[i("strong",null,"大崙頭山"),a("，海拔476公尺，位於內湖、雙溪界稜上，山稜西伸大崙尾山，東邊五指山，南展碧山巖之三叉點山峰，是內湖地區最高的山脈，山頂為寬平石板水泥地，有顆無名基石5號，也有親山步道的蓋章處。該山名列台灣小百岳#008。")],-1),n=i("p",null,[i("strong",null,"大崙尾山"),a("，又稱赤上天山，海拔451公尺，位於內湖區北勢湖與士林區雙溪界稜，有顆三等三角點一○七三號，旁邊有座涼亭，還有中央氣象局的自動測量裝置。")],-1),m={href:"http://www.ed.taipei.gov.tw/cgi-bin/SM_theme?page=48369993",target:"_blank",rel:"noopener noreferrer"},c=s('<p>登山日期： 2010/01/31</p><h2 id="行車路線" tabindex="-1"><a class="header-anchor" href="#行車路線" aria-hidden="true">#</a> 行車路線</h2><p>中山高下重慶北路交流道往士林，至中正路右轉過百齡橋(台2乙線)，接上福林路(台2甲線)在雙溪公園右轉接至善路，過故宮博物院、至善國中後，遇中社路右轉經雙溪國小至大崙尾山北面登山口路旁停車。(登山口無廁所，於255公車「明溪街」站牌候車亭旁有公廁)</p><h2 id="登山路線" tabindex="-1"><a class="header-anchor" href="#登山路線" aria-hidden="true">#</a> 登山路線</h2><p>→大崙尾山登山口(階梯步道，旁有親山步道導覽圖)<br> →叉路(右往金龍產業道路、剪刀石山，續行更陡的石階步道)<br> →叉路(接金龍產業道路，取左往大崙尾山)<br> →寬廣休憩地(右有叉路接大崙尾山南面步道，取直續行階梯)<br> →鐵皮屋休憩地(有涼亭、花園)<br> →大崙尾山(三角點位於路旁，旁有涼亭，直行石板路往打印台，先取左往山屋)<br> →山屋(展望不錯，穿越山屋續行)<br> →氣象資料收集站(續行接回石板路)<br> →叉路(左有泥土路徑可能是往碧溪步道，續直行)<br> →叉路(左泥土路徑不知往何處，續直行)<br> →親山步道打印台叉路(腹地寬廣可供休憩，右往九蓮寺，取左接碎石路往大崙頭山)<br> →接碧山路(有攤販，吃盤臭豆腐後續往前行)<br> →叉路(右有指示往福音山，續直行)<br> →大崙湖<br> →大崙頭山登山口<br> →叉路(直行為石板路轉為石板階梯，取左泥土路徑，途中有數處可切回石板階梯)<br> →切回石板階梯(聽山友說，需走石板階梯才會到達大崙頭山山頂)<br> →大崙頭山(也設有親山步道打印台，登上觀景台視野達360度，休息後續行)<br> →叉路(直行往森林步道，取左枕木自然步道)<br> →接碧溪產業道路(取左行，途中圍籬有缺口可切入碧溪步道)<br> →觀景台(不續走柏油產道，往回頭走碧溪步道)<br> →步道叉路(直行為碧溪步道，取左回中社路)<br> →叉路(左往大崙尾山，取右往中社路)<br> →長壽坡(左叉路往大崙尾山，取右往中社路)<br> →中社路坍塌路段(現為休憩小平台)<br> →士林分局靶場(出鐵門後接上中社路)<br> →回到大崙尾山登山口</p><h2 id="走走資訊" tabindex="-1"><a class="header-anchor" href="#走走資訊" aria-hidden="true">#</a> 走走資訊</h2><div class="hint-container info"><p class="hint-container-title">相關信息</p><p>登山時間：約3小時</p><p>高低落差：約170公尺</p></div><h2 id="走走相片" tabindex="-1"><a class="header-anchor" href="#走走相片" aria-hidden="true">#</a> 走走相片</h2><p>大崙尾山登山口<br><img src="https://1013399.github.io/image-6/318/144780593_l.jpg" alt="大崙尾山登山口" loading="lazy"></p><p>登山口旁親山步道導覽圖<br><img src="https://1013399.github.io/image-6/318/144780627_l.jpg" alt="大崙尾山 親山步道導覽圖" loading="lazy"></p><p>右往金龍產業道路、剪刀石山，續行石階步道<br><img src="https://1013399.github.io/image-6/318/144780632_l.jpg" alt="大崙尾山" loading="lazy"></p><p>接金龍產業道路，取左往大崙尾山<br><img src="https://1013399.github.io/image-6/318/144780639_l.jpg" alt="大崙尾山" loading="lazy"></p><p>道路左側有白山羊雕像<br><img src="https://1013399.github.io/image-6/318/144780647_l.jpg" alt="大崙尾山" loading="lazy"></p><p>寬廣休憩地(右有叉路接大崙尾山南面步道)<br><img src="https://1013399.github.io/image-6/318/144780654_l.jpg" alt="大崙尾山" loading="lazy"></p><p>續行石階步道<br><img src="https://1013399.github.io/image-6/318/144780671_l.jpg" alt="大崙尾山" loading="lazy"></p><p>鐵皮屋休憩地<br><img src="https://1013399.github.io/image-6/318/144780774_l.jpg" alt="大崙尾山" loading="lazy"></p><p>大崙尾山涼亭(直行往親山步道打印台，先取左往山屋)<br><img src="https://1013399.github.io/image-6/318/144780784_l.jpg" alt="大崙尾山涼亭" loading="lazy"></p><p>大崙尾山三等三角點一○七三號<br><img src="https://1013399.github.io/image-6/318/144780831_l.jpg" alt="大崙尾山三等三角點一○七三號" loading="lazy"></p><p>山屋旁涼亭<br><img src="https://1013399.github.io/image-6/318/144780864_l.jpg" alt="大崙尾山" loading="lazy"></p><p>山屋<br><img src="https://1013399.github.io/image-6/318/144780868_l.jpg" alt="大崙尾山" loading="lazy"></p><p>氣象資料收集站<br><img src="https://1013399.github.io/image-6/318/144780870_l.jpg" alt="大崙尾山 氣象資料收集站" loading="lazy"></p><p>叉路(續直行)<br><img src="https://1013399.github.io/image-6/318/144780872_l.jpg" alt="大崙尾山" loading="lazy"></p><p>叉路(續直行)<br><img src="https://1013399.github.io/image-6/318/144780875_l.jpg" alt="大崙尾山" loading="lazy"></p><p>親山步道打印台<br><img src="https://1013399.github.io/image-6/318/144780879_l.jpg" alt="大崙尾山" loading="lazy"></p><p>碎石路<br><img src="https://1013399.github.io/image-6/318/144780953_l.jpg" alt="大崙尾山" loading="lazy"></p><p>接碧山路<br><img src="https://1013399.github.io/image-6/318/144781046_l.jpg" alt="大崙尾山" loading="lazy"></p><p>親山步道入口意象(往回拍攝)<br><img src="https://1013399.github.io/image-6/318/144781142_l.jpg" alt="大崙尾山親山步道入口" loading="lazy"></p><p>福音山叉路<br><img src="https://1013399.github.io/image-6/318/144781815_l.jpg" alt="福音山叉路" loading="lazy"></p><p>大崙湖<br><img src="https://1013399.github.io/image-6/318/144781335_l.jpg" alt="大崙湖 大崙尾山、大崙頭山" loading="lazy"></p><p>大崙頭山登山口<br><img src="https://1013399.github.io/image-6/318/144781343_l.jpg" alt="大崙頭山登山口" loading="lazy"></p><p>叉路(直行為石板路轉為石板階梯，取左泥土路徑)<br><img src="https://1013399.github.io/image-6/318/144781353_l.jpg" alt="大崙頭山" loading="lazy"></p><p>切回石板階梯<br><img src="https://1013399.github.io/image-6/318/144781363_l.jpg" alt="大崙頭山" loading="lazy"></p><p>大崙頭山(也設有親山步道打印台，登上觀景台視野達360度)<br><img src="https://1013399.github.io/image-6/318/144781367_l.jpg" alt="大崙頭山" loading="lazy"></p><p>大崙頭山無名基石5號<br><img src="https://1013399.github.io/image-6/318/144781377_l.jpg" alt="大崙頭山無名基石5號" loading="lazy"></p><p>大崙頭山遠眺七星山(山下有中華電信衛星台)<br><img src="https://1013399.github.io/image-6/318/144781372_l.jpg" alt="大崙頭山" loading="lazy"></p><p>叉路(取左自然步道)<br><img src="https://1013399.github.io/image-6/318/144781385_l.jpg" alt="大崙頭山" loading="lazy"></p><p>自然步道為枕木步道<br><img src="https://1013399.github.io/image-6/318/144781396_l.jpg" alt="大崙頭山" loading="lazy"></p><p>接碧溪產業道路(取左行)<br><img src="https://1013399.github.io/image-6/318/144781402_l.jpg" alt="大崙頭山" loading="lazy"></p><p>產道途中圍籬有缺口可切入碧溪步道<br><img src="https://1013399.github.io/image-6/318/144781406_l.jpg" alt="大崙頭山" loading="lazy"></p><p>觀景台(往回頭走碧溪步道)<br><img src="https://1013399.github.io/image-6/318/144781509_l.jpg" alt="大崙頭山" loading="lazy"></p><p>步道叉路(取左回中社路)<br><img src="https://1013399.github.io/image-6/318/144781510_l.jpg" alt="大崙頭山" loading="lazy"></p><p>叉路(左往大崙尾山，取右往中社路)<br><img src="https://1013399.github.io/image-6/318/144781513_l.jpg" alt="大崙頭山" loading="lazy"></p><p>長壽坡叉路(取右往中社路)<br><img src="https://1013399.github.io/image-6/318/144781537_l.jpg" alt="大崙頭山" loading="lazy"></p><p>長壽坡(有隻松鼠正在覓食)<br><img src="https://1013399.github.io/image-6/318/144781540_l.jpg" alt="大崙頭山" loading="lazy"></p><p>中社路坍塌路段(現為休憩小平台)<br><img src="https://1013399.github.io/image-6/318/144781562_l.jpg" alt="大崙頭山" loading="lazy"></p><p>士林分局靶場(好像已經荒廢)<br><img src="https://1013399.github.io/image-6/318/144780589_l.jpg" alt="士林分局靶場" loading="lazy"></p><h2 id="參考地圖" tabindex="-1"><a class="header-anchor" href="#參考地圖" aria-hidden="true">#</a> 參考地圖</h2>',47),d={href:"http://www.ed.taipei.gov.tw/cgi-bin/SM_theme?page=483a4879",target:"_blank",rel:"noopener noreferrer"},_=i("br",null,null,-1),u=i("img",{src:"https://1013399.github.io/image-6/318/144781752_l.jpg",alt:"大崙尾山、大崙頭山 參考地圖",loading:"lazy"},null,-1);function j(y,z){const t=p("ExternalLinkIcon");return r(),e("div",null,[b,n,o(" more "),i("p",null,[a("大崙頭尾山親山步道相關介紹："),i("a",m,[a("台北市親山步道主題網"),g(t)])]),c,i("p",null,[a("原圖連結→"),i("a",d,[a("台北市親山步道主題網"),g(t)]),_,u])])}const w=l(h,[["render",j],["__file","post-318-2010-02-03.html.vue"]]);export{w as default};
